/**
 * Resolve an Attio API client that works in both runtime and test environments.
 * In tests/offline, prefer the mocked getAttioClient if available.
 */
function resolveAttioClient(): AxiosInstance {
  const mod: any = AttioClientModule as any;
  // Always prefer explicit factory if present (enables Vitest mocks)
  if (typeof mod.getAttioClient === 'function') {
    return mod.getAttioClient();
  }
  try {
    return getLazyAttioClient();
  } catch {
    if (
      typeof mod.createAttioClient === 'function' &&
      process.env.ATTIO_API_KEY
    ) {
      return mod.createAttioClient(process.env.ATTIO_API_KEY);
    }
    if (
      typeof mod.buildAttioClient === 'function' &&
      process.env.ATTIO_API_KEY
    ) {
      return mod.buildAttioClient({ apiKey: process.env.ATTIO_API_KEY });
    }
    throw new Error('Unable to resolve Attio API client');
  }
}

/**
 * Handlers for resource-related requests
 */
import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import {
  ListResourcesRequestSchema,
  ReadResourceRequestSchema,
} from '@modelcontextprotocol/sdk/types.js';
import { ServerContext } from '../server/createServer.js';
import { setGlobalContext } from '../api/lazy-client.js';
import { createErrorResult } from '../utils/error-handler.js';
import {
  listCompanies,
  getCompanyDetails,
} from '../objects/companies/index.js';
import { listPeople, getPersonDetails } from '../objects/people/index.js';
import { getLists, getListDetails } from '../objects/lists.js';
import { parseResourceUri, formatResourceUri } from '../utils/uri-parser.js';
import { ResourceType, AttioRecord, AttioList } from '../types/attio.js';

/**
 * Type for API errors with response data
 */
interface ApiError extends Error {
  response?: {
    data?: Record<string, unknown>;
  };
}

/**
 * Format a single record for resource response
 *
 * @param record - The record to format
 * @param type - The type of resource
 * @returns Formatted resource object
 */
function formatRecordAsResource(record: AttioRecord, type: ResourceType) {
  return {
    uri: formatResourceUri(type, record.id?.record_id || ''),
    name:
      (record.values?.name as any)?.[0]?.value ||
      `Unknown ${type.slice(0, -1)}`,
    mimeType: 'application/json',
  };
}

/**
 * Format a list for resource response
 *
 * @param list - The list to format
 * @returns Formatted resource object
 */
function formatListAsResource(list: AttioList) {
  return {
    uri: formatResourceUri(ResourceType.LISTS, list.id?.list_id || ''),
    name: list.name || 'Unknown list',
    mimeType: 'application/json',
  };
}

/**
 * Registers resource-related request handlers with the server
 *
 * @param server - The MCP server instance
 */
export function registerResourceHandlers(
  server: Server,
  context?: ServerContext
): void {
  // Set the global context for lazy initialization if provided
  if (context) {
    setGlobalContext(context);
  }
  // Handler for listing resources (Companies, People, and Lists)
  server.setRequestHandler(ListResourcesRequestSchema, async (request) => {
    try {
      // Determine resource type (default to companies if not specified)
      const resourceType =
        (request.params?.type as ResourceType) || ResourceType.COMPANIES;

      switch (resourceType) {
        case ResourceType.PEOPLE:
          try {
            const people = await listPeople();
            return {
              resources: people.map((person) =>
                formatRecordAsResource(person, ResourceType.PEOPLE)
              ),
            };
          } catch {
            // For resource requests, always return resources array even on error
            // This allows capability scanning to work without API key
            return {
              resources: [],
            };
          }

        case ResourceType.LISTS:
          try {
            const lists = await getLists();
            // Ensure lists is always an array
            const safeList = Array.isArray(lists) ? lists : [];
            return {
              resources: safeList.map((list) => formatListAsResource(list)),
            };
          } catch {
            // For resource requests, always return resources array even on error
            return {
              resources: [],
            };
          }

        case ResourceType.COMPANIES:
        default:
          try {
            const companies = await listCompanies();
            return {
              resources: companies.map((company) =>
                formatRecordAsResource(company, ResourceType.COMPANIES)
              ),
            };
          } catch {
            // For resource requests, always return resources array even on error
            // This allows capability scanning to work without API key
            return {
              resources: [],
            };
          }
      }
    } catch (error: unknown) {
      return createErrorResult(
        error instanceof Error ? error : new Error('Unknown error'),
        'unknown',
        'unknown',
        {}
      );
    }
  });

  // Handler for reading resource details (Companies, People, and Lists)
  server.setRequestHandler(ReadResourceRequestSchema, async (request) => {
    try {
      const uri = request.params.uri;
      const [resourceType, id] = parseResourceUri(uri);

      switch (resourceType) {
        case ResourceType.PEOPLE:
          try {
            const person = await getPersonDetails(id);

            return {
              contents: [
                {
                  uri,
                  text: JSON.stringify(person, null, 2),
                  mimeType: 'application/json',
                },
              ],
            };
          } catch (error: unknown) {
            return createErrorResult(
              error instanceof Error ? error : new Error('Unknown error'),
              `/objects/people/${id}`,
              'GET',
              (error as ApiError).response?.data || {}
            );
          }

        case ResourceType.LISTS:
          try {
            const list = await getListDetails(id);

            return {
              contents: [
                {
                  uri,
                  text: JSON.stringify(list, null, 2),
                  mimeType: 'application/json',
                },
              ],
            };
          } catch (error: unknown) {
            return createErrorResult(
              error instanceof Error ? error : new Error('Unknown error'),
              `/lists/${id}`,
              'GET',
              (error as ApiError).response?.data || {}
            );
          }

        case ResourceType.COMPANIES:
          try {
            const company = await getCompanyDetails(id);

            return {
              contents: [
                {
                  uri,
                  text: JSON.stringify(company, null, 2),
                  mimeType: 'application/json',
                },
              ],
            };
          } catch (error: unknown) {
            return createErrorResult(
              error instanceof Error ? error : new Error('Unknown error'),
              `/objects/companies/${id}`,
              'GET',
              (error as ApiError).response?.data || {}
            );
          }

        default:
          throw new Error(`Unsupported resource type: ${resourceType}`);
      }
    } catch (error: unknown) {
      return createErrorResult(
        error instanceof Error ? error : new Error('Unknown error'),
        request.params.uri,
        'GET',
        {}
      );
    }
  });
}

/**
 * Attribute management tool configurations for companies
 */
import { Company } from '../../../types/attio.js';
import {
  getCompanyFields,
  getCompanyCustomFields,
  discoverCompanyAttributes,
  getCompanyAttributes,
} from '../../../objects/companies/index.js';
import { ToolConfig } from '../../tool-types.js';

// Company attribute tool configurations
export const attributeToolConfigs = {
  fields: {
    name: 'get-company-fields',
    handler: getCompanyFields,
    formatResult: (company: Partial<Company>) => {
      const name = (company.values?.name as any)?.[0]?.value || 'Unknown';
      const id = company.id?.record_id || 'Unknown';
      const fieldCount = Object.keys(company.values || {}).length;
      const fields = Object.keys(company.values || {});

      // Create a simplified version of the values for display
      const simplifiedValues: Record<string, unknown> = {};
      for (const [key, value] of Object.entries(company.values || {})) {
        if (Array.isArray(value) && value.length > 0) {
          // Extract just the actual value from the array structure
          const firstItem = value[0];
          if (firstItem && firstItem.value !== undefined) {
            simplifiedValues[key] = firstItem.value;
          } else if (firstItem && firstItem.target_record_id) {
            // Handle reference fields
            simplifiedValues[key] = `Reference: ${firstItem.target_record_id}`;
          } else {
            simplifiedValues[key] = firstItem;
          }
        } else {
          simplifiedValues[key] = value;
        }
      }

      return `Company: ${name} (ID: ${id})
Fields retrieved: ${fieldCount} (${fields.join(', ')})

${JSON.stringify(simplifiedValues, null, 2)}`;
    },
  } as ToolConfig,

  customFields: {
    name: 'get-company-custom-fields',
    handler: async (
      companyId: string,
      customFieldNames?: string[] | string
    ) => {
      // Support both array of field names and comma-separated string
      let fields: string[] | undefined;

      if (customFieldNames) {
        if (typeof customFieldNames === 'string') {
          fields = customFieldNames.split(',').map((f: string) => f.trim());
        } else if (Array.isArray(customFieldNames)) {
          fields = customFieldNames;
        }
      }

      return await getCompanyCustomFields(companyId, fields);
    },
    formatResult: (company: Partial<Company>) => {
      const name = (company.values?.name as any)?.[0]?.value || 'Unknown';
      const id = company.id?.record_id || 'Unknown';
      const customFields = { ...company.values };
      delete customFields.name;

      const fieldCount = Object.keys(customFields).length;

      return `Company: ${name} (ID: ${id})
Custom fields: ${fieldCount}

${
  fieldCount > 0
    ? JSON.stringify(customFields, null, 2)
    : 'No custom fields found'
}`;
    },
  } as ToolConfig,

  discoverAttributes: {
    name: 'discover-company-attributes',
    handler: discoverCompanyAttributes,
    formatResult: (result: Record<string, unknown>): string => {
      // Type-safe property access with proper narrowing
      const all = Array.isArray(result.all) ? result.all : [];
      const standard = Array.isArray(result.standard) ? result.standard : [];
      const custom = Array.isArray(result.custom) ? result.custom : [];

      // Sanity check for empty or invalid results
      if (all.length === 0 && standard.length === 0 && custom.length === 0) {
        return 'No company attributes found. This may occur if there are no companies in the workspace.';
      }

      let output = `Company Attributes Discovery\n`;
      output += `Total attributes: ${all.length}\n`;
      output += `Standard fields: ${standard.length}\n`;
      output += `Custom fields: ${custom.length}\n\n`;

      output += `STANDARD FIELDS:\n`;
      if (standard.length > 0) {
        standard.forEach((field: unknown) => {
          output += `  - ${String(field)}\n`;
        });
      } else {
        output += '  None found\n';
      }

      output += `\nCUSTOM FIELDS:\n`;
      if (custom.length > 0) {
        custom.forEach((field: unknown) => {
          const fieldInfo = all.find(
            (f: unknown) =>
              typeof f === 'object' &&
              f !== null &&
              'name' in f &&
              (f as { name: unknown }).name === field
          );
          const fieldType =
            fieldInfo && typeof fieldInfo === 'object' && 'type' in fieldInfo
              ? String((fieldInfo as { type: unknown }).type)
              : 'unknown';
          output += `  - ${String(field)} (${fieldType})\n`;
        });
      } else {
        output += '  None found\n';
      }

      return output;
    },
  } as ToolConfig,

  getAttributes: {
    name: 'get-company-attributes',
    handler: getCompanyAttributes,
    formatResult: (result: Record<string, unknown>): string => {
      // Enhanced error handling for unexpected result structure
      if (!result || typeof result !== 'object') {
        return `Error: Unable to process the response. Received: ${JSON.stringify(
          result
        )}`;
      }

      // Handle case where the result contains an error object
      if (result.error) {
        const errorMessage =
          typeof result.error === 'object' &&
          result.error !== null &&
          'message' in result.error &&
          typeof (result.error as { message: unknown }).message === 'string'
            ? (result.error as { message: string }).message
            : JSON.stringify(result.error);
        return `Error retrieving attribute: ${errorMessage}`;
      }

      if (result.value !== undefined) {
        // Return specific attribute value
        const company =
          typeof result.company === 'string' ? result.company : 'Unknown';
        return `Company: ${company}\nAttribute value: ${
          typeof result.value === 'object'
            ? JSON.stringify(result.value, null, 2)
            : String(result.value)
        }`;
      } else if (result.attributes && Array.isArray(result.attributes)) {
        // Return list of attributes
        const company =
          typeof result.company === 'string' ? result.company : 'Unknown';
        return `Company: ${company}\nAvailable attributes (${
          result.attributes.length
        }):\n${result.attributes
          .map((attr: unknown) => `  - ${String(attr)}`)
          .join('\n')}`;
      } else {
        // Fallback for unexpected result structure
        return `Unexpected result format. Received: ${JSON.stringify(result)}`;
      }
    },
  } as ToolConfig,
};

