      description:
        'Resource type to search (companies, people, records, tasks, deals)',
    },
    queries: {
      type: 'array' as const,
      items: { type: 'string' as const },
      description: 'Array of search query strings',
      minItems: 1,
    },
    limit: {
      type: 'number' as const,
      minimum: 1,
