  'batch-search-companies': 'search',
  'batch-get-company-details': 'get',
  'batch-create-records': 'create',
  'batch-update-records': 'update',
};

/**
 * Get the count of tools that will be consolidated
 */
export function getConsolidationStats() {
  const deprecatedCount = Object.keys(deprecatedToolMappings).length;
  const universalCount = allUniversalTools.length;
  const reductionCount = deprecatedCount - universalCount;
  const reductionPercentage = Math.round(
    (reductionCount / deprecatedCount) * 100
  );

  return {
    deprecatedCount,
    universalCount,
    reductionCount,
    reductionPercentage,
    summary: `${deprecatedCount}  ${universalCount} tools (${reductionPercentage}% reduction)`,
  };
}

/**
 * Utility to check if a tool name is a universal tool
 */
export function isUniversalTool(toolName: string): boolean {
  return allUniversalTools.includes(toolName);
}

/**
 * Utility to check if a tool name is deprecated and should be migrated
 */
export function isDeprecatedTool(toolName: string): boolean {
  return toolName in deprecatedToolMappings;
}

/**
 * Get the universal tool equivalent for a deprecated tool
 */
export function getUniversalEquivalent(
  deprecatedToolName: string
): string | undefined {
  return deprecatedToolMappings[deprecatedToolName];
}

/**
 * Get migration parameters for converting a deprecated tool call to universal
 */
export function getMigrationParams(
  deprecatedToolName: string,
  originalParams: any
): any {
  const universalTool = getUniversalEquivalent(deprecatedToolName);
  const resourceType = resourceTypeMappings[deprecatedToolName];

  if (!universalTool || !resourceType) {
    throw new Error(
      `No migration path found for deprecated tool: ${deprecatedToolName}`
    );
  }

  // Base parameters for all universal tools
  const baseParams = {
    resource_type: resourceType,
    ...originalParams,
  };

  // Add specific parameters based on the universal tool type
  switch (universalTool) {
    case 'get-detailed-info': {
      const infoType = infoTypeMappings[deprecatedToolName];
      if (infoType) {
        baseParams.info_type = infoType;
      }
      break;
    }

    case 'search-by-content': {
      const contentType = contentTypeMappings[deprecatedToolName];
      if (contentType) {
        baseParams.content_type = contentType;
      }
      break;
    }

    case 'search-by-timeframe': {
      const timeframeType = timeframeTypeMappings[deprecatedToolName];
      if (timeframeType) {
        baseParams.timeframe_type = timeframeType;
      }
      break;
    }

    case 'search-by-relationship': {
      const relationshipType = relationshipTypeMappings[deprecatedToolName];
      if (relationshipType) {
        baseParams.relationship_type = relationshipType;
      }
      break;
    }

    case 'batch-operations': {
      const operationType = batchOperationTypeMappings[deprecatedToolName];
      if (operationType) {
        baseParams.operation_type = operationType;
      }
      break;
    }
  }

  return baseParams;
}

/**
 * Log consolidation statistics
 */
export function logConsolidationStats(): void {
  const stats = getConsolidationStats();
  console.error(`Universal tool consolidation: ${stats.summary}`);
  console.error(
    `Reduced tool count by ${stats.reductionCount} tools (${stats.reductionPercentage}% reduction)`
  );
}

/**
 * Advanced universal search tool configuration
 */

import { UniversalToolConfig, AdvancedSearchParams } from '../types.js';
import { AttioRecord } from '../../../../types/attio.js';

import { validateUniversalToolParams } from '../schemas.js';
import { ErrorService } from '../../../../services/ErrorService.js';
import { formatResourceType } from '../shared-handlers.js';

/**
 * Universal advanced search tool
 * Consolidates complex filtering across all resource types
 */
export const advancedSearchConfig: UniversalToolConfig = {
  name: 'advanced-search',
  handler: async (params: AdvancedSearchParams): Promise<AttioRecord[]> => {
    try {
      const sanitizedParams = validateUniversalToolParams(
        'advanced-search',
        params
      );

      const { resource_type } = sanitizedParams;

      // Advanced search uses Attio's non-$ operator dialect (equals, contains, gte/lte, is_not_empty, ...).
      // Perform a light de-normalization: translate any $-prefixed operators to the expected strings
      // and coerce is_not_empty value to true when omitted.
      let filters = sanitizedParams.filters as Record<string, unknown>;
      try {
        const deDollar = (cond: string): string => {
          if (!cond) return cond;
          if (cond.startsWith('$')) {
            const raw = cond.slice(1);
            switch (raw) {
              case 'eq':
                return 'equals';
              case 'contains':
                return 'contains';
              case 'starts_with':
                return 'starts_with';
              case 'ends_with':
                return 'ends_with';
              case 'gt':
                return 'gt';
              case 'gte':
                return 'gte';
              case 'lt':
                return 'lt';
              case 'lte':
                return 'lte';
              case 'not_empty':
                return 'is_not_empty';
              default:
                return raw; // fallback
            }
          }
          // Also accept already-correct tokens and legacy typos
          if (
            cond === 'is_not_empty' ||
            cond === 'equals' ||
            cond === 'contains' ||
            cond === 'starts_with' ||
            cond === 'ends_with' ||
            cond === 'gt' ||
            cond === 'gte' ||
            cond === 'lt' ||
            cond === 'lte'
          )
            return cond;
          return cond;
        };

        if (
          filters &&
          typeof filters === 'object' &&
          Array.isArray((filters as Record<string, unknown>).filters)
        ) {
          filters = {
            ...filters,
            filters: (
              (filters as Record<string, unknown>).filters as Record<
                string,
                unknown
              >[]
            ).map((f) => {
              if (!f || typeof f !== 'object') return f;
              const next = { ...f } as Record<string, unknown>;
              if (typeof next.condition === 'string') {
                next.condition = deDollar(next.condition);
              }
              if (
                next.condition === 'is_not_empty' &&
                (next.value == null || next.value === '')
              ) {
                next.value = true;
              }
              return next;
            }),
          };
        }
      } catch {
        // If transformation fails, proceed with original filters; downstream validation will report details
        filters = sanitizedParams.filters as Record<string, unknown>;
      }

      // Delegate to universal search handler defined elsewhere
      // We intentionally avoid importing the handler here to keep concerns separated
      const { handleUniversalSearch } = await import('../shared-handlers.js');
      return await handleUniversalSearch({
        resource_type,
        query: sanitizedParams.query,
        filters,
        limit: sanitizedParams.limit,
        offset: sanitizedParams.offset,
      });
    } catch (error: unknown) {
      const ctx = (params as any)?.resource_type
        ? String((params as any).resource_type)
        : '';
      throw ErrorService.createUniversalError('advanced search', ctx, error);
    }
  },
  formatResult: (results: AttioRecord[], resourceType?: string) => {
    const count = Array.isArray(results) ? results.length : 0;
    const typeName = resourceType
      ? formatResourceType(resourceType as any)
      : 'record';
    const headerType = resourceType
      ? count === 1
        ? typeName
        : `${typeName}s`
      : 'records';

    if (!Array.isArray(results)) {
      return `Advanced search found 0 ${headerType}:`;
    }

    const lines = results.map(
      (record: Record<string, unknown>, index: number) => {
        const values = (record as any).values as Record<string, unknown>;
        const recordId = (record as any).id as Record<string, unknown>;

        const coerce = (v: unknown): string | undefined => {
          if (v == null) return undefined;
          if (typeof v === 'string') return v;
          if (Array.isArray(v)) {
            const first = v[0] as any;
            if (typeof first === 'string') return first;
            if (first && typeof first === 'object' && 'value' in first)
              return String(first.value);
          }
          if (typeof v === 'object' && 'value' in (v as any))
            return String((v as any).value);
          return undefined;
        };

        const name =
          coerce(values?.name) ??
          coerce(values?.full_name) ??
          coerce(values?.title) ??
          'Unnamed';
        const industry = coerce(values?.industry);
        const location = coerce(values?.location);
        const website = coerce(values?.website);
        const id = (recordId?.record_id as string) || 'unknown';

        let details = name;
        if (industry) details += ` [${industry}]`;
        if (location) details += ` (${location})`;
        if (website) details += ` - ${website}`;
        details += ` (ID: ${id})`;

        return `${index + 1}. ${details}`;
      }
    );

    return `Advanced search found ${count} ${headerType}:\n${lines.join('\n')}`;
  },
};

