/**
 * Enhanced logging utilities for tool execution using structured logging
 */

import { ToolErrorContext } from '../../../types/tool-types.js';
import {
  error,
  warn,
  createScopedLogger,
  OperationType,
  setLogContext,
  generateCorrelationId,
  PerformanceTimer,
} from '../../../utils/logger.js';
import { CallToolRequest } from '@modelcontextprotocol/sdk/types.js';

/**
 * Initialize tool execution context with correlation ID
 */
export function initializeToolContext(toolName: string): string {
  const correlationId = generateCorrelationId();
  setLogContext({
    correlationId,
    operation: toolName,
    operationType: OperationType.TOOL_EXECUTION,
  });
  return correlationId;
}

/**
 * Create a scoped logger for tool execution
 */
export function createToolLogger(toolName: string, toolType?: string) {
  return createScopedLogger(
    `tool:${toolName}`,
    toolType || toolName,
    OperationType.TOOL_EXECUTION
  );
}

/**
 * Enhanced tool execution request logging with structured format
 *
 * @param toolType - The type of tool being executed (e.g., 'search', 'create', 'update')
 * @param toolName - The name of the tool as defined in the MCP configuration
 * @param request - The tool request data
 * @returns PerformanceTimer for tracking execution duration
 */
export function logToolRequest(
  toolType: string,
  toolName: string,
  request: CallToolRequest
): PerformanceTimer {
  const logger = createToolLogger(toolName, toolType);

  // Enhanced logging to debug MCP protocol issues
  const debugMode = process.env.MCP_DEBUG_REQUESTS === 'true';

  const requestData = {
    toolType,
    toolName,
    argumentsCount: request.params.arguments
      ? Object.keys(request.params.arguments).length
      : 0,
    hasArguments: !!request.params.arguments,
    // Log full request structure in debug mode
    ...(debugMode && {
      rawRequest: {
        method: (request as any).method,
        params: request.params,
        jsonrpc: (request as any).jsonrpc,
        id: (request as any).id,
      },
      paramsKeys: Object.keys(request.params || {}),
      argumentsStructure: request.params.arguments
        ? Object.keys(request.params.arguments)
        : 'missing',
    }),
    ...(process.env.NODE_ENV === 'development' && {
      arguments: request.params.arguments,
    }),
  };

  // Log warning if arguments are missing (helps diagnose protocol issues)
  if (!request.params.arguments && debugMode) {
    warn(
      `tool:${toolName}`,
      `Tool called without arguments wrapper - params keys: ${Object.keys(
        request.params || {}
      ).join(', ')}`,
      { params: request.params },
      toolType,
      OperationType.TOOL_EXECUTION
    );
  }

  return logger.operationStart(
    'execute',
    OperationType.TOOL_EXECUTION,
    requestData
  );
}

/**
 * Log successful tool execution
 *
 * @param toolName - The name of the tool
 * @param toolType - The type of tool
 * @param result - The execution result
 * @param timer - Performance timer from logToolRequest
 */
export function logToolSuccess(
  toolName: string,
  toolType: string,
  result: any,
  timer: PerformanceTimer
): void {
  const logger = createToolLogger(toolName, toolType);
  const duration = timer.end();

  const resultSummary = {
    success: true,
    hasContent: !!result?.content,
    contentLength: result?.content?.length || 0,
    resultType: Array.isArray(result?.content)
      ? 'array'
      : typeof result?.content,
  };

  logger.operationSuccess(
    'execute',
    resultSummary,
    OperationType.TOOL_EXECUTION,
    duration
  );
}

/**
 * Enhanced tool execution error logging with structured format
 *
 * @param toolName - The name of the tool where the error occurred
 * @param toolType - The type of tool where the error occurred
 * @param error - The error that was caught during execution
 * @param timer - Performance timer from logToolRequest
 * @param additionalInfo - Optional additional information about the execution context
 */
export function logToolError(
  toolName: string,
  toolType: string,
  error: unknown,
  timer: PerformanceTimer,
  additionalInfo: ToolErrorContext = {}
): void {
  const logger = createToolLogger(toolName, toolType);
  const duration = timer.end();

  const errorContext = {
    toolType,
    toolName,
    errorType: error instanceof Error ? error.constructor.name : typeof error,
    hasStack: error instanceof Error && !!error.stack,
    ...additionalInfo,
  };

  logger.operationFailure(
    'execute',
    error,
    errorContext,
    OperationType.TOOL_EXECUTION,
    duration
  );
}

/**
 * Log tool validation errors
 */
export function logToolValidationError(
  toolName: string,
  toolType: string,
  validationError: string,
  context?: any
): void {
  warn(
    `tool:${toolName}`,
    `Validation failed: ${validationError}`,
    context,
    toolType,
    OperationType.VALIDATION
  );
}

/**
 * Log tool configuration errors
 */
export function logToolConfigError(
  toolName: string,
  configError: string,
  context?: any
): void {
  error(
    'tool:registry',
    `Configuration error for tool ${toolName}: ${configError}`,
    undefined,
    context,
    'config-lookup',
    OperationType.SYSTEM
  );
}

/**
 * Log API fallback attempts during tool execution
 */
export function logToolFallback(
  toolName: string,
  toolType: string,
  reason: string,
  fallbackMethod: string
): void {
  warn(
    `tool:${toolName}`,
    `Using fallback method: ${fallbackMethod}`,
    { reason, fallbackMethod },
    toolType,
    OperationType.API_CALL
  );
}

/**
 * Validation utilities for tool execution
 *
 * Provides parameter validation and resource ID validation for tool requests
 */

import { createErrorResult } from '../../../utils/error-handler.js';
import { ResourceType } from '../../../types/attio.js';
import {
  AttributeValidationParams,
  ValidationResult,
} from '../../../types/tool-types.js';

/**
 * Validates attribute parameters for company operations
 *
 * @param attributes - The attributes object to validate (key-value pairs of company attributes)
 * @returns True if validation passes, or an error message string if validation fails
 */
export function validateAttributes(
  attributes: AttributeValidationParams | null | undefined
): ValidationResult {
  if (!attributes) {
    return 'Attributes parameter is required';
  }

  if (typeof attributes !== 'object' || Array.isArray(attributes)) {
    return 'Attributes parameter must be an object';
  }

  if (Object.keys(attributes).length === 0) {
    return 'Attributes parameter cannot be empty';
  }

  return true;
}

/**
 * Helper function to validate and extract resource ID based on resource type
 *
 * @param resourceType - The type of resource (companies, people, etc.)
 * @param args - The request arguments containing the ID
 * @param apiPath - The API path for error reporting
 * @returns The validated ID or an error response
 */
export function validateResourceId(
  resourceType: ResourceType,
  args: any,
  apiPath: string
): string | { error: ReturnType<typeof createErrorResult> } {
  const idParamName =
    resourceType === ResourceType.COMPANIES ? 'companyId' : 'personId';
  const id = args?.[idParamName] as string;

  if (!id) {
    return {
      error: createErrorResult(
        new Error(`${idParamName} parameter is required`),
        apiPath,
        'GET',
        {
          status: 400,
          message: `Missing required parameter: ${idParamName}`,
        }
      ),
    };
  }

  return id;
}

/**
 * Error types for tool operations
 */

/**
 * Interface for API errors with response data
 */
export interface ApiError extends Error {
  response?: {
    data: any;
    status: number;
  };
  details?: any;
}

/**
 * Interface for ValueMatchError with original error
 */
export interface ValueMatchApiError extends Error {
  originalError?: ApiError;
  details?: any;
}

/**
 * Type guard to check if error is an ApiError
 */
export function isApiError(error: unknown): error is ApiError {
  return error instanceof Error && 'response' in error;
}

/**
 * Type guard to check if error has response data
 */
export function hasResponseData(
  error: unknown
): error is { response: { data: any } } {
  return (
    error !== null &&
    typeof error === 'object' &&
    'response' in error &&
    error.response !== null &&
    typeof error.response === 'object' &&
    'data' in error.response
  );
}

/**
 * Result formatting module - handles transformation and formatting of tool results
 */
import { AttioRecord, AttioListEntry } from '../../types/attio.js';
import { processListEntries } from '../../utils/record-utils.js';
import {
  safeJsonStringify,
  sanitizeMcpResponse,
} from '../../utils/json-serializer.js';

/**
 * Safely extract value from record attributes
 *
 * This function handles various record formats and extracts values from them,
 * including standard AttioRecord objects and similar record-like structures
 * that may come from different API responses or transformations.
 *
 * @param record - The record to extract the value from, which might be an AttioRecord or a similar structure
 * @param fieldName - The name of the field to extract
 * @returns The extracted value as a string, or 'Unknown' if not found
 */
function getAttributeValue(
  record:
    | {
        values?: Record<string, unknown> | undefined;
        [key: string]: any;
      }
    | undefined,
  fieldName: string
): string {
  if (!record?.values) return 'Unknown';

  const fieldValue = record.values[fieldName];

  if (!fieldValue) return 'Unknown';

  // Handle different value formats
  if (Array.isArray(fieldValue) && fieldValue.length > 0) {
    const firstValue = fieldValue[0];
    if (
      typeof firstValue === 'object' &&
      firstValue !== null &&
      'value' in firstValue
    ) {
      return (firstValue.value as string) || 'Unknown';
    }
    return String(firstValue) || 'Unknown';
  } else if (
    typeof fieldValue === 'object' &&
    fieldValue !== null &&
    'value' in fieldValue
  ) {
    return (fieldValue.value as string) || 'Unknown';
  }

  // Fallback for any other format
  return String(fieldValue) || 'Unknown';
}

/**
 * Format search results for display
 *
 * @param results - Array of search results
 * @param resourceType - The type of resource being searched
 * @returns Formatted string output
 */
export function formatSearchResults(
  results: AttioRecord[],
  resourceType: string
): string {
  if (!results || results.length === 0) {
    return `No ${resourceType} found.`;
  }

  return results
    .map((record, index) => {
      const name = getAttributeValue(record, 'name');
      const id = record.id?.record_id || 'Unknown ID';
      return `${index + 1}. ${name} (ID: ${id})`;
    })
    .join('\n');
}

/**
 * Format record details for display
 *
 * @param record - The record to format
 * @returns Formatted string output
 */
export function formatRecordDetails(record: AttioRecord): string {
  if (!record) {
    return 'No record found.';
  }

  const attributes = record.attributes || ({} as Record<string, unknown>);
  const formattedAttrs = Object.entries(attributes)
    .map(([key, attr]) => {
      const value = (attr as any).value || 'N/A';
      return `${key}: ${value}`;
    })
    .join('\n');

  return `Record Details:\n${formattedAttrs}`;
}

/**
 * Format list entries for display
 *
 * @param entries - Array of list entries
 * @returns Formatted string output
 */
export function formatListEntries(entries: AttioListEntry[]): string {
  if (!entries || entries.length === 0) {
    return 'No entries found.';
  }

  const processedEntries = processListEntries(entries);

  return processedEntries
    .map((entry, index) => {
      const record = entry.record;
      const name = getAttributeValue(record, 'name');
      const entryId = entry.id?.entry_id || 'Unknown Entry ID';
      const recordId = record?.id?.record_id || 'Unknown ID';

      return `${index + 1}. ${name} (Entry: ${entryId}, Record: ${recordId})`;
    })
    .join('\n');
}

/**
 * Format batch operation results for display
 *
 * @param result - Batch operation result
 * @param operation - The type of batch operation
 * @returns Formatted string output
 */
export function formatBatchResults(result: any, operation: string): string {
  const summary = result.summary;
  const details = result.results
    .map((r: any) =>
      r.success
        ? ` Record ${r.id}: ${operation} successfully`
        : ` Record ${r.id}: Failed - ${r.error?.message || 'Unknown error'}`
    )
    .join('\n');

  return (
    `Batch ${operation} operation completed:\n` +
    `Total: ${summary.total}, Succeeded: ${summary.succeeded}, Failed: ${summary.failed}\n` +
    details
  );
}

/**
 * Format standard response content
 *
 * @param content - The content to format
 * @param isError - Whether this is an error response
 * @returns Formatted response object
 */
export function formatResponse(
  content: string | any,
  isError: boolean = false
) {
  // Handle non-string content by converting it to a string
  let formattedContent: string;

  if (typeof content === 'string') {
    formattedContent = content;
  } else if (content === undefined || content === null) {
    // Prevent "undefined" or "null" from being returned as response content
    formattedContent = isError
      ? 'An unknown error occurred'
      : 'Operation completed successfully, but no content was returned';
  } else {
    try {
      // Try to convert the content to a string representation using safe serialization
      formattedContent =
        typeof content === 'object'
          ? safeJsonStringify(content, {
              maxDepth: 6,
              includeStackTraces: false,
            })
          : String(content);
    } catch (error: unknown) {
      if (process.env.DEBUG || process.env.NODE_ENV === 'development') {
        console.error(
          '[formatResponse] Error converting content to string:',
          error
        );
      }
      formattedContent = 'Error: Content could not be serialized';
    }
  }

  // Ensure we never return empty content
  if (!formattedContent) {
    formattedContent = isError
      ? 'An unknown error occurred'
      : 'Operation completed successfully';
  }

  const response = {
    content: [
      {
        type: 'text',
        text: formattedContent,
      },
    ],
    isError,
  };

  // Sanitize the final response to ensure it's MCP-compatible
  return sanitizeMcpResponse(response);
}

