    };

/**
 * Find the tool config for a given tool name
 *
 * @param toolName - The name of the tool
 * @returns Tool configuration or undefined if not found
 */
export function findToolConfig(toolName: string):
  | {
      resourceType: ResourceType;
      toolConfig: ToolConfig;
      toolType: string;
    }
  | undefined {
  // Debug logging for tool lookup in development
  const debugMode = process.env.NODE_ENV === 'development' || process.env.DEBUG;

  // Debug logging for all tool lookups in development
  if (debugMode) {
    console.error(`[findToolConfig] Looking for tool: ${toolName}`);
  }

  // Search in resource-specific tools first
  for (const resourceType of Object.values(ResourceType)) {
    const resourceConfig = TOOL_CONFIGS[resourceType];
    if (!resourceConfig) {
      if (debugMode) {
        console.error(
          `[findToolConfig] No config found for resource type: ${resourceType}`
        );
      }
      continue;
    }

    // For debugging, log all available tools for a resource type
    if (debugMode) {
      const toolTypes = Object.keys(resourceConfig);
      if (toolTypes.includes(toolName.replace(/-/g, ''))) {
        console.error(
          `[findToolConfig] Tool might be found under a different name. Available tool types:`,
          toolTypes
        );
      }

      // Specific logging for commonly problematic tools
      const commonProblematicTools = [
        'discover-company-attributes',
        'get-company-basic-info',
      ];
      if (
        commonProblematicTools.includes(toolName) &&
        resourceType === ResourceType.COMPANIES
      ) {
        const toolTypeKey =
          toolName === 'discover-company-attributes'
            ? 'discoverAttributes'
            : 'basicInfo';

        // Use a type-safe way to check for existence
        const hasToolType = Object.keys(resourceConfig).includes(toolTypeKey);
        if (hasToolType) {
          const config =
            resourceConfig[toolTypeKey as keyof typeof resourceConfig];
          console.error(`[findToolConfig] Found ${toolTypeKey} config:`, {
            name: (config as any).name,
            hasHandler: typeof (config as any).handler === 'function',
            hasFormatter: typeof (config as any).formatResult === 'function',
          });
        } else {
          console.warn(
            `[findToolConfig] ${toolTypeKey} not found in ${resourceType} configs!`
          );
        }
      }
    }

    for (const [toolType, config] of Object.entries(resourceConfig)) {
      if (config && config.name === toolName) {
        if (debugMode) {
          console.error(
            `[findToolConfig] Found tool: ${toolName}, type: ${toolType}, resource: ${resourceType}`
          );
        }

        return {
          resourceType: resourceType as ResourceType,
          toolConfig: config as ToolConfig,
          toolType,
        };
      }
    }
  }

  // Search in universal tools (high priority for consolidation)
  const universalConfig = TOOL_CONFIGS.UNIVERSAL;
  if (universalConfig) {
    for (const [toolType, config] of Object.entries(universalConfig)) {
      if (config && config.name === toolName) {
        if (debugMode) {
          console.error(
            `[findToolConfig] Found universal tool: ${toolName}, type: ${toolType}, category: UNIVERSAL`
          );
        }

        return {
          resourceType: 'UNIVERSAL' as any, // Using 'UNIVERSAL' as a special marker
          toolConfig: config as ToolConfig,
          toolType,
        };
      }
    }
  }

  // Search in general tools if not found in resource-specific or universal tools
  const generalConfig = TOOL_CONFIGS.GENERAL;
  if (generalConfig) {
    for (const [toolType, config] of Object.entries(generalConfig)) {
      if (config && config.name === toolName) {
        if (debugMode) {
          console.error(
            `[findToolConfig] Found tool: ${toolName}, type: ${toolType}, category: GENERAL`
          );
        }

        return {
          resourceType: 'GENERAL' as any, // Using 'GENERAL' as a special marker
          toolConfig: config as ToolConfig,
          toolType,
        };
      }
    }
  }

  if (debugMode) {
    console.warn(`[findToolConfig] Tool not found: ${toolName}`);
  }

  return undefined;
}

#!/usr/bin/env node

/**
 * HTTP entry point for the Attio MCP server
 * Implements Streamable HTTP transport for Smithery compatibility
 */

// Load environment variables from .env file manually to avoid dotenv banner output
import * as fs from 'fs';
import * as path from 'path';

function loadEnvFile() {
  try {
    const envPath = path.resolve(process.cwd(), '.env');
    if (fs.existsSync(envPath)) {
      const envContent = fs.readFileSync(envPath, 'utf8');
      const lines = envContent.split('\n');

      for (const line of lines) {
        const trimmedLine = line.trim();
        if (trimmedLine && !trimmedLine.startsWith('#')) {
          const [key, ...valueParts] = trimmedLine.split('=');
          if (key && valueParts.length > 0) {
            const value = valueParts.join('=').replace(/^["']|["']$/g, '');
            if (!process.env[key.trim()]) {
              process.env[key.trim()] = value;
            }
          }
        }
      }
    }
  } catch (error) {
    // Silent failure to avoid stdout contamination
  }
}

loadEnvFile();

import express from 'express';
import cors from 'cors';
import { createServer } from './server/createServer.js';
import { StreamableHTTPServerTransport } from '@modelcontextprotocol/sdk/server/streamableHttp.js';
import { error as logError, OperationType } from './utils/logger.js';

const app = express();
app.use(express.json());

// Configure CORS for Smithery and local development
app.use(
  cors({
    origin: process.env.ALLOWED_ORIGINS
      ? process.env.ALLOWED_ORIGINS.split(',')
      : ['https://smithery.ai', 'http://localhost:3000'],
    allowedHeaders: ['Content-Type', 'Mcp-Session-Id', 'mcp-session-id'],
    exposedHeaders: ['Mcp-Session-Id'],
  })
);

// Ensure clean preflight handling for Streamable HTTP endpoint
app.options('/mcp', cors());

// Health check endpoint for load balancers
app.get('/health', (_req, res) => {
  res.json({ status: 'healthy', timestamp: Date.now() });
});

// Main MCP endpoint - stateless MVP (new transport per request)
app.all('/mcp', async (req, res) => {
  try {
    const server = createServer();
    // Stateless mode - no session management for simplicity
    const transport = new StreamableHTTPServerTransport({
      sessionIdGenerator: undefined, // Stateless mode
    });

    // Clean up when connection closes
    res.on('close', () => {
      transport.close();
      server.close();
    });

    await server.connect(transport);
    await transport.handleRequest(req, res, req.body);
  } catch (error) {
    logError(
      'http',
      'Error handling MCP request',
      error,
      undefined,
      'mcp-request-error',
      OperationType.API_CALL
    );

    if (!res.headersSent) {
      res.status(500).json({
        jsonrpc: '2.0',
        error: {
          code: -32603,
          message: 'Internal server error',
        },
        id: null,
      });
    }
  }
});

// Start server
const PORT = Number(process.env.PORT) || 3000;

app
  .listen(PORT, () => {
    // Use stderr to avoid contaminating any stdout protocol streams in mixed environments
    console.error(`Attio MCP HTTP Server listening on port ${PORT}`);
    console.error(`Health check: http://localhost:${PORT}/health`);
    console.error(`MCP endpoint: http://localhost:${PORT}/mcp`);
  })
  .on('error', (error) => {
    logError(
      'http',
      'Failed to start HTTP server',
      error,
      { port: PORT },
      'server-startup',
      OperationType.SYSTEM
    );
    process.exit(1);
  });

#!/usr/bin/env node

// Load environment variables from .env file manually to avoid dotenv banner output
// This ensures MCP JSON-RPC protocol compliance by preventing stdout contamination
import * as fs from 'fs';
import * as path from 'path';

function loadEnvFile() {
  try {
    const envPath = path.resolve(process.cwd(), '.env');
    if (fs.existsSync(envPath)) {
      const envContent = fs.readFileSync(envPath, 'utf8');
      const lines = envContent.split('\n');

      for (const line of lines) {
        const trimmedLine = line.trim();
        if (trimmedLine && !trimmedLine.startsWith('#')) {
          const [key, ...valueParts] = trimmedLine.split('=');
          if (key && valueParts.length > 0) {
            const value = valueParts.join('=').replace(/^["']|["']$/g, '');
            if (!process.env[key.trim()]) {
              process.env[key.trim()] = value;
            }
          }
        }
      }
    }
  } catch (error) {
    // Silent failure to avoid stdout contamination
    // Environment variables will need to be set manually if .env loading fails
  }
}

loadEnvFile();
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import { createServer } from './server/createServer.js';
import { error as logError, OperationType } from './utils/logger.js';

// Main function - simplified MCP server following FastMCP patterns
async function main() {
  try {
    // Create the configured MCP server
    const mcpServer = createServer();

    // Handle EPIPE errors gracefully (broken pipe during shutdown)
    process.stdout.on('error', (error: NodeJS.ErrnoException) => {
      if (error.code === 'EPIPE') {
        // Graceful exit on broken pipe - Claude Desktop closed connection
        process.exit(0);
      } else {
        console.error('[MCP] stdout error:', error);
      }
    });

    process.stderr.on('error', (error: NodeJS.ErrnoException) => {
      if (error.code === 'EPIPE') {
        // Graceful exit on broken pipe - Claude Desktop closed connection
        process.exit(0);
      } else {
        console.error('[MCP] stderr error:', error);
      }
    });

    // Global handler for uncaught EPIPE errors from MCP SDK
    process.on('uncaughtException', (error: NodeJS.ErrnoException) => {
      if (error.code === 'EPIPE') {
        // Graceful exit on broken pipe - Claude Desktop closed connection
        process.exit(0);
      } else {
        console.error('[MCP] Uncaught exception:', error);
        // Re-throw other uncaught exceptions
        throw error;
      }
    });

    // Connect to stdio transport - this is all we need!
    const transport = new StdioServerTransport();
    await mcpServer.connect(transport);

    // Server is now running and will process requests via stdio
    // Claude Desktop manages the process lifecycle - no PID files or health checks needed
  } catch (error: unknown) {
    logError(
      'main',
      'Server startup failed',
      error,
      { pid: process.pid },
      'server-startup',
      OperationType.SYSTEM
    );
    process.exit(1);
  }
}

main().catch((error) => {
  logError(
    'main',
    'Unhandled error in main process',
    error,
    { pid: process.pid },
    'main-unhandled',
    OperationType.SYSTEM
  );
  process.exit(1);
});

/**
 * Centralized HTTP  MCP error mapping
 * Converts HTTP responses to MCP-compliant result format
 */

export interface HttpResponse {
  status: number;
  body: any;
}

export interface McpResult {
  isError: boolean;
  content?: Array<{ type: 'text'; text: string }>;
  error?: {
    code: number;
    type: string;
    message?: string;
  };
}

/**
 * Convert HTTP response to MCP-compliant result format
 * Non-2xx responses become isError: true with appropriate error messages
 */
export function toMcpResult(resp: HttpResponse): McpResult {
  if (resp.status >= 200 && resp.status < 300) {
    return {
      isError: false,
      content: [{ type: 'text', text: JSON.stringify(resp.body) }],
    };
  }

  // Check if we have a structured error with code and type
  if (resp.body?.code && resp.body?.type) {
    return {
      isError: true,
      error: {
        code: resp.status,
        type: resp.body.type,
        message: resp.body.message,
      },
      content: [
        { type: 'text', text: resp.body.message || `HTTP ${resp.status}` },
      ],
    };
  }

  // Fallback to simple message format
  const msg =
    resp.body?.message ||
    resp.body?.error?.message ||
    (resp.status === 404
      ? 'record not found'
      : resp.status === 400
        ? 'invalid record_id format'
        : `HTTP ${resp.status}`);

  return {
    isError: true,
    content: [{ type: 'text', text: msg }],
  };
}

/**
 * Check if a result looks like an HTTP response
 */
export function isHttpResponseLike(result: any): result is HttpResponse {
  return (
    result &&
    typeof result === 'object' &&
    typeof result.status === 'number' &&
    'body' in result
  );
}

/**
 * Performance Monitor Compatibility Layer
 *
 * Provides instance-based singleton API for legacy tests while using
 * the improved static PerformanceTracker implementation.
 */

import {
  PerformanceTracker,
  PerformanceMetrics,
  PerformanceSummary,
} from './performance.js';

/**
 * Instance-based wrapper for PerformanceTracker static methods
 */
class PerformanceMonitorInstance {
  private static _instance: PerformanceMonitorInstance | undefined;
  private activeOperations: Map<string, number> = new Map();

  static getInstance(): PerformanceMonitorInstance {
    if (!this._instance) {
      this._instance = new PerformanceMonitorInstance();
    }
    return this._instance;
  }

  // Instance methods that delegate to static PerformanceTracker methods
  startOperation(toolName: string, metadata?: Record<string, unknown>): number {
    const startTime = PerformanceTracker.startOperation(toolName, metadata);
    this.activeOperations.set(toolName, startTime);
    return startTime;
  }

  endOperation(
    toolName: string,
    startTimeOrSuccess?: number | boolean,
    success: boolean = true,
    error?: string,
    metadata?: Record<string, unknown>
  ): PerformanceMetrics {
    // Support both patterns:
    // 1. endOperation(toolName, startTime, success, error, metadata) - explicit startTime
    // 2. endOperation(toolName, success, error, metadata) - use stored startTime

    let startTime: number;
    let actualSuccess: boolean;
    let actualError: string | undefined;
    let actualMetadata: Record<string, unknown> | undefined;

    if (typeof startTimeOrSuccess === 'number') {
      // Pattern 1: explicit startTime provided
      startTime = startTimeOrSuccess;
      actualSuccess = success;
      actualError = error;
      actualMetadata = metadata;
    } else {
      // Pattern 2: use stored startTime
      const storedStartTime = this.activeOperations.get(toolName);
      if (storedStartTime === undefined) {
        throw new Error(
          `No active operation found for tool: ${toolName}. Call startOperation() first.`
        );
      }
      startTime = storedStartTime;
      actualSuccess =
        typeof startTimeOrSuccess === 'boolean' ? startTimeOrSuccess : true;
      actualError = typeof success === 'string' ? (success as any) : error;
      actualMetadata =
        error && typeof error === 'object' ? (error as any) : metadata;

      // Clean up stored operation
      this.activeOperations.delete(toolName);
    }

    return PerformanceTracker.endOperation(
      toolName,
      startTime,
      actualSuccess,
      actualError,
      actualMetadata
    );
  }

  getSummary(toolName?: string): PerformanceSummary {
    return PerformanceTracker.getSummary(toolName);
  }

  clear(): void {
    this.activeOperations.clear();
    return PerformanceTracker.clear();
  }

  reset(): void {
    // Alias for clear() to match legacy test expectations
    this.activeOperations.clear();
    return PerformanceTracker.clear();
  }

  getMetrics(): {
    totalOperations: number;
    averageTime: number;
    slowOperations: number;
  } {
    const summary = PerformanceTracker.getSummary();
    const slowOps = PerformanceTracker.getSlowOperations(100);

    return {
      totalOperations: summary.totalOperations,
      averageTime: summary.averageDuration,
      slowOperations: slowOps.length,
    };
  }

  isEnabled(): boolean {
    return PerformanceTracker.isEnabled();
  }

  setEnabled(enabled: boolean): void {
    return PerformanceTracker.setEnabled(enabled);
  }

  async measureAsync<T>(
    toolName: string,
    fn: () => Promise<T>,
    metadata?: Record<string, unknown>
  ): Promise<T> {
    return PerformanceTracker.measureAsync(toolName, fn, metadata);
  }

  measure<T>(
    toolName: string,
    fn: () => T,
    metadata?: Record<string, unknown>
  ): T {
    return PerformanceTracker.measure(toolName, fn, metadata);
  }

  getSlowOperations(threshold?: number): PerformanceMetrics[] {
    return PerformanceTracker.getSlowOperations(threshold);
  }

  getFailedOperations(): PerformanceMetrics[] {
    return PerformanceTracker.getFailedOperations();
  }

  generateReport(): string {
    return PerformanceTracker.generateReport();
  }
}

/**
 * Singleton PerformanceMonitor for backward compatibility
 */
export const PerformanceMonitor = PerformanceMonitorInstance;

// Export the instance for direct usage
export const performanceMonitor = PerformanceMonitorInstance.getInstance();

/**
 * Enhanced Performance Tracking Middleware for Phase 2
 *
 * Provides advanced performance monitoring, timing splits, and automated alerts
 * for the Attio MCP Server. Tracks API vs MCP overhead, performance budgets,
 * and regression detection.
 */

import { performance } from 'perf_hooks';
import { EventEmitter } from 'events';

/**
 * Performance timing breakdown
 */
export interface TimingSplit {
  total: number;
  attioApi: number;
  mcpOverhead: number;
  validation: number;
  serialization: number;
  other: number;
}

/**
 * Enhanced performance metrics with detailed timing
 */
export interface EnhancedPerformanceMetrics {
  toolName: string;
  operationType: string;
  resourceType?: string;
  startTime: number;
  endTime: number;
  duration: number;
  timingSplit: TimingSplit;
  success: boolean;
  cached: boolean;
  error?: string;
  statusCode?: number;
  recordCount?: number;
  metadata?: Record<string, unknown>;
}

/**
 * Performance budget configuration
 */
export interface PerformanceBudget {
  notFound: number; // Target for 404 responses (2000ms)
  search: number; // Target for search operations (3000ms)
  create: number; // Target for create operations (3000ms)
  update: number; // Target for update operations (3000ms)
  delete: number; // Target for delete operations (2000ms)
  batchSmall: number; // Target for batch operations < 10 items (5000ms)
  batchLarge: number; // Target for batch operations >= 10 items (10000ms)
  default: number; // Default budget for other operations (3000ms)
}

/**
 * Performance alert levels
 */
export enum AlertLevel {
  INFO = 'info',
  WARNING = 'warning',
  CRITICAL = 'critical',
}

/**
 * Performance alert
 */
export interface PerformanceAlert {
  level: AlertLevel;
  toolName: string;
  operationType: string;
  duration: number;
  budget: number;
  percentOver: number;
  timestamp: Date;
  message: string;
}

/**
 * Cache entry for 404 responses
 */
interface CacheEntry {
  timestamp: number;
  result: unknown;
  ttl: number;
}

/**
 * Enhanced Performance Tracking Service
 */
export class EnhancedPerformanceTracker extends EventEmitter {
  private static instance: EnhancedPerformanceTracker;
  private metrics: EnhancedPerformanceMetrics[] = [];
  private cache404: Map<string, CacheEntry> = new Map();
  private budgets: PerformanceBudget;
  private alerts: PerformanceAlert[] = [];
  private maxMetrics: number;
  private metricsIndex: number = 0;
  private isBufferFull: boolean = false;
  private enabled: boolean = true;
  private degradationThreshold: number = 0.2; // 20% degradation threshold

  // Timing context for current operation
  private timingContext: Map<string, any> = new Map();

  private constructor() {
    super();

    this.maxMetrics = parseInt(process.env.PERF_MAX_METRICS || '5000', 10);

    // Initialize performance budgets
    this.budgets = {
      notFound: parseInt(process.env.PERF_BUDGET_NOT_FOUND || '2000', 10),
      search: parseInt(process.env.PERF_BUDGET_SEARCH || '3000', 10),
      create: parseInt(process.env.PERF_BUDGET_CREATE || '3000', 10),
      update: parseInt(process.env.PERF_BUDGET_UPDATE || '3000', 10),
      delete: parseInt(process.env.PERF_BUDGET_DELETE || '2000', 10),
      batchSmall: parseInt(process.env.PERF_BUDGET_BATCH_SMALL || '5000', 10),
      batchLarge: parseInt(process.env.PERF_BUDGET_BATCH_LARGE || '10000', 10),
      default: parseInt(process.env.PERF_BUDGET_DEFAULT || '3000', 10),
    };

    // Set up cache cleanup interval (every 5 minutes)
    setInterval(() => this.cleanupCache(), 5 * 60 * 1000);
  }

  /**
   * Get singleton instance
   */
  static getInstance(): EnhancedPerformanceTracker {
    if (!this.instance) {
      this.instance = new EnhancedPerformanceTracker();
    }
    return this.instance;
  }

  /**
   * Start tracking an operation with context
   */
  startOperation(
    toolName: string,
    operationType: string,
    metadata?: Record<string, unknown>
  ): string {
    if (!this.enabled) return '';

    const operationId = `${toolName}-${Date.now()}-${Math.random()}`;
    const startTime = performance.now();

    this.timingContext.set(operationId, {
      toolName,
      operationType,
      startTime,
      metadata,
      timings: {
        validation: 0,
        attioApi: 0,
        serialization: 0,
        other: 0,
      },
    });

    return operationId;
  }

  /**
   * Mark a timing checkpoint
   */
  markTiming(
    operationId: string,
    phase: keyof TimingSplit,
    duration: number
  ): void {
    const context = this.timingContext.get(operationId);
    if (context && context.timings[phase] !== undefined) {
      context.timings[phase] += duration;
    }
  }

  /**
   * Mark API call start
   */
  markApiStart(_operationId: string): number {
    return performance.now();
  }

  /**
   * Mark API call end
   */
  markApiEnd(operationId: string, apiStartTime: number): void {
    const duration = performance.now() - apiStartTime;
    this.markTiming(operationId, 'attioApi', duration);
  }

  /**
   * End tracking an operation
   */
  endOperation(
    operationId: string,
    success: boolean = true,
    error?: string,
    statusCode?: number,
    additionalMetadata?: Record<string, any>
  ): EnhancedPerformanceMetrics | null {
    if (!this.enabled || !operationId) return null;

    const context = this.timingContext.get(operationId);
    if (!context) return null;

    const endTime = performance.now();
    const duration = endTime - context.startTime;

    // Calculate MCP overhead
    const mcpOverhead =
      duration -
      (context.timings.validation +
        context.timings.attioApi +
        context.timings.serialization +
        context.timings.other);

    const metrics: EnhancedPerformanceMetrics = {
      toolName: context.toolName,
      operationType: context.operationType,
      resourceType: context.metadata?.resourceType,
      startTime: context.startTime,
      endTime,
      duration,
      timingSplit: {
        total: duration,
        attioApi: context.timings.attioApi,
        mcpOverhead: Math.max(0, mcpOverhead),
        validation: context.timings.validation,
        serialization: context.timings.serialization,
        other: context.timings.other,
      },
      success,
      cached: false,
      error,
      statusCode,
      recordCount: additionalMetadata?.recordCount,
      metadata: { ...context.metadata, ...additionalMetadata },
    };

    // Store metrics
    this.storeMetrics(metrics);

    // Check performance budget
    this.checkBudget(metrics);

    // Clean up context
    this.timingContext.delete(operationId);

    // Log in development
    if (process.env.NODE_ENV === 'development') {
      this.logMetrics(metrics);
    }

    return metrics;
  }

  /**
   * Store metrics in circular buffer
   */
  private storeMetrics(metrics: EnhancedPerformanceMetrics): void {
    if (this.isBufferFull) {
      this.metrics[this.metricsIndex] = metrics;
    } else {
      this.metrics.push(metrics);
      if (this.metrics.length >= this.maxMetrics) {
        this.isBufferFull = true;
      }
    }

    this.metricsIndex = (this.metricsIndex + 1) % this.maxMetrics;
  }

  /**
   * Check if operation exceeds budget
   */
  private checkBudget(metrics: EnhancedPerformanceMetrics): void {
    const budget = this.getBudgetForOperation(
      metrics.operationType,
      metrics.statusCode,
      metrics.recordCount
    );

    if (metrics.duration > budget) {
      const percentOver = ((metrics.duration - budget) / budget) * 100;

      let level: AlertLevel;
      if (percentOver > 100) {
        level = AlertLevel.CRITICAL;
      } else if (percentOver > this.degradationThreshold * 100) {
        level = AlertLevel.WARNING;
      } else {
        level = AlertLevel.INFO;
      }

      const alert: PerformanceAlert = {
        level,
        toolName: metrics.toolName,
        operationType: metrics.operationType,
        duration: metrics.duration,
        budget,
        percentOver,
        timestamp: new Date(),
        message: `${metrics.toolName} (${
          metrics.operationType
        }) exceeded budget by ${percentOver.toFixed(
          1
        )}% (${metrics.duration.toFixed(0)}ms vs ${budget}ms budget)`,
      };

      this.alerts.push(alert);
      this.emit('performanceAlert', alert);

      // Log critical alerts immediately using structured logging
      if (level === AlertLevel.CRITICAL) {
        process.stderr.write(
          JSON.stringify({
            level: 'error',
            message: `PERFORMANCE CRITICAL: ${alert.message}`,
            type: 'performance_alert',
            data: alert,
          }) + '\n'
        );
      } else if (level === AlertLevel.WARNING) {
        process.stderr.write(
          JSON.stringify({
            level: 'warn',
            message: `PERFORMANCE WARNING: ${alert.message}`,
            type: 'performance_alert',
            data: alert,
          }) + '\n'
        );
      }
    }
  }

  /**
   * Get budget for specific operation
   */
  private getBudgetForOperation(
    operationType: string,
    statusCode?: number,
    recordCount?: number
  ): number {
    // 404 responses have their own budget
    if (statusCode === 404) {
      return this.budgets.notFound;
    }

    // Map operation types to budgets
    switch (operationType.toLowerCase()) {
      case 'search':
      case 'list':
      case 'query':
        return this.budgets.search;
      case 'create':
        return this.budgets.create;
      case 'update':
        return this.budgets.update;
      case 'delete':
        return this.budgets.delete;
      case 'batch':
        return recordCount && recordCount >= 10
          ? this.budgets.batchLarge
          : this.budgets.batchSmall;
      default:
        return this.budgets.default;
    }
  }

  /**
   * Cache a 404 response
   */
  cache404Response(key: string, result: unknown, ttl: number = 60000): void {
    this.cache404.set(key, {
      timestamp: Date.now(),
      result,
      ttl,
    });
  }

  /**
   * Get cached 404 response
   */
  getCached404(key: string): unknown | null {
    const entry = this.cache404.get(key);
    if (!entry) return null;

    const age = Date.now() - entry.timestamp;
    if (age > entry.ttl) {
      this.cache404.delete(key);
      return null;
    }

    return entry.result;
  }

  /**
   * Clean up expired cache entries
   */
  private cleanupCache(): void {
    const now = Date.now();
    for (const [key, entry] of Array.from(this.cache404.entries())) {
      if (now - entry.timestamp > entry.ttl) {
        this.cache404.delete(key);
      }
    }
  }

  /**
   * Get performance statistics
   */
  getStatistics(
    toolName?: string,
    timeWindow?: number
  ): Record<string, unknown> {
    const now = Date.now();
    const windowStart = timeWindow ? now - timeWindow : 0;

    let relevantMetrics = this.metrics;

    if (toolName) {
      relevantMetrics = relevantMetrics.filter((m) => m.toolName === toolName);
    }

    if (timeWindow) {
      relevantMetrics = relevantMetrics.filter(
        (m) => m.startTime >= windowStart
      );
    }

    if (relevantMetrics.length === 0) {
      return {};
    }

    const durations = relevantMetrics
      .map((m) => m.duration)
      .sort((a, b) => a - b);
    const apiTimes = relevantMetrics
      .map((m) => m.timingSplit.attioApi)
      .sort((a, b) => a - b);
    const overheads = relevantMetrics
      .map((m) => m.timingSplit.mcpOverhead)
      .sort((a, b) => a - b);

    return {
      count: relevantMetrics.length,
      successRate:
        (relevantMetrics.filter((m) => m.success).length /
          relevantMetrics.length) *
        100,
      cacheHitRate:
        (relevantMetrics.filter((m) => m.cached).length /
          relevantMetrics.length) *
        100,
      timing: {
        average: this.average(durations),
        min: durations[0],
        max: durations[durations.length - 1],
        p50: this.percentile(durations, 50),
        p95: this.percentile(durations, 95),
        p99: this.percentile(durations, 99),
      },
      apiTiming: {
        average: this.average(apiTimes),
        p50: this.percentile(apiTimes, 50),
        p95: this.percentile(apiTimes, 95),
        p99: this.percentile(apiTimes, 99),
      },
      overhead: {
        average: this.average(overheads),
        p50: this.percentile(overheads, 50),
        p95: this.percentile(overheads, 95),
        p99: this.percentile(overheads, 99),
      },
      budgetViolations: this.alerts.filter(
        (a) => !toolName || a.toolName === toolName
      ).length,
    };
  }

  /**
   * Calculate average
   */
  private average(values: number[]): number {
    if (values.length === 0) return 0;
    return values.reduce((a, b) => a + b, 0) / values.length;
  }

  /**
   * Calculate percentile
   */
  private percentile(sortedValues: number[], percentile: number): number {
    if (sortedValues.length === 0) return 0;
    const index = Math.ceil((percentile / 100) * sortedValues.length) - 1;
    return sortedValues[Math.max(0, index)];
  }

  /**
   * Log metrics in development
   */
  private logMetrics(metrics: EnhancedPerformanceMetrics): void {
    const icon = metrics.success ? '' : '';
    const timeIcon =
      metrics.duration >
      this.getBudgetForOperation(metrics.operationType, metrics.statusCode)
        ? ''
        : metrics.duration > 1000
          ? ''
          : '';

    console.error(
      `${icon} ${metrics.toolName} (${
        metrics.operationType
      }): ${timeIcon} ${metrics.duration.toFixed(0)}ms ` +
        `[API: ${metrics.timingSplit.attioApi.toFixed(
          0
        )}ms, MCP: ${metrics.timingSplit.mcpOverhead.toFixed(0)}ms]` +
        (metrics.cached ? '  CACHED' : '') +
        (metrics.error ? `  ${metrics.error}` : '')
    );
  }

  /**
   * Generate performance report
   */
  generateReport(): string {
    const stats = this.getStatistics();
    const recentAlerts = this.alerts.slice(-10);

    if (!stats) {
      return 'No performance data available';
    }

    return `
Performance Report
==================
Total Operations: ${stats.count}
Success Rate: ${(stats.successRate as any).toFixed(1)}%
Cache Hit Rate: ${(stats.cacheHitRate as any).toFixed(1)}%

Timing Statistics (ms)
----------------------
Average: ${(stats.timing as any).average.toFixed(0)}
Min: ${(stats.timing as any).min.toFixed(0)}
Max: ${(stats.timing as any).max.toFixed(0)}
P50: ${(stats.timing as any).p50.toFixed(0)}
P95: ${(stats.timing as any).p95.toFixed(0)}
P99: ${(stats.timing as any).p99.toFixed(0)}

API vs MCP Overhead (ms)
------------------------
API Average: ${(stats.apiTiming as any).average.toFixed(0)}
API P95: ${(stats.apiTiming as any).p95.toFixed(0)}
API P99: ${(stats.apiTiming as any).p99.toFixed(0)}
MCP Average: ${(stats.overhead as any).average.toFixed(0)}
MCP P95: ${(stats.overhead as any).p95.toFixed(0)}
MCP P99: ${(stats.overhead as any).p99.toFixed(0)}

Budget Violations: ${stats.budgetViolations}

Recent Alerts
-------------
${recentAlerts
  .map(
    (a) =>
      `[${a.level.toUpperCase()}] ${a.toolName}: ${a.duration.toFixed(
        0
      )}ms (${a.percentOver.toFixed(0)}% over budget)`
  )
  .join('\n')}
    `.trim();
  }

  /**
   * Clear all data
   */
  clear(): void {
    this.metrics = [];
    this.alerts = [];
    this.cache404.clear();
    this.timingContext.clear();
    this.metricsIndex = 0;
    this.isBufferFull = false;
  }

  /**
   * Export metrics for analysis
   */
  exportMetrics(): Record<string, unknown> {
    return {
      timestamp: new Date().toISOString(),
      metrics: this.metrics,
      statistics: this.getStatistics(),
      alerts: this.alerts,
      budgets: this.budgets,
    };
  }
}

// Export singleton instance
export const enhancedPerformanceTracker =
  EnhancedPerformanceTracker.getInstance();

/**
 * Performance Tracking Middleware
 *
 * Provides performance monitoring and timing for all MCP tool operations.
 * Tracks execution time, API latency, and performance bottlenecks.
 */

import { performance } from 'perf_hooks';

/**
 * Performance metrics for a single operation
 */
export interface PerformanceMetrics {
  toolName: string;
  startTime: number;
  endTime: number;
  duration: number;
  success: boolean;
  error?: string;
  metadata?: Record<string, unknown>;
}

/**
 * Performance thresholds for alerting
 */
export interface PerformanceThresholds {
  warning: number; // milliseconds
  critical: number; // milliseconds
}

/**
 * Performance summary statistics
 */
export interface PerformanceSummary {
  totalOperations: number;
  successfulOperations: number;
  failedOperations: number;
  averageDuration: number;
  minDuration: number;
  maxDuration: number;
  p50Duration: number;
  p95Duration: number;
  p99Duration: number;
}

/**
 * Performance tracking service
 */
export class PerformanceTracker {
  private static metrics: PerformanceMetrics[] = [];
  private static thresholds: Map<string, PerformanceThresholds> = new Map();
  private static enabled: boolean =
    process.env.PERFORMANCE_TRACKING !== 'false';
  private static readonly maxMetrics: number = parseInt(
    process.env.PERF_MAX_METRICS || '1000',
    10
  );
  private static metricsIndex: number = 0;
  private static isBufferFull: boolean = false;

  /**
   * Set performance thresholds for a tool
   */
  static setThresholds(
    toolName: string,
    thresholds: PerformanceThresholds
  ): void {
    this.thresholds.set(toolName, thresholds);
  }

  /**
   * Get default thresholds
   */
  static getDefaultThresholds(): PerformanceThresholds {
    return {
      warning: 1000, // 1 second
      critical: 5000, // 5 seconds
    };
  }

  /**
   * Start tracking an operation
   */
  static startOperation(
    toolName: string,
    _metadata?: Record<string, unknown>
  ): number {
    if (!this.enabled) return 0;

    const startTime = performance.now();

    // Log slow operations in development
    if (process.env.NODE_ENV === 'development') {
      const thresholds =
        this.thresholds.get(toolName) || this.getDefaultThresholds();

      // Set a timeout to warn about slow operations
      setTimeout(() => {
        const duration = performance.now() - startTime;
        if (duration > thresholds.critical) {
          console.warn(
            ` Critical: ${toolName} is taking too long (${duration.toFixed(
              2
            )}ms)`
          );
        } else if (duration > thresholds.warning) {
          console.warn(
            ` Warning: ${toolName} is slow (${duration.toFixed(2)}ms)`
          );
        }
      }, thresholds.warning);
    }

    return startTime;
  }

  /**
   * End tracking an operation
   */
  static endOperation(
    toolName: string,
    startTime: number,
    success: boolean = true,
    error?: string,
    metadata?: Record<string, unknown>
  ): PerformanceMetrics {
    if (!this.enabled) {
      return {
        toolName,
        startTime: 0,
        endTime: 0,
        duration: 0,
        success,
      };
    }

    const endTime = performance.now();
    const duration = endTime - startTime;

    const metrics: PerformanceMetrics = {
      toolName,
      startTime,
      endTime,
      duration,
      success,
      error,
      metadata,
    };

    // Use circular buffer for efficient memory management
    if (this.isBufferFull) {
      // Overwrite oldest entry
      this.metrics[this.metricsIndex] = metrics;
    } else {
      // Still filling the buffer
      this.metrics.push(metrics);
      if (this.metrics.length >= this.maxMetrics) {
        this.isBufferFull = true;
      }
    }

    // Update circular buffer index
    this.metricsIndex = (this.metricsIndex + 1) % this.maxMetrics;

    // Log performance in development
    if (process.env.NODE_ENV === 'development') {
      const thresholds =
        this.thresholds.get(toolName) || this.getDefaultThresholds();
      const statusIcon = success ? '' : '';
      const timeColor =
        duration > thresholds.critical
          ? ''
          : duration > thresholds.warning
            ? ''
            : '';

      console.error(
        `${statusIcon} ${toolName}: ${timeColor} ${duration.toFixed(2)}ms` +
          (error ? ` (Error: ${error})` : '')
      );
    }

    return metrics;
  }

  /**
   * Get performance summary for a tool
   */
  static getSummary(toolName?: string): PerformanceSummary {
    const relevantMetrics = toolName
      ? this.metrics.filter((m) => m.toolName === toolName)
      : this.metrics;

    if (relevantMetrics.length === 0) {
      return {
        totalOperations: 0,
        successfulOperations: 0,
        failedOperations: 0,
        averageDuration: 0,
        minDuration: 0,
        maxDuration: 0,
        p50Duration: 0,
        p95Duration: 0,
        p99Duration: 0,
      };
    }

    const durations = relevantMetrics
      .map((m) => m.duration)
      .sort((a, b) => a - b);
    const successCount = relevantMetrics.filter((m) => m.success).length;
    const failureCount = relevantMetrics.length - successCount;

    return {
      totalOperations: relevantMetrics.length,
      successfulOperations: successCount,
      failedOperations: failureCount,
      averageDuration:
        durations.length > 0
          ? durations.reduce((a, b) => a + b, 0) / durations.length
          : 0,
      minDuration: durations.length > 0 ? durations[0] : 0,
      maxDuration: durations.length > 0 ? durations[durations.length - 1] : 0,
      p50Duration: durations.length > 0 ? this.getPercentile(durations, 50) : 0,
      p95Duration: durations.length > 0 ? this.getPercentile(durations, 95) : 0,
      p99Duration: durations.length > 0 ? this.getPercentile(durations, 99) : 0,
    };
  }

  /**
   * Get percentile value from sorted array
   */
  private static getPercentile(
    sortedArray: number[],
    percentile: number
  ): number {
    if (sortedArray.length === 0) return 0;
    const index = Math.ceil((percentile / 100) * sortedArray.length) - 1;
    return sortedArray[Math.max(0, index)];
  }

  /**
   * Clear all metrics
   */
  static clear(): void {
    this.metrics = [];
    this.metricsIndex = 0;
    this.isBufferFull = false;
  }

  /**
   * Get all metrics
   */
  static getMetrics(): PerformanceMetrics[] {
    return [...this.metrics];
  }

  /**
   * Export metrics as JSON
   */
  static exportMetrics(): string {
    return JSON.stringify(
      {
        timestamp: new Date().toISOString(),
        enabled: this.enabled,
        metrics: this.metrics,
        summary: this.getSummary(),
      },
      null,
      2
    );
  }

  /**
   * Enable or disable tracking
   */
  static setEnabled(enabled: boolean): void {
    this.enabled = enabled;
  }

  /**
   * Check if tracking is enabled
   */
  static isEnabled(): boolean {
    return this.enabled;
  }

  /**
   * Measure async function performance
   */
  static async measureAsync<T>(
    toolName: string,
    fn: () => Promise<T>,
    metadata?: Record<string, unknown>
  ): Promise<T> {
    const startTime = this.startOperation(toolName, metadata);

    try {
      const result = await fn();
      this.endOperation(toolName, startTime, true, undefined, metadata);
      return result;
    } catch (error: unknown) {
      this.endOperation(
        toolName,
        startTime,
        false,
        error instanceof Error ? error.message : String(error),
        metadata
      );
      throw error;
    }
  }

  /**
   * Measure sync function performance
   */
  static measure<T>(
    toolName: string,
    fn: () => T,
    metadata?: Record<string, unknown>
  ): T {
    const startTime = this.startOperation(toolName, metadata);

    try {
      const result = fn();
      this.endOperation(toolName, startTime, true, undefined, metadata);
      return result;
    } catch (error: unknown) {
      this.endOperation(
        toolName,
        startTime,
        false,
        error instanceof Error ? error.message : String(error),
        metadata
      );
      throw error;
    }
  }

  /**
   * Get slow operations above threshold
   */
  static getSlowOperations(threshold?: number): PerformanceMetrics[] {
    const limit = threshold || 1000; // Default 1 second
    return this.metrics.filter((m) => m.duration > limit);
  }

  /**
   * Get failed operations
   */
  static getFailedOperations(): PerformanceMetrics[] {
    return this.metrics.filter((m) => !m.success);
  }

  /**
   * Generate performance report
   */
  static generateReport(): string {
    const summary = this.getSummary();
    const slowOps = this.getSlowOperations();
    const failedOps = this.getFailedOperations();

    return `
Performance Report
==================
Total Operations: ${summary.totalOperations}
Successful: ${summary.successfulOperations} (${(
      (summary.successfulOperations / summary.totalOperations) *
      100
    ).toFixed(1)}%)
Failed: ${summary.failedOperations} (${(
      (summary.failedOperations / summary.totalOperations) *
      100
    ).toFixed(1)}%)

Timing Statistics
-----------------
Average: ${summary.averageDuration.toFixed(2)}ms
Min: ${summary.minDuration.toFixed(2)}ms
Max: ${summary.maxDuration.toFixed(2)}ms
P50: ${summary.p50Duration.toFixed(2)}ms
P95: ${summary.p95Duration.toFixed(2)}ms
P99: ${summary.p99Duration.toFixed(2)}ms

Slow Operations: ${slowOps.length}
Failed Operations: ${failedOps.length}
    `.trim();
  }
}

/**
 * Performance monitoring decorator
 */
export function trackPerformance(toolName?: string) {
  return function (
    target: unknown,
    propertyKey: string,
    descriptor: PropertyDescriptor
  ) {
    const originalMethod = descriptor.value;
    const name =
      toolName || `${(target as any).constructor.name}.${propertyKey}`;

    descriptor.value = async function (...args: unknown[]) {
      const startTime = PerformanceTracker.startOperation(name);

      try {
        const result = await originalMethod.apply(this, args);
        PerformanceTracker.endOperation(name, startTime, true);
        return result;
      } catch (error: unknown) {
        PerformanceTracker.endOperation(
          name,
          startTime,
          false,
          error instanceof Error ? error.message : String(error)
        );
        throw error;
      }
    };

    return descriptor;
  };
}

