/**
 * JSON Schema Validation Middleware
 *
 * Provides strict JSON-schema validation for all MCP tools
 * to prevent runtime type errors from reaching business logic.
 *
 * This middleware validates all tool parameters against their schemas
 * and returns proper 4xx ValidationError for schema violations.
 */

import {
  UniversalValidationError,
  ErrorType,
  HttpStatusCode,
  InputSanitizer,
  SanitizedValue,
  SanitizedObject,
} from '../handlers/tool-configs/universal/schemas.js';

/**
 * Schema validation result
 */
export interface ValidationResult {
  valid: boolean;
  sanitizedParams?: SanitizedObject;
  errors?: ValidationError[];
}

/**
 * Validation error details
 */
export interface ValidationError {
  field: string;
  message: string;
  value?: unknown;
  expected?: string;
  suggestion?: string;
}

/**
 * Schema definition for validation
 */
export interface SchemaDefinition {
  type: string;
  properties?: Record<string, unknown>;
  required?: string[];
  additionalProperties?: boolean;
  enum?: string[];
  minimum?: number;
  maximum?: number;
  minLength?: number;
  maxLength?: number;
  pattern?: string;
  format?: string;
  default?: unknown;
}

/**
 * Validates a value against a JSON schema
 */
export class JsonSchemaValidator {
  /**
   * Validate parameters against a JSON schema
   */
  static validate(params: unknown, schema: SchemaDefinition): ValidationResult {
    // First sanitize the input
    const sanitized = InputSanitizer.sanitizeObject(params);

    if (
      !sanitized ||
      typeof sanitized !== 'object' ||
      Array.isArray(sanitized)
    ) {
      return {
        valid: false,
        errors: [
          {
            field: 'root',
            message: 'Parameters must be an object',
            value: params,
            expected: 'object',
          },
        ],
      };
    }

    const sanitizedParams = sanitized as SanitizedObject;

    // Validate against schema
    const validationErrors = this.validateObject(sanitizedParams, schema, '');

    if (validationErrors.length > 0) {
      return {
        valid: false,
        errors: validationErrors,
      };
    }

    return {
      valid: true,
      sanitizedParams,
    };
  }

  /**
   * Validate an object against a schema
   */
  private static validateObject(
    obj: SanitizedObject,
    schema: SchemaDefinition,
    path: string
  ): ValidationError[] {
    const errors: ValidationError[] = [];

    // Check type
    if (schema.type === 'object') {
      // Check required fields
      if (schema.required) {
        for (const field of schema.required) {
          if (
            !(field in obj) ||
            obj[field] === null ||
            obj[field] === undefined
          ) {
            errors.push({
              field: path ? `${path}.${field}` : field,
              message: `Missing required field: ${field}`,
              expected: 'required field to be present',
            });
          }
        }
      }

      // Check properties
      if (schema.properties) {
        for (const [key, value] of Object.entries(obj)) {
          if (schema.properties[key]) {
            const fieldPath = path ? `${path}.${key}` : key;
            errors.push(
              ...this.validateValue(
                value,
                schema.properties[key] as SchemaDefinition,
                fieldPath
              )
            );
          } else if (schema.additionalProperties === false) {
            errors.push({
              field: path ? `${path}.${key}` : key,
              message: `Unknown field: ${key}`,
              value,
              suggestion: 'Remove this field or check the field name',
            });
          }
        }
      }
    }

    return errors;
  }

  /**
   * Validate a single value against a schema
   */
  private static validateValue(
    value: SanitizedValue,
    schema: SchemaDefinition,
    path: string
  ): ValidationError[] {
    const errors: ValidationError[] = [];

    // Handle null/undefined
    if (value === null || value === undefined) {
      if (!schema.default) {
        return errors; // Allow null/undefined if no default specified
      }
    }

    // Check type
    const actualType = Array.isArray(value) ? 'array' : typeof value;

    switch (schema.type) {
      case 'string':
        if (typeof value !== 'string') {
          errors.push({
            field: path,
            message: `Expected string, got ${actualType}`,
            value,
            expected: 'string',
          });
        } else {
          // Check string constraints
          if (schema.minLength && value.length < schema.minLength) {
            errors.push({
              field: path,
              message: `String must be at least ${schema.minLength} characters`,
              value,
              expected: `minimum length: ${schema.minLength}`,
            });
          }
          if (schema.maxLength && value.length > schema.maxLength) {
            errors.push({
              field: path,
              message: `String must be at most ${schema.maxLength} characters`,
              value,
              expected: `maximum length: ${schema.maxLength}`,
            });
          }
          if (schema.pattern) {
            const regex = new RegExp(schema.pattern);
            if (!regex.test(value)) {
              errors.push({
                field: path,
                message: `String does not match pattern: ${schema.pattern}`,
                value,
                expected: `pattern: ${schema.pattern}`,
              });
            }
          }
          if (schema.enum && !schema.enum.includes(value)) {
            errors.push({
              field: path,
              message: `Invalid value: ${value}`,
              value,
              expected: `one of: ${schema.enum.join(', ')}`,
            });
          }
        }
        break;

      case 'number':
        if (typeof value !== 'number') {
          errors.push({
            field: path,
            message: `Expected number, got ${actualType}`,
            value,
            expected: 'number',
          });
        } else {
          // Check number constraints
          if (schema.minimum !== undefined && value < schema.minimum) {
            errors.push({
              field: path,
              message: `Value must be at least ${schema.minimum}`,
              value,
              expected: `minimum: ${schema.minimum}`,
            });
          }
          if (schema.maximum !== undefined && value > schema.maximum) {
            errors.push({
              field: path,
              message: `Value must be at most ${schema.maximum}`,
              value,
              expected: `maximum: ${schema.maximum}`,
            });
          }
        }
        break;

      case 'boolean':
        if (typeof value !== 'boolean') {
          errors.push({
            field: path,
            message: `Expected boolean, got ${actualType}`,
            value,
            expected: 'boolean',
          });
        }
        break;

      case 'array':
        if (!Array.isArray(value)) {
          errors.push({
            field: path,
            message: `Expected array, got ${actualType}`,
            value,
            expected: 'array',
          });
        }
        break;

      case 'object':
        if (typeof value !== 'object' || Array.isArray(value)) {
          errors.push({
            field: path,
            message: `Expected object, got ${actualType}`,
            value,
            expected: 'object',
          });
        } else if (value !== null) {
          // Recursively validate nested object
          errors.push(
            ...this.validateObject(value as SanitizedObject, schema, path)
          );
        }
        break;
    }

    return errors;
  }
}

/**
 * Parameter validation middleware
 */
export class ParameterValidationMiddleware {
  /**
   * Validate parameters for universal tools
   */
  static validateUniversalParams(
    toolName: string,
    params: unknown,
    schema: SchemaDefinition
  ): SanitizedObject {
    // First validate against JSON schema
    const result = JsonSchemaValidator.validate(params, schema);

    if (!result.valid) {
      const errorMessages = result.errors!.map(
        (e) =>
          `${e.field}: ${e.message}${e.suggestion ? ` (${e.suggestion})` : ''}`
      );

      throw new UniversalValidationError(
        `Validation failed for ${toolName}:\n${errorMessages.join('\n')}`,
        ErrorType.USER_ERROR,
        {
          httpStatusCode: HttpStatusCode.UNPROCESSABLE_ENTITY,
          suggestion: 'Fix the validation errors and try again',
        }
      );
    }

    const sanitizedParams = result.sanitizedParams!;

    // Additional specific validations for universal tools
    this.validatePaginationParams(sanitizedParams);
    this.validateIdFormat(sanitizedParams);

    return sanitizedParams;
  }

  /**
   * Validate pagination parameters (limit, offset)
   */
  private static validatePaginationParams(params: SanitizedObject): void {
    // Validate limit
    if (
      'limit' in params &&
      params.limit !== null &&
      params.limit !== undefined
    ) {
      const limit = Number(params.limit);

      if (isNaN(limit) || !Number.isInteger(limit)) {
        throw new UniversalValidationError(
          'Parameter "limit" must be an integer',
          ErrorType.USER_ERROR,
          {
            field: 'limit',
            httpStatusCode: HttpStatusCode.UNPROCESSABLE_ENTITY,
            suggestion: 'Provide a valid integer for limit',
            example: 'limit: 10',
          }
        );
      }

      if (limit < 1) {
        throw new UniversalValidationError(
          'Parameter "limit" must be at least 1',
          ErrorType.USER_ERROR,
          {
            field: 'limit',
            httpStatusCode: HttpStatusCode.UNPROCESSABLE_ENTITY,
            suggestion: 'Use a positive integer for limit',
            example: 'limit: 10',
          }
        );
      }

      if (limit > 100) {
        throw new UniversalValidationError(
          'Parameter "limit" must not exceed 100',
          ErrorType.USER_ERROR,
          {
            field: 'limit',
            httpStatusCode: HttpStatusCode.UNPROCESSABLE_ENTITY,
            suggestion: 'Use a value between 1 and 100',
            example: 'limit: 50',
          }
        );
      }
    }

    // Validate offset
    if (
      'offset' in params &&
      params.offset !== null &&
      params.offset !== undefined
    ) {
      const offset = Number(params.offset);

      if (isNaN(offset) || !Number.isInteger(offset)) {
        throw new UniversalValidationError(
          'Parameter "offset" must be an integer',
          ErrorType.USER_ERROR,
          {
            field: 'offset',
            httpStatusCode: HttpStatusCode.UNPROCESSABLE_ENTITY,
            suggestion: 'Provide a valid integer for offset',
            example: 'offset: 0',
          }
        );
      }

      if (offset < 0) {
        throw new UniversalValidationError(
          'Parameter "offset" must be non-negative',
          ErrorType.USER_ERROR,
          {
            field: 'offset',
            httpStatusCode: HttpStatusCode.UNPROCESSABLE_ENTITY,
            suggestion: 'Use a non-negative integer for offset',
            example: 'offset: 0',
          }
        );
      }
    }
  }

  /**
   * Validate ID format for record_id and similar fields
   */
  private static validateIdFormat(params: SanitizedObject): void {
    const idFields = [
      'record_id',
      'source_id',
      'target_id',
      'company_id',
      'person_id',
    ];

    for (const field of idFields) {
      if (
        field in params &&
        params[field] !== null &&
        params[field] !== undefined
      ) {
        const id = String(params[field]);

        // Basic ID format validation (alphanumeric with underscores and hyphens)
        const idRegex = /^[a-zA-Z0-9_-]+$/;

        if (!idRegex.test(id)) {
          throw new UniversalValidationError(
            `Invalid ${field} format: "${id}"`,
            ErrorType.USER_ERROR,
            {
              field,
              httpStatusCode: HttpStatusCode.UNPROCESSABLE_ENTITY,
              suggestion: `The ${field} should contain only letters, numbers, underscores, and hyphens`,
              example: `${field}: 'comp_abc123' or 'person_xyz789'`,
            }
          );
        }

        // Check for reasonable length
        if (id.length < 3 || id.length > 100) {
          throw new UniversalValidationError(
            `Invalid ${field} length: ${id.length} characters`,
            ErrorType.USER_ERROR,
            {
              field,
              httpStatusCode: HttpStatusCode.UNPROCESSABLE_ENTITY,
              suggestion: `The ${field} should be between 3 and 100 characters`,
              example: `${field}: 'comp_abc123'`,
            }
          );
        }
      }
    }
  }
}

/**
 * Create a validation error response
 */
export function createValidationErrorResponse(
  error: UniversalValidationError
): any {
  return {
    error: error.toErrorResponse().error,
    status: 'error',
    code: error.httpStatusCode,
  };
}

/**
 * Wrap a handler with validation middleware
 */
export function withValidation<T extends (...args: any[]) => any>(
  handler: T,
  schema: SchemaDefinition,
  toolName: string
): T {
  return (async (...args: any[]) => {
    try {
      // Validate parameters if provided
      if (args[0]) {
        const validatedParams =
          ParameterValidationMiddleware.validateUniversalParams(
            toolName,
            args[0],
            schema
          );
        // Replace original params with validated ones
        args[0] = validatedParams;
      }

      // Call the original handler
      return await handler(...args);
    } catch (error: unknown) {
      // If it's already a validation error, re-throw
      if (error instanceof UniversalValidationError) {
        throw error;
      }

      // Otherwise wrap in a system error
      throw new UniversalValidationError(
        `Unexpected error in ${toolName}: ${
          error instanceof Error ? error.message : String(error)
        }`,
        ErrorType.SYSTEM_ERROR,
        {
          httpStatusCode: HttpStatusCode.INTERNAL_SERVER_ERROR,
          cause: error instanceof Error ? error : undefined,
        }
      );
    }
  }) as T;
}

/**
 * Base operations for all Attio objects with dynamic field detection
 */
import { formatAllAttributes } from '../api/attribute-types.js';
import {
  createObjectRecord,
  updateObjectRecord,
  deleteObjectRecord,
} from './records/index.js';
import { ResourceType, AttioRecord } from '../types/attio.js';
import { getAttributeSlug } from '../utils/attribute-mapping/index.js';

/**
 * Translates all attribute names in a record using the attribute mapping system
 *
 * @param objectType - The type of object (companies, people, etc.)
 * @param attributes - Raw attributes object with user-friendly names
 * @returns Attributes object with API-compatible attribute names
 */
function translateAttributeNames(
  objectType: ResourceType,
  attributes: Record<string, unknown>
): Record<string, unknown> {
  const translated: Record<string, unknown> = {};

  for (const [userKey, value] of Object.entries(attributes)) {
    // Translate the attribute name using the mapping system
    const apiKey = getAttributeSlug(userKey, objectType);

    // Log the translation in development mode
    if (process.env.NODE_ENV === 'development' && userKey !== apiKey) {
      console.error(
        `[translateAttributeNames:${objectType}] Mapped "${userKey}" -> "${apiKey}"`
      );
    }

    translated[apiKey] = value;
  }

  return translated;
}

/**
 * Creates a new object record with dynamic field formatting
 *
 * @param objectType - The type of object (companies, people, etc.)
 * @param attributes - Raw attributes for object creation
 * @param validator - Optional validator function
 * @returns Created object record
 */
export async function createObjectWithDynamicFields<T extends AttioRecord>(
  objectType: ResourceType,
  attributes: any,
  validator?: (attrs: any) => Promise<unknown>
): Promise<T> {
  // Validate if validator provided
  const validatedAttributes = validator
    ? await validator(attributes)
    : attributes;

  // Translate attribute names using the mapping system (e.g., "b2b_segment" -> "type_persona")
  const mappedAttributes = translateAttributeNames(
    objectType,
    validatedAttributes
  );

  // Use dynamic field type detection to format attributes correctly
  const transformedAttributes = await formatAllAttributes(
    objectType,
    mappedAttributes
  );

  // Debug log to help diagnose issues (includes E2E mode)
  if (
    process.env.NODE_ENV === 'development' ||
    process.env.E2E_MODE === 'true'
  ) {
    console.error(
      `[createObjectWithDynamicFields:${objectType}] Original attributes:`,
      JSON.stringify(validatedAttributes, null, 2)
    );
    console.error(
      `[createObjectWithDynamicFields:${objectType}] Mapped attributes:`,
      JSON.stringify(mappedAttributes, null, 2)
    );
    console.error(
      `[createObjectWithDynamicFields:${objectType}] Final transformed attributes:`,
      JSON.stringify(transformedAttributes, null, 2)
    );
  }

  try {
    // Create the object
    const result = await createObjectRecord<T>(
      objectType,
      transformedAttributes
    );

    if (
      process.env.NODE_ENV === 'development' ||
      process.env.E2E_MODE === 'true'
    ) {
      console.error(
        `[createObjectWithDynamicFields:${objectType}] Result from createObjectRecord:`,
        {
          result,
          hasId: !!result?.id,
          hasValues: !!result?.values,
          resultType: typeof result,
          isEmptyObject: result && Object.keys(result).length === 0,
        }
      );
    }

    // Additional check for empty objects that might slip through, but allow legitimate create responses
    const looksLikeCreatedRecord =
      result &&
      typeof result === 'object' &&
      (('id' in result && (result as any).id?.record_id) ||
        'record_id' in result ||
        'web_url' in result ||
        'created_at' in result);

    if (
      !result ||
      (typeof result === 'object' &&
        Object.keys(result).length === 0 &&
        !looksLikeCreatedRecord)
    ) {
      // For companies, allow empty results to pass through to createObjectRecord fallback logic
      if (objectType === ResourceType.COMPANIES) {
        if (
          process.env.NODE_ENV === 'development' ||
          process.env.E2E_MODE === 'true'
        ) {
          console.error(
            `[createObjectWithDynamicFields:${objectType}] Empty result detected, passing to createObjectRecord fallback`
          );
        }
        return result; // Let createObjectRecord handle the fallback
      }

      throw new Error(
        `Create operation returned empty result for ${objectType}`
      );
    }

    return result;
  } catch (error: unknown) {
    console.error(
      `[createObjectWithDynamicFields:${objectType}] Error creating record:`,
      error instanceof Error ? error.message : String(error)
    );
    throw error;
  }
}

/**
 * Updates an existing object record with dynamic field formatting
 *
 * @param objectType - The type of object (companies, people, etc.)
 * @param recordId - ID of the record to update
 * @param attributes - Raw attributes to update
 * @param validator - Optional validator function
 * @returns Updated object record
 */
export async function updateObjectWithDynamicFields<T extends AttioRecord>(
  objectType: ResourceType,
  recordId: string,
  attributes: any,
  validator?: (id: string, attrs: any) => Promise<unknown>
): Promise<T> {
  // Validate if validator provided
  const validatedAttributes = validator
    ? await validator(recordId, attributes)
    : attributes;

  // Translate attribute names using the mapping system (e.g., "b2b_segment" -> "type_persona")
  const mappedAttributes = translateAttributeNames(
    objectType,
    validatedAttributes
  );

  // Use dynamic field type detection to format attributes correctly
  const transformedAttributes = await formatAllAttributes(
    objectType,
    mappedAttributes
  );

  if (process.env.NODE_ENV === 'development') {
    console.error(
      `[updateObjectWithDynamicFields:${objectType}] Original attributes:`,
      JSON.stringify(validatedAttributes, null, 2)
    );
    console.error(
      `[updateObjectWithDynamicFields:${objectType}] Mapped attributes:`,
      JSON.stringify(mappedAttributes, null, 2)
    );
    console.error(
      `[updateObjectWithDynamicFields:${objectType}] Final transformed attributes:`,
      JSON.stringify(transformedAttributes, null, 2)
    );
  }

  // Update the object
  const result = await updateObjectRecord<T>(
    objectType,
    recordId,
    transformedAttributes
  );

  // Additional check for empty objects that might slip through
  // For companies, allow empty results to pass through since updateRecord has fallback logic
  if (
    !result ||
    (typeof result === 'object' && Object.keys(result).length === 0)
  ) {
    // For companies, the updateRecord function has fallback logic that should handle empty responses
    if (objectType === ResourceType.COMPANIES) {
      if (
        process.env.NODE_ENV === 'development' ||
        process.env.E2E_MODE === 'true'
      ) {
        console.error(
          `[updateObjectWithDynamicFields:${objectType}] Empty result detected for update, allowing fallback logic to handle`
        );
      }
      // The fallback should have been handled in updateRecord, if we still get empty result, something is wrong
      if (!result) {
        throw new Error(
          `Update operation returned null result for ${objectType} record: ${recordId}`
        );
      }
      // Empty object might be a valid result from fallback, return it
      return result;
    }

    throw new Error(
      `Update operation returned empty result for ${objectType} record: ${recordId}`
    );
  }

  return result;
}

/**
 * Updates a specific attribute of an object with dynamic field formatting
 *
 * @param objectType - The type of object (companies, people, etc.)
 * @param recordId - ID of the record to update
 * @param attributeName - Name of the attribute to update
 * @param attributeValue - New value for the attribute
 * @param updateFn - The update function to use
 * @returns Updated object record
 */
export async function updateObjectAttributeWithDynamicFields<
  T extends AttioRecord,
>(
  objectType: ResourceType,
  recordId: string,
  attributeName: string,
  attributeValue: any,
  updateFn: (id: string, attrs: any) => Promise<T>
): Promise<T> {
  // Update the specific attribute using the provided update function
  const attributes = { [attributeName]: attributeValue };
  return await updateFn(recordId, attributes);
}

/**
 * Deletes an object record
 *
 * @param objectType - The type of object (companies, people, etc.)
 * @param recordId - ID of the record to delete
 * @param validator - Optional validator function
 * @returns True if deletion was successful
 */
export async function deleteObjectWithValidation(
  objectType: ResourceType,
  recordId: string,
  validator?: (id: string) => void
): Promise<boolean> {
  // Validate if validator provided
  if (validator) {
    validator(recordId);
  }

  // Delete the object
  return await deleteObjectRecord(objectType, recordId);
}

/**
 * Batch operations for company records
 */
import {
  ResourceType,
  Company,
  BatchResponse,
  BatchConfig,
  RecordAttributes,
} from '../types/attio.js';
import { CompanyFieldValue } from '../types/tool-types.js';
import {
  executeBatchOperations,
  batchCreateRecords,
  batchUpdateRecords,
} from '../api/operations/index.js';
import {
  createCompany,
  updateCompany,
  deleteCompany,
  searchCompanies,
  getCompanyDetails,
} from './companies/index.js';
import { CompanyValidator } from '../validators/company-validator.js';
import { validateBatchOperation } from '../utils/batch-validation.js';

/**
 * Helper function to execute a batch operation with improved error handling
 *
 * This function centralizes batch operations for companies, providing consistent
 * error handling, proper object type setting, and fallback to individual operations
 * when the batch API is unavailable.
 *
 * @template T - The type of input records (e.g., RecordAttributes for create, {id, attributes} for update)
 * @template R - The type of output records (typically Company)
 * @param operationType - The type of operation (create, update, delete, etc.)
 * @param records - The records to process
 * @param batchFunction - The batch API function to call
 * @param singleFunction - The single-record fallback function
 * @param batchConfig - Optional batch configuration
 * @returns Batch response with results for each record and summary statistics
 * @throws Error if records is not an array or validation fails
 */
async function executeBatchCompanyOperation<T, R>(
  operationType: 'create' | 'update' | 'delete' | 'search' | 'get',
  records: T[],
  batchFunction: (params: any) => Promise<R[]>,
  singleFunction: (params: T) => Promise<R>,
  batchConfig?: Partial<BatchConfig>
): Promise<BatchResponse<R>> {
  // Validation check
  if (!Array.isArray(records) || records.length === 0) {
    throw new Error(
      `Invalid ${operationType} parameters: records must be a non-empty array`
    );
  }

  // Validate batch operation for DoS protection
  const validation = validateBatchOperation({
    items: records,
    operationType,
    resourceType: ResourceType.COMPANIES,
    checkPayload: operationType === 'create' || operationType === 'update',
  });

  if (!validation.isValid) {
    throw new Error(validation.error);
  }

  try {
    // Attempt to use the batch API
    const results = await batchFunction({
      objectSlug: ResourceType.COMPANIES, // Always explicitly set the resource type
      records:
        operationType === 'create'
          ? records.map((r: any) => ({ attributes: r }))
          : records,
    });

    // Format the response
    return {
      results: results.map((result, index) => ({
        id: `${operationType}_company_${index}`,
        success: true,
        data: result,
      })),
      summary: {
        total: records.length,
        succeeded: results.length,
        failed: records.length - results.length,
      },
    };
  } catch (error: unknown) {
    // Log the error for debugging
    console.error(
      `[batchCompany${
        operationType.charAt(0).toUpperCase() + operationType.slice(1)
      }] ` +
        `Batch API failed with error: ${
          error instanceof Error ? error.message : String(error)
        }`
    );

    // Fall back to individual operations
    return executeBatchOperations<T, R>(
      records.map((record, index) => ({
        id: `${operationType}_company_${index}`,
        params: record,
      })),
      singleFunction,
      batchConfig
    );
  }
}

/**
 * Creates multiple company records in batch operation
 *
 * This function creates multiple company records in a single API call, with automatic
 * fallback to individual operations if the batch API is unavailable. All input data
 * is validated before processing.
 *
 * @example
 * ```typescript
 * // Create multiple companies
 * const companies = [
 *   { name: "Acme Corp", website: "https://acme.com", industry: "Technology" },
 *   { name: "Umbrella Inc", website: "https://umbrella.com", industry: "Manufacturing" }
 * ];
 *
 * const result = await batchCreateCompanies({ companies });
 * console.error(`Created ${result.summary.succeeded} of ${result.summary.total} companies`);
 * ```
 *
 * Note on parameter structure:
 * This function expects a different parameter structure compared to generic record batch operations.
 * While generic batch operations take (objectSlug, records, objectId), company-specific batch operations
 * expect an object with {companies, config} to match the structure expected by the MCP tool schema.
 *
 * @param params - Object containing array of companies and optional config
 * @returns Batch response with created companies
 */
export async function batchCreateCompanies(params: {
  companies: RecordAttributes[];
  config?: Partial<BatchConfig>;
}): Promise<BatchResponse<Company>> {
  // Early validation of parameters - fail fast
  if (!params) {
    throw new Error('Invalid request: params object is required');
  }

  // Extract and validate companies array
  const { companies, config: batchConfig } = params;

  if (!companies) {
    throw new Error("Invalid request: 'companies' parameter is required");
  }

  if (!Array.isArray(companies)) {
    throw new Error("Invalid request: 'companies' parameter must be an array");
  }

  if (companies.length === 0) {
    throw new Error("Invalid request: 'companies' array cannot be empty");
  }

  // Validate array contents - ensure each item is a valid object
  companies.forEach((company, index) => {
    if (!company || typeof company !== 'object') {
      throw new Error(
        `Invalid company data at index ${index}: must be a non-null object`
      );
    }
    // Basic validation: name property must exist (but can be empty - that will be handled gracefully)
    if (!('name' in company)) {
      throw new Error(
        `Invalid company data at index ${index}: 'name' is required`
      );
    }
  });

  try {
    // Use the generic batch create with graceful validation
    // Attempt validation for each company, but allow individual failures
    const validatedCompanies = await Promise.all(
      companies.map(async (company, index) => {
        try {
          return await CompanyValidator.validateCreate(
            company as Record<string, CompanyFieldValue>
          );
        } catch (error: unknown) {
          // Log validation error but allow operation to continue for individual handling
          console.warn(
            `Validation failed for company at index ${index}:`,
            error instanceof Error ? error.message : String(error)
          );
          return company; // Pass through for individual handling in fallback operations
        }
      })
    );

    // Use the shared helper function for consistent handling
    return executeBatchCompanyOperation<RecordAttributes, Company>(
      'create',
      validatedCompanies,
      batchCreateRecords,
      createCompany,
      batchConfig
    );
  } catch (error: unknown) {
    if (error instanceof Error && error.message.includes('validation')) {
      // Re-throw validation errors with more context
      throw new Error(`Company validation failed: ${error.message}`);
    }

    // For other errors, log and then rethrow
    // Error occurred during batch creation
    throw error;
  }
}

/**
 * Updates multiple company records in batch operation
 *
 * This function updates multiple company records in a single API call, with automatic
 * fallback to individual operations if the batch API is unavailable. It performs extensive
 * validation to ensure all required fields are present and properly formatted.
 *
 * @example
 * ```typescript
 * // Update multiple companies
 * const updates = [
 *   { id: "3bdf5c9d-aa78-492a-a4c1-5a143e94ef0e", attributes: { industry: "New Industry" } },
 *   { id: "e252e8df-d6b6-4909-a03c-6c9f144c4580", attributes: { website: "https://new-site.com" } }
 * ];
 *
 * const result = await batchUpdateCompanies({ updates });
 * console.error(`Updated ${result.summary.succeeded} of ${result.summary.total} companies`);
 * ```
 *
 * Note on parameter structure:
 * This function expects a different parameter structure compared to generic record batch operations.
 * While generic batch operations take (objectSlug, records, objectId), company-specific batch operations
 * expect an object with {updates, config} to match the structure expected by the MCP tool schema.
 *
 * @param params - Object containing array of updates and optional config
 * @returns Batch response with updated companies
 */
export async function batchUpdateCompanies(params: {
  updates: Array<{ id: string; attributes: RecordAttributes }>;
  config?: Partial<BatchConfig>;
}): Promise<BatchResponse<Company>> {
  // Early validation of parameters - fail fast
  if (!params) {
    throw new Error('Invalid request: params object is required');
  }

  // Extract and validate updates array
  const { updates, config: batchConfig } = params;

  if (!updates) {
    throw new Error("Invalid request: 'updates' parameter is required");
  }

  if (!Array.isArray(updates)) {
    throw new Error("Invalid request: 'updates' parameter must be an array");
  }

  if (updates.length === 0) {
    throw new Error("Invalid request: 'updates' array cannot be empty");
  }

  // Validate array contents - ensure each item has required fields
  updates.forEach((update, index) => {
    if (!update || typeof update !== 'object') {
      throw new Error(
        `Invalid update data at index ${index}: must be a non-null object`
      );
    }
    if (!update.id) {
      throw new Error(
        `Invalid update data at index ${index}: 'id' is required`
      );
    }
    if (!update.attributes || typeof update.attributes !== 'object') {
      throw new Error(
        `Invalid update data at index ${index}: 'attributes' must be a non-null object`
      );
    }
  });

  try {
    // Use the shared helper function for consistent handling
    return executeBatchCompanyOperation<
      { id: string; attributes: RecordAttributes },
      Company
    >(
      'update',
      updates,
      batchUpdateRecords,
      (params) => updateCompany(params.id, params.attributes),
      batchConfig
    );
  } catch (error: unknown) {
    // Enhanced error handling with more context
    if (error instanceof Error) {
      if (error.message.includes('not found')) {
        throw new Error(
          `Company update failed: One or more company IDs do not exist`
        );
      } else {
        // Provide more detailed error
        throw new Error(`Company batch update failed: ${error.message}`);
      }
    }

    // For other errors, log and then rethrow
    console.error('[batchUpdateCompanies] Error updating companies:', error);
    throw error;
  }
}

