          idType: typeof sampleCompany?.id,
          idKeys: sampleCompany?.id ? Object.keys(sampleCompany.id) : null,
          company: sampleCompany,
        }
      );
      return {
        standard: [],
        custom: [],
        all: [],
      };
    }

    if (process.env.NODE_ENV === 'development') {
      console.error(
        `[discoverCompanyAttributes] Using sample company ID: ${sampleCompanyId}`
      );
    }

    const sampleCompanyDetails = await getCompanyDetails(sampleCompanyId);
    const values = sampleCompanyDetails.values || {};

    if (process.env.NODE_ENV === 'development') {
      console.error(
        `[discoverCompanyAttributes] Retrieved ${
          Object.keys(values).length
        } fields from sample company`
      );
    }

    const standardFields = new Set([
      'name',
      'website',
      'industry',
      'domains',
      'description',
      'logo_url',
      'primary_location',
      'employee_range',
      'foundation_date',
