/**
 * Batch operations for People
 */
import {
  batchSearchObjects,
  batchGetObjectDetails,
  BatchConfig,
  BatchResponse,
} from '../../api/operations/index.js';
import { ResourceType, Person } from '../../types/attio.js';
import { FilterValidationError } from '../../errors/api-errors.js';
import { isValidId } from '../../utils/validation.js';
import { searchPeople } from './search.js';
import { getPersonDetails } from './basic.js';

/**
 * Performs batch search operations on people
 * Searches for multiple people using different queries
 *
 * @param queries - Array of search queries
 * @param config - Optional batch configuration
 * @returns BatchResponse with results and errors for each query
 */
export async function batchSearchPeople(
  queries: string[],
  config?: BatchConfig
): Promise<BatchResponse<Person[]>> {
  try {
    // Validate queries
    if (!Array.isArray(queries) || queries.length === 0) {
      throw new FilterValidationError('Must provide at least one search query');
    }

    for (const query of queries) {
      if (!query || query.trim().length === 0) {
        throw new FilterValidationError(`Invalid query: cannot be empty`);
      }
      if (query.length > 1000) {
        throw new FilterValidationError(`Invalid query '${query}': too long`);
      }
    }

    try {
      return await batchSearchObjects<Person>(
        ResourceType.PEOPLE,
        queries,
        config
      );
    } catch (batchError) {
      // Fallback to individual searches
      const results = [];
      let succeeded = 0;
      let failed = 0;

      for (const query of queries) {
        try {
          const searchResults = await searchPeople(query);
          results.push({
            query,
            data: searchResults,
            success: true,
          });
          succeeded++;
        } catch (searchError) {
          results.push({
            query,
            error:
              searchError instanceof Error
                ? searchError
                : new Error(String(searchError)),
            success: false,
          });
          failed++;
        }
      }

      return {
        results,
        summary: {
          total: queries.length,
          succeeded,
          failed,
        },
      };
    }
  } catch (error: unknown) {
    const errorMessage = error instanceof Error ? error.message : String(error);
    if (errorMessage.includes('validation')) {
      throw new FilterValidationError(
        `Batch search validation failed: ${errorMessage}`
      );
    }
    throw new Error(`Failed to batch search people: ${errorMessage}`);
  }
}

/**
 * Performs batch detail retrieval for multiple people
 * Gets detailed information for multiple people by their IDs
 *
 * @param personIds - Array of person IDs
 * @param config - Optional batch configuration
 * @returns BatchResponse with details and errors for each person
 */
export async function batchGetPeopleDetails(
  personIds: string[],
  config?: BatchConfig
): Promise<BatchResponse<Person>> {
  try {
    // Validate person IDs
    if (!Array.isArray(personIds) || personIds.length === 0) {
      throw new FilterValidationError('Must provide at least one person ID');
    }

    for (const personId of personIds) {
      if (!isValidId(personId)) {
        throw new FilterValidationError(`Invalid person ID '${personId}'`);
      }
    }

    try {
      return await batchGetObjectDetails<Person>(
        ResourceType.PEOPLE,
        personIds,
        config
      );
    } catch (batchError) {
      // Fallback to individual detail retrieval
      const results = [];
      let succeeded = 0;
      let failed = 0;

      for (const personId of personIds) {
        try {
          const personDetails = await getPersonDetails(personId);
          results.push({
            id: personId,
            data: personDetails,
            success: true,
          });
          succeeded++;
        } catch (detailError) {
          results.push({
            id: personId,
            error:
              detailError instanceof Error
                ? detailError
                : new Error(String(detailError)),
            success: false,
          });
          failed++;
        }
      }

      return {
        results,
        summary: {
          total: personIds.length,
          succeeded,
          failed,
        },
      };
    }
  } catch (error: unknown) {
    const errorMessage = error instanceof Error ? error.message : String(error);
    if (errorMessage.includes('validation')) {
      throw new FilterValidationError(
        `Batch get details validation failed: ${errorMessage}`
      );
    }
    throw new Error(`Failed to batch get people details: ${errorMessage}`);
  }
}

/**
 * Record formatting utilities
 */
import { RecordAttributes } from '../../types/attio.js';

/**
 * Helper function to format a single record attribute based on type
 *
 * @param key - Attribute key
 * @param value - Attribute value to format
 * @returns Properly formatted attribute value for the API
 */
export function formatRecordAttribute(key: string, value: any): any {
  if (value === null || value === undefined) {
    return value;
  }

  if (value instanceof Date) {
    return value.toISOString();
  }

  if (
    typeof value === 'number' &&
    (key.includes('price') || key.includes('revenue') || key.includes('cost'))
  ) {
    return value;
  }

  if (
    typeof value === 'object' &&
    value !== null &&
    !Array.isArray(value) &&
    (key.includes('address') || key.includes('location'))
  ) {
    if ('street' in value || 'city' in value || 'country' in value) {
      return value;
    }
  }

  if (typeof value === 'string' && value.match(/^record_[a-z0-9]+$/)) {
    return {
      record_id: value,
    };
  }

  return value;
}

/**
 * Formats a full set of record attributes for API requests
 *
 * @param attributes - Raw attribute key-value pairs
 * @returns Formatted attributes object ready for API submission
 */
export function formatRecordAttributes(
  attributes: Record<string, unknown>
): RecordAttributes {
  const formattedAttributes: RecordAttributes = {};

  for (const [key, value] of Object.entries(attributes)) {
    formattedAttributes[key] = formatRecordAttribute(key, value);
  }

  return formattedAttributes;
}

