          }))
        : [],
    } as unknown as AttioTask;
  }

  return apiUpdate(taskId, updates);
}

export async function deleteTask(taskId: string): Promise<boolean> {
  // Check if we should use mock data for testing
  if (shouldUseMockData()) {
    // Validate task ID
    if (!isValidId(taskId)) {
      throw new Error(`Task not found: ${taskId}`);
    }

    if (
      process.env.NODE_ENV === 'development' ||
      process.env.VERBOSE_TESTS === 'true'
    ) {
      console.error('[MockInjection] Using mock data for task deletion');
    }

    // Return mock success response
    return true;
  }

  // Delegate to API operations implementation (handles retries and envelopes)
  try {
    return await apiDelete(taskId);
  } catch (err: any) {
    const status = err?.response?.status ?? err?.status;
    const code = err?.response?.data?.code ?? err?.code;
    const msg = (err?.response?.data?.message ?? err?.message ?? '')
      .toString()
      .toLowerCase();
    // Normalize soft "not found" to boolean false so the service maps it to a structured 404
    if (status === 404 || code === 'not_found' || msg.includes('not found'))
      return false;
    throw err;
  }
}

export async function linkRecordToTask(
  taskId: string,
  recordId: string
): Promise<boolean> {
  // Check if we should use mock data for testing
  if (shouldUseMockData()) {
    // Validate task ID
    if (!isValidId(taskId)) {
      throw new Error(`Task not found: ${taskId}`);
    }

    // Validate record ID
    if (!isValidId(recordId)) {
      throw new Error(`Record not found: ${recordId}`);
    }

    if (
      process.env.NODE_ENV === 'development' ||
      process.env.VERBOSE_TESTS === 'true'
    ) {
      console.error('[MockInjection] Using mock data for task-record linking');
    }

    // Return mock success response
    return true;
  }

  return apiLink(taskId, recordId);
}

export async function unlinkRecordFromTask(
  taskId: string,
  recordId: string
): Promise<boolean> {
  return apiUnlink(taskId, recordId);
}

/**
 * Error handling utilities for the prompts module
 */
import { ErrorType, formatErrorResponse } from '../utils/error-handler.js';

// Extend the error response type to allow string codes for Express
interface PromptErrorResponse {
  error: {
    code: string | number;
    message: string;
    type: ErrorType;
    details?: any;
  };
  content: Array<{ type: string; text: string }>;
  isError: boolean;
}

/**
 * Creates a standardized error response for prompt-related errors
 *
 * @param error - The error object
 * @param message - Error message
 * @param statusCode - HTTP status code
 * @returns Formatted error result
 */
export function createErrorResult(
  error: Error,
  message: string,
  statusCode: number
): PromptErrorResponse {
  let errorType = ErrorType.UNKNOWN_ERROR;

  // Determine error type based on status code
  if (statusCode === 400) {
    errorType = ErrorType.VALIDATION_ERROR;
  } else if (statusCode === 401 || statusCode === 403) {
    errorType = ErrorType.AUTHENTICATION_ERROR;
  } else if (statusCode === 404) {
    errorType = ErrorType.NOT_FOUND_ERROR;
  } else if (statusCode === 429) {
    errorType = ErrorType.RATE_LIMIT_ERROR;
  } else if (statusCode >= 500) {
    errorType = ErrorType.SERVER_ERROR;
  }

  const errorDetails = {
    statusCode,
    message,
  };

  // Get the base response from the utility function
  const baseResponse = formatErrorResponse(error, errorType, errorDetails);

  // Create a new response object with our extended type
  const response: PromptErrorResponse = {
    ...baseResponse,
    error: {
      ...baseResponse.error,
      code: String(statusCode), // Convert to string for Express
    },
  };

  return response;
}

