                  }
                }

                // name exact match as fallback
                if (name && !record?.id?.record_id) {
                  const { data } = await client.post(
                    `/objects/${companiesSlug}/records/search`,
                    {
                      filter: { name: { eq: name } },
                      limit: 1,
                      order: { created_at: 'desc' },
