/**
 * UniversalCreateService provides centralized record creation functionality
 */
export class UniversalCreateService {
  /**
   * Universal record creation with comprehensive field validation, mapping, and type safety
   *
   * This is the main entry point for creating records of any type in the Attio MCP Server.
   * It provides a unified interface that handles field mapping, validation, type conversion,
   * and error handling across all resource types.
   *
   * ## Features:
   * - **Field Mapping**: Automatically maps common field name variations to Attio schema
   * - **Attribute Discovery**: Fetches live schema information with caching for validation
   * - **Type Safety**: Validates and converts field types to match Attio expectations
   * - **Collision Detection**: Prevents multiple fields from mapping to the same target
   * - **Enhanced Errors**: Provides detailed, actionable error messages with suggestions
   * - **Performance Tracking**: Records metrics for monitoring and optimization
   *
   * @param params - The record creation parameters
   * @param params.resource_type - Type of record to create (companies, people, etc.)
   * @param params.record_data - The data for the new record
   * @returns Promise resolving to the created AttioRecord with full metadata
   *
   * @throws {UniversalValidationError} When field validation fails with enhanced details
   * @throws {Error} For authentication, network, or other system errors
   *
   * @example Basic company creation:
   * ```typescript
   * const company = await UniversalCreateService.createRecord({
   *   resource_type: UniversalResourceType.COMPANIES,
   *   record_data: {
   *     name: "Acme Corporation",
   *     domains: ["acme.com"],
   *     description: "A software company"
   *   }
   * });
   * ```
   */
  static async createRecord(
    params: UniversalCreateParams
  ): Promise<AttioRecord> {
    // CRITICAL FIX: Ensure record_data is always a plain object (not JSON string)
    // Must mutate the original params.record_data, not just local variable
    if (typeof params.record_data === 'string') {
      try {
        params.record_data = JSON.parse(params.record_data);
      } catch {
        throw new UniversalValidationError('record_data must be an object');
      }
    }
    const { resource_type } = params;
    const record_data = params.record_data; // Use the potentially parsed record_data
    if (
      !record_data ||
      typeof record_data !== 'object' ||
      Array.isArray(record_data)
    ) {
      throw new UniversalValidationError('record_data must be a JSON object');
    }

    logger.debug('Entry point - createRecord', {
      resource_type,
      record_data: JSON.stringify(record_data, null, 2),
    });

    // Pre-validate fields and provide helpful suggestions
    // For records, only validate top-level fields (don't validate inside values)
    let fieldsToValidate: Record<string, unknown>;
    if (resource_type === UniversalResourceType.RECORDS) {
      // Only validate top-level keys for records (exclude inner values)
      const { values, ...topLevelFields } = record_data;
      fieldsToValidate =
        Object.keys(topLevelFields).length > 0
          ? (topLevelFields as Record<string, unknown>)
          : ({ object: 'placeholder' } as Record<string, unknown>); // Ensure non-empty object for validation
      logger.debug('Records validation: checking only top-level fields', {
        topLevelKeys: Object.keys(topLevelFields),
        excludedValuesKeys: values
          ? Object.keys(values as Record<string, unknown>)
          : [],
      });
    } else {
      fieldsToValidate = (record_data.values || record_data) as Record<
        string,
        unknown
      >; // Normal validation for other types
    }

    const fieldValidation = validateFields(resource_type, fieldsToValidate);
    logger.debug('Field validation result', {
      valid: fieldValidation.valid,
      warnings: fieldValidation.warnings,
      errors: fieldValidation.errors,
      suggestions: fieldValidation.suggestions,
    });

    if (fieldValidation.warnings.length > 0) {
      logger.warn('Field validation warnings', {
        warnings: fieldValidation.warnings,
      });
    }
    if (fieldValidation.suggestions.length > 0) {
      const truncated = ValidationService.truncateSuggestions(
        fieldValidation.suggestions
      );
      logger.info('Field suggestions available', {
        suggestions: truncated,
      });
    }
    if (!fieldValidation.valid) {
      // Build a clear, helpful error message
      let errorMessage = ERROR_MESSAGES.VALIDATION_FAILED(resource_type);
      let remediation: string[] = [];

      // Add each error on its own line for clarity
      if (fieldValidation.errors.length > 0) {
        errorMessage +=
          '\n' + fieldValidation.errors.map((err) => `   ${err}`).join('\n');
      }

      // Add suggestions if available (truncated to prevent buffer overflow)
      if (fieldValidation.suggestions.length > 0) {
        const truncated = ValidationService.truncateSuggestions(
          fieldValidation.suggestions
        );
        errorMessage += '\n\n Suggestions:\n';
        errorMessage += truncated.map((sug) => `   ${sug}`).join('\n');

        remediation = truncated.slice(0, MAX_VALIDATION_SUGGESTIONS);
      }

      // List available fields for this resource type
      const mapping = FIELD_MAPPINGS[resource_type];
      if (mapping && mapping.validFields.length > 0) {
        errorMessage += `\n\n Available fields for ${resource_type}:\n  ${mapping.validFields.join(
          ', '
        )}`;
        remediation.push(
          `Use valid fields: ${mapping.validFields.slice(0, 5).join(', ')}`
        );
      }

      throw createEnhancedValidationError(errorMessage, {
        field: 'record_data',
        errorCode: 'FIELD_VALIDATION_FAILED',
        suggestion: ValidationService.truncateSuggestions(
          fieldValidation.suggestions
        )
          .join('. ')
          .substring(0, MAX_SUGGESTION_TEXT_LENGTH),
        remediation,
      });
    }

    // Fetch available attributes for attribute-aware mapping (both api_slug and title)
    // Skip attribute discovery for notes (they don't have /objects/notes/attributes endpoint)
    let availableAttributes: string[] | undefined;
    if (resource_type === UniversalResourceType.NOTES) {
      logger.debug('Skipping attribute discovery for notes', {
        reason:
          'Notes are not under /objects/ and do not have attributes endpoint',
      });
      availableAttributes = undefined;
    } else {
      try {
        const { UniversalMetadataService } = await import(
          './UniversalMetadataService.js'
        );
        // For records, we need to extract the objectSlug for metadata discovery
        const options: { objectSlug?: string } = {};
        if (resource_type === UniversalResourceType.RECORDS) {
          const objectSlug = record_data.object || record_data.object_api_slug;
          if (objectSlug && typeof objectSlug === 'string') {
            options.objectSlug = objectSlug;
          }
        }

        const attributeResult =
          await UniversalMetadataService.discoverAttributesForResourceType(
            resource_type,
            options
          );

        // Include both api_slug, title, and name fields, normalize to lowercase, and dedupe
        const attrs = (attributeResult?.attributes as any[]) ?? [];
        availableAttributes = Array.from(
          new Set(
            attrs.flatMap((a) =>
              [
                a?.api_slug,
                a?.title,
                a?.name, // accept all, some schemas use `title`, some `name`
              ].filter((s: unknown): s is string => typeof s === 'string')
            )
          )
        ).map((s) => s.toLowerCase());
      } catch (error) {
        // If attribute discovery fails, proceed without it (fallback behavior)
        const errorMessage =
          error instanceof Error ? error.message : String(error);
        logger.warn(ERROR_MESSAGES.ATTRIBUTE_DISCOVERY_FAILED(resource_type), {
          resource_type,
          fallback: 'proceeding without attributes',
          error: errorMessage,
          category: ErrorCategory.EXTERNAL_SERVICE,
        });
        availableAttributes = undefined;

        // If this is a critical error (auth, network), consider throwing
        if (errorMessage.includes('401') || errorMessage.includes('403')) {
          throw createEnhancedValidationError(
            `Authentication failed during attribute discovery for ${resource_type}`,
            {
              category: ErrorCategory.AUTHENTICATION,
              errorCode: 'ATTR_DISCOVERY_AUTH_FAILED',
              suggestion: 'Check API credentials and permissions',
              remediation: [
                'Verify ATTIO_API_KEY is valid and has proper permissions',
              ],
            }
          );
        }
      }
    }

    // For records, extract objectSlug BEFORE mapping to ensure it doesn't get stripped
    let recordsObjectSlug: string | undefined;
    if (resource_type === UniversalResourceType.RECORDS) {
      const original = record_data;
      recordsObjectSlug = (original.object_api_slug ||
        original.object_slug ||
        original.object) as string;

      if (!recordsObjectSlug || typeof recordsObjectSlug !== 'string') {
        throw new UniversalValidationError(
          'Creating a "records" item requires an object slug (e.g., object: "companies")',
          ErrorType.USER_ERROR,
          { field: 'object' }
        );
      }
      logger.debug('RECORDS objectSlug extracted', {
        recordsObjectSlug,
      });
    }

    // Map field names to correct ones with collision detection
    const mappingResult = await mapRecordFields(
      resource_type,
      (record_data.values || record_data) as Record<string, unknown>,
      availableAttributes
    );
    if (mappingResult.errors && mappingResult.errors.length > 0) {
      // Check if this is a field collision error
      const firstError = mappingResult.errors[0];
      const collisionMatch = firstError.match(
        /Multiple fields map to "([^"]+)": (.+)/
      );

      if (collisionMatch) {
        const [, targetField, fieldsStr] = collisionMatch;
        const collidingFields = fieldsStr.split(', ');
        throw createFieldCollisionError(collidingFields, targetField);
      }

      // Generic mapping error with enhanced details
      throw createEnhancedValidationError(
        `Field mapping failed for ${resource_type}: ${mappingResult.errors.join(
          '; '
        )}`,
        {
          field: 'record_data',
          errorCode: 'FIELD_MAPPING_ERROR',
          suggestion: 'Check field names and resolve any conflicts',
          remediation: mappingResult.errors.slice(
            0,
            MAX_VALIDATION_SUGGESTIONS
          ),
        }
      );
    }

    const { mapped: mappedData, warnings } = mappingResult;
    if (warnings.length > 0) {
      logger.info('Field mapping applied with warnings', {
        warnings,
      });
    }

    // FEATURE: Enhanced validation for Issue #413 - conditionally disabled
    // Requires: task attribute metadata API support in Attio
    // Status: Ready for activation via ENABLE_ENHANCED_VALIDATION=true
    if (process.env.ENABLE_ENHANCED_VALIDATION === 'true') {
      const validation = await validateRecordFields(
        resource_type,
        mappedData as Record<string, unknown>,
        false
      );
      if (!validation.isValid) {
        const errorMessage = validation.error || 'Validation failed';
        throw new UniversalValidationError(errorMessage, ErrorType.USER_ERROR, {
          suggestion: 'Please fix the validation errors and try again.',
          field: undefined,
        });
      }
    }

    switch (resource_type) {
      case UniversalResourceType.COMPANIES: {
        const { CompanyCreateStrategy } = await import(
          './create/strategies/CompanyCreateStrategy.js'
        );
        return (await new CompanyCreateStrategy().create({
          resourceType: resource_type,
          values: mappedData,
        })) as AttioRecord;
      }

      case UniversalResourceType.LISTS: {
        const { ListCreateStrategy } = await import(
          './create/strategies/ListCreateStrategy.js'
        );
        return (await new ListCreateStrategy().create({
          resourceType: resource_type,
          values: mappedData,
        })) as AttioRecord;
      }

      case UniversalResourceType.PEOPLE: {
        const { PersonCreateStrategy } = await import(
          './create/strategies/PersonCreateStrategy.js'
        );
        return (await new PersonCreateStrategy().create({
          resourceType: resource_type,
          values: mappedData,
        })) as AttioRecord;
      }

      case UniversalResourceType.RECORDS: {
        const { RecordCreateStrategy } = await import(
          './create/strategies/RecordCreateStrategy.js'
        );
        const context = { objectSlug: recordsObjectSlug } as Record<
          string,
          unknown
        >;
        return (await new RecordCreateStrategy().create({
          resourceType: resource_type,
          values: mappedData,
          context,
        })) as AttioRecord;
      }

      case UniversalResourceType.DEALS: {
        const { DealCreateStrategy } = await import(
          './create/strategies/DealCreateStrategy.js'
        );
        return (await new DealCreateStrategy().create({
          resourceType: resource_type,
          values: mappedData,
        })) as AttioRecord;
      }

      case UniversalResourceType.TASKS: {
        const { TaskCreateStrategy } = await import(
          './create/strategies/TaskCreateStrategy.js'
        );
        return (await new TaskCreateStrategy().create({
          resourceType: resource_type,
          values: mappedData,
        })) as AttioRecord;
      }

      case UniversalResourceType.NOTES: {
        const { NoteCreateStrategy } = await import(
          './create/strategies/NoteCreateStrategy.js'
        );
        return (await new NoteCreateStrategy().create({
          resourceType: resource_type,
          values: mappedData,
        })) as AttioRecord;
      }

      default:
        return this.handleUnsupportedResourceType(resource_type, params);
    }
  }

  // (All resource-specific create flows are implemented by strategies above.)

  /**
   * Handle unsupported resource types with correction attempts
   */
  private static async handleUnsupportedResourceType(
    resource_type: string,
    params: UniversalCreateParams
  ): Promise<AttioRecord> {
    // Check if resource type can be corrected
    const resourceValidation = validateResourceType(resource_type);
    if (resourceValidation.corrected) {
      // Retry with corrected resource type
      logger.info('Resource type corrected', {
        originalType: resource_type,
        correctedType: resourceValidation.corrected,
      });
      return this.createRecord({
        ...params,
        resource_type: resourceValidation.corrected,
      });
    }
    throw new UniversalValidationError(
      `Unsupported resource type: ${resource_type}`,
      ErrorType.USER_ERROR,
      {
        suggestion:
          resourceValidation.suggestion ||
          `Valid resource types are: ${getValidResourceTypes()}`,
      }
    );
  }
}

/**
 * UniversalDeleteService - Centralized record deletion operations
 *
 * Extracted from shared-handlers.ts as part of Issue #489 Phase 3.
 * Provides universal delete functionality across all resource types.
 */

import { UniversalResourceType } from '../handlers/tool-configs/universal/types.js';
import type { UniversalDeleteParams } from '../handlers/tool-configs/universal/types.js';
import { isValidId } from '../utils/validation.js';
import { debug } from '../utils/logger.js';

// Import delete functions for each resource type
import {
  deleteCompany,
  getCompanyDetails,
} from '../objects/companies/index.js';
import { deletePerson } from '../objects/people-write.js';
import { deleteList, getListDetails } from '../objects/lists.js';
import {
  deleteObjectRecord,
  getObjectRecord,
} from '../objects/records/index.js';
import { deleteTask, getTask } from '../objects/tasks.js';
import { deleteNote } from '../objects/notes.js';
import { getPersonDetails } from '../objects/people/basic.js';
import { shouldUseMockData } from './create/index.js';

/**
 * UniversalDeleteService provides centralized record deletion functionality
 */
export class UniversalDeleteService {
  /**
   * Helper to detect 404 errors from various API error formats
   */
  private static is404Error(err: unknown): boolean {
    const anyErr = err as any;
    const status = anyErr?.response?.status ?? anyErr?.status;
    const code = anyErr?.response?.data?.code ?? anyErr?.code;
    const msg = (anyErr?.response?.data?.message ?? anyErr?.message ?? '')
      .toString()
      .toLowerCase();

    return (
      status === 404 ||
      code === 'not_found' ||
      msg.includes('not found') ||
      msg.includes('404')
    );
  }
  /**
   * Delete a record across any supported resource type
   *
   * @param params - Delete operation parameters
   * @returns Promise resolving to success status and record ID
   */
  static async deleteRecord(
    params: UniversalDeleteParams
  ): Promise<{ success: boolean; record_id: string }> {
    const { resource_type, record_id } = params;

    switch (resource_type) {
      case UniversalResourceType.COMPANIES:
        try {
          await deleteCompany(record_id);
          return { success: true, record_id };
        } catch (error: unknown) {
          // Map API errors to structured format
          if (this.is404Error(error)) {
            throw {
              status: 404,
              body: {
                code: 'not_found',
                message: `Company record with ID "${record_id}" not found.`,
              },
            };
          }
          throw error;
        }

      case UniversalResourceType.PEOPLE:
        try {
          await deletePerson(record_id);
          return { success: true, record_id };
        } catch (error: unknown) {
          // Map API errors to structured format
          if (this.is404Error(error)) {
            throw {
              status: 404,
              body: {
                code: 'not_found',
                message: `Person record with ID "${record_id}" not found.`,
              },
            };
          }
          throw error;
        }

      case UniversalResourceType.LISTS:
        await deleteList(record_id);
        return { success: true, record_id };

      case UniversalResourceType.RECORDS:
        try {
          await deleteObjectRecord('records', record_id);
          return { success: true, record_id };
        } catch (error: unknown) {
          // Map API errors to structured format
          if (this.is404Error(error)) {
            throw {
              status: 404,
              body: {
                code: 'not_found',
                message: `Record with ID "${record_id}" not found.`,
              },
            };
          }
          throw error;
        }

      case UniversalResourceType.DEALS:
        await deleteObjectRecord('deals', record_id);
        return { success: true, record_id };

      case UniversalResourceType.TASKS:
        // In mock mode, pre-validate IDs and emit deterministic message expected by tests
        if (shouldUseMockData()) {
          if (!isValidId(record_id)) {
            const err: any = new Error(`Task not found: ${record_id}`);
            err.status = 404;
            err.body = {
              code: 'not_found',
              message: `Task not found: ${record_id}`,
            };
            throw err;
          }
          if (
            process.env.NODE_ENV === 'development' ||
            process.env.VERBOSE_TESTS === 'true'
          ) {
            debug(
              'UniversalDeleteService',
              '[MockInjection] Using mock data for task deletion'
            );
          }
          return { success: true, record_id };
        }

        try {
          const resp = await deleteTask(record_id);

          // deleteTask returns boolean - if false, treat as not found
          if (resp === false) {
            const err: any = new Error(
              `Task with ID "${record_id}" not found.`
            );
            err.status = 404;
            err.body = {
              code: 'not_found',
              message: `Task with ID "${record_id}" not found.`,
            };
            throw err;
          }

          return { success: true, record_id };
        } catch (error: unknown) {
          // Map API errors to structured format, with a single retry for occasional eventual consistency
          if (this.is404Error(error)) {
            // Best-effort verification: if task still exists, wait briefly and retry once
            try {
              const exists = await getTask(record_id).then(
                () => true,
                () => false
              );
              if (exists) {
                await new Promise((r) => setTimeout(r, 500));
                const retried = await deleteTask(record_id);
                if (retried) return { success: true, record_id };
              }
            } catch {
              // ignore and fall through to not_found mapping
            }

            const err: any = new Error(
              `Task with ID "${record_id}" not found.`
            );
            err.status = 404;
            err.body = {
              code: 'not_found',
              message: `Task with ID "${record_id}" not found.`,
            };
            throw err; // dispatcher should mark isError=true
          }
          // Map specific 400 errors for task ID validation to clearer messages
          const anyErr: any = error as any;
          const status = anyErr?.response?.status ?? anyErr?.status;
          const errorMessage = (
            anyErr?.response?.data?.message ??
            anyErr?.message ??
            ''
          )
            .toString()
            .toLowerCase();

          if (status === 400) {
            // Only map task_id related 400 errors to avoid masking other validation issues
            if (
              errorMessage.includes('task_id') ||
              errorMessage.includes('invalid task') ||
              errorMessage.includes('malformed')
            ) {
              // Throw a plain Error so the MCP wrapper surfaces the message text (matches test regex)
              throw new Error(`Invalid request to delete task ${record_id}`);
            }
            // For other 400 errors, preserve original error to maintain validation visibility
            debug(
              'UniversalDeleteService',
              `Preserving 400 error for task deletion (not task_id related): ${errorMessage}`
            );
          }
          throw error;
        }

      case UniversalResourceType.NOTES:
        const result = await deleteNote(record_id);
        return { success: result.success, record_id };

      default:
        throw new Error(
          `Unsupported resource type for delete: ${resource_type}`
        );
    }
  }
}

/**
 * UniversalMetadataService - Centralized metadata and attribute operations
 *
 * Extracted from shared-handlers.ts as part of Issue #489 Phase 3.
 * Provides universal metadata discovery and attribute management across all resource types.
 */

import { UniversalResourceType } from '../handlers/tool-configs/universal/types.js';
import type { UniversalAttributesParams } from '../handlers/tool-configs/universal/types.js';
import { getLazyAttioClient } from '../api/lazy-client.js';
import { secureValidateCategories } from '../utils/validation/field-validation.js';
import { CachingService } from './CachingService.js';
import { OBJECT_SLUG_MAP } from '../constants/universal.constants.js';
import type {
  AttioAttribute,
  AttributeResponse,
  UnknownRecord,
} from '../types/service-types.js';
import { isAttioAttribute } from '../types/service-types.js';
import {
  debug,
  error,
  info,
  OperationType,
  createScopedLogger,
} from '../utils/logger.js';

// Import resource-specific attribute functions
import {
  getCompanyAttributes,
  discoverCompanyAttributes,
} from '../objects/companies/index.js';
import { getListAttributes } from '../objects/lists.js';

// Create scoped logger for this service
const logger = createScopedLogger(
  'UniversalMetadataService',
  undefined,
  OperationType.DATA_PROCESSING
);

/**
 * Performance metrics tracking for attribute discovery operations
 */
class AttributeDiscoveryMetrics {
  private static metrics: Array<{
    timestamp: number;
    resourceType: string;
    objectSlug?: string;
    duration: number;
    cacheHit: boolean;
    attributeCount: number;
    error?: string;
  }> = [];

  static recordDiscovery(
    resourceType: string,
    duration: number,
    options?: {
      objectSlug?: string;
      cacheHit?: boolean;
      attributeCount?: number;
      error?: string;
    }
  ): void {
    this.metrics.push({
      timestamp: Date.now(),
      resourceType,
      objectSlug: options?.objectSlug,
      duration,
      cacheHit: options?.cacheHit || false,
      attributeCount: options?.attributeCount || 0,
      error: options?.error,
    });

    // Keep only last 1000 entries to prevent memory growth
    if (this.metrics.length > 1000) {
      this.metrics.shift();
    }

    // Log performance info
    if (options?.cacheHit) {
      debug('UniversalMetadataService', 'Attribute discovery cache hit', {
        resourceType,
        objectSlug: options.objectSlug,
        duration,
        attributeCount: options.attributeCount,
      });
    } else {
      info('UniversalMetadataService', 'Attribute discovery completed', {
        resourceType,
        objectSlug: options?.objectSlug,
        duration,
        attributeCount: options?.attributeCount,
        performance:
          duration < 1000 ? 'good' : duration < 3000 ? 'moderate' : 'slow',
      });
    }
  }

  static getMetrics(options?: {
    resourceType?: string;
    since?: number; // timestamp
    includeErrors?: boolean;
  }) {
    let filtered = this.metrics;

    if (options?.resourceType) {
      filtered = filtered.filter(
        (m) => m.resourceType === options.resourceType
      );
    }

    if (options?.since) {
      filtered = filtered.filter((m) => m.timestamp >= options.since!);
    }

    if (!options?.includeErrors) {
      filtered = filtered.filter((m) => !m.error);
    }

    const totalRequests = filtered.length;
    const cacheHits = filtered.filter((m) => m.cacheHit).length;
    const totalDuration = filtered.reduce((sum, m) => sum + m.duration, 0);
    const avgDuration = totalRequests > 0 ? totalDuration / totalRequests : 0;
    const errors = filtered.filter((m) => m.error).length;

    return {
      totalRequests,
      cacheHits,
      cacheHitRate: totalRequests > 0 ? cacheHits / totalRequests : 0,
      avgDuration,
      totalDuration,
      errors,
      errorRate: totalRequests > 0 ? errors / totalRequests : 0,
      slowRequests: filtered.filter((m) => m.duration > 3000).length,
      byResourceType: this.getResourceTypeBreakdown(filtered),
    };
  }

  private static getResourceTypeBreakdown(metrics: typeof this.metrics) {
    const breakdown: Record<
      string,
      {
        count: number;
        avgDuration: number;
        cacheHitRate: number;
        errorRate: number;
      }
    > = {};

    for (const metric of metrics) {
      if (!breakdown[metric.resourceType]) {
        breakdown[metric.resourceType] = {
          count: 0,
          avgDuration: 0,
          cacheHitRate: 0,
          errorRate: 0,
        };
      }

      const stats = breakdown[metric.resourceType];
      stats.count++;
      stats.avgDuration =
        (stats.avgDuration * (stats.count - 1) + metric.duration) / stats.count;

      if (metric.cacheHit) {
        stats.cacheHitRate =
          (stats.cacheHitRate * (stats.count - 1) + 1) / stats.count;
      } else {
        stats.cacheHitRate =
          (stats.cacheHitRate * (stats.count - 1)) / stats.count;
      }

      if (metric.error) {
        stats.errorRate =
          (stats.errorRate * (stats.count - 1) + 1) / stats.count;
      } else {
        stats.errorRate = (stats.errorRate * (stats.count - 1)) / stats.count;
      }
    }

    return breakdown;
  }

  static clearMetrics(): void {
    this.metrics = [];
  }
}

/**
 * UniversalMetadataService provides centralized metadata and attribute operations
 */
export class UniversalMetadataService {
  /**
   * Discover attributes for a specific resource type with caching support
   * Special handling for tasks which use /tasks API instead of /objects/tasks
   *
   * @param resourceType - The resource type to discover attributes for
   * @param options - Optional configuration including categories and object slug
   * @returns Promise resolving to attribute discovery results with caching metadata
   */
  static async discoverAttributesForResourceType(
    resourceType: UniversalResourceType,
    options?: {
      categories?: string[]; // Category filtering support
      objectSlug?: string; // Object slug for records routing
      useCache?: boolean; // Whether to use caching (default: true)
      cacheTtl?: number; // Custom cache TTL in milliseconds
    }
  ): Promise<Record<string, unknown>> {
    // Check if caching should be used (default: true)
    const useCache = options?.useCache !== false;
    const startTime = Date.now();

    // Handle tasks as special case - they don't use /objects/{type}/attributes
    if (resourceType === UniversalResourceType.TASKS) {
      if (useCache) {
        return CachingService.getOrLoadAttributes(
          async () => {
            const result = await this.discoverTaskAttributes(options);
            const duration = Date.now() - startTime;
            const attributeCount = Array.isArray(result?.attributes)
              ? result.attributes.length
              : 0;

            AttributeDiscoveryMetrics.recordDiscovery(resourceType, duration, {
              cacheHit: false,
              attributeCount,
            });

            return result;
          },
          resourceType,
          undefined,
          options?.cacheTtl
        ).then((result) => {
          if (result.fromCache) {
            const duration = Date.now() - startTime;
            const attributeCount = Array.isArray(result.data?.attributes)
              ? result.data.attributes.length
              : 0;

            AttributeDiscoveryMetrics.recordDiscovery(resourceType, duration, {
              cacheHit: true,
              attributeCount,
            });
          }
          return result.data;
        });
      }

      const result = await this.discoverTaskAttributes(options);
      const duration = Date.now() - startTime;
      const attributeCount = Array.isArray(result?.attributes)
        ? result.attributes.length
        : 0;

      AttributeDiscoveryMetrics.recordDiscovery(resourceType, duration, {
        cacheHit: false,
        attributeCount,
      });

      return result;
    }

    // Handle records as special case - they need object-specific routing
    if (resourceType === UniversalResourceType.RECORDS) {
      if (!options?.objectSlug) {
        throw new Error(
          'discoverAttributesForResourceType(records) requires objectSlug in options'
        );
      }

      if (useCache) {
        return CachingService.getOrLoadAttributes(
          async () => {
            const result = await this.discoverObjectAttributes(
              options.objectSlug!,
              options
            );
            const duration = Date.now() - startTime;
            const attributeCount = Array.isArray(result?.attributes)
              ? result.attributes.length
              : 0;

            AttributeDiscoveryMetrics.recordDiscovery(resourceType, duration, {
              objectSlug: options.objectSlug,
              cacheHit: false,
              attributeCount,
            });

            return result;
          },
          resourceType,
          options.objectSlug,
          options?.cacheTtl
        ).then((result) => {
          if (result.fromCache) {
            const duration = Date.now() - startTime;
            const attributeCount = Array.isArray(result.data?.attributes)
              ? result.data.attributes.length
              : 0;

            AttributeDiscoveryMetrics.recordDiscovery(resourceType, duration, {
              objectSlug: options.objectSlug,
              cacheHit: true,
              attributeCount,
            });
          }
          return result.data;
        });
      }

      const result = await this.discoverObjectAttributes(
        options.objectSlug,
        options
      );
      const duration = Date.now() - startTime;
      const attributeCount = Array.isArray(result?.attributes)
        ? result.attributes.length
        : 0;

      AttributeDiscoveryMetrics.recordDiscovery(resourceType, duration, {
        objectSlug: options.objectSlug,
        cacheHit: false,
        attributeCount,
      });

      return result;
    }

    // For standard resource types, use caching if enabled
    if (useCache) {
      return CachingService.getOrLoadAttributes(
        async () => {
          const result = await this.performAttributeDiscovery(
            resourceType,
            options
          );
          const duration = Date.now() - startTime;
          const attributeCount = Array.isArray(result?.attributes)
            ? result.attributes.length
            : 0;

          AttributeDiscoveryMetrics.recordDiscovery(resourceType, duration, {
            cacheHit: false,
            attributeCount,
          });

          return result;
        },
        resourceType,
        undefined,
        options?.cacheTtl
      ).then((result) => {
        if (result.fromCache) {
          const duration = Date.now() - startTime;
          const attributeCount = Array.isArray(result.data?.attributes)
            ? result.data.attributes.length
            : 0;

          AttributeDiscoveryMetrics.recordDiscovery(resourceType, duration, {
            cacheHit: true,
            attributeCount,
          });
        }
        return result.data;
      });
    }

    // Perform direct attribute discovery without caching
    try {
      const result = await this.performAttributeDiscovery(
        resourceType,
        options
      );
      const duration = Date.now() - startTime;
      const attributeCount = Array.isArray(result?.attributes)
        ? result.attributes.length
        : 0;

      AttributeDiscoveryMetrics.recordDiscovery(resourceType, duration, {
        cacheHit: false,
        attributeCount,
      });

      return result;
    } catch (error) {
      const duration = Date.now() - startTime;
      const errorMessage =
        error instanceof Error ? error.message : String(error);

      AttributeDiscoveryMetrics.recordDiscovery(resourceType, duration, {
        cacheHit: false,
        attributeCount: 0,
        error: errorMessage,
      });

      throw error;
    }
  }

  /**
   * Perform the actual attribute discovery API call
   * Extracted to support both cached and non-cached execution paths
   *
   * @private
   */
  private static async performAttributeDiscovery(
    resourceType: UniversalResourceType,
    options?: {
      categories?: string[];
    }
  ): Promise<Record<string, unknown>> {
    const client = getLazyAttioClient();

    try {
      // Convert resource type to API slug for schema discovery (uses plural object api_slugs)
      // Note: Attio's schema discovery uses /objects/{api_slug}/attributes where api_slug is plural
      const resourceSlug =
        OBJECT_SLUG_MAP[resourceType.toLowerCase()] ||
        resourceType.toLowerCase();
      let path = `/objects/${resourceSlug}/attributes`;

      // NEW: Add category filtering to query parameters with security validation
      if (options?.categories && options.categories.length > 0) {
        // Validate and sanitize category names to prevent injection attacks
        const validatedCategories = secureValidateCategories(
          options.categories,
          'category filtering in get-attributes'
        );

        if (validatedCategories.length > 0) {
          const categoriesParam = validatedCategories.join(',');
          path += `?categories=${encodeURIComponent(categoriesParam)}`;
        }
      }

      const response = await client.get<AttributeResponse>(path);

      // Tolerant parsing: Attio may return arrays, or objects with attributes/all/standard/custom
      const parsed = UniversalMetadataService.parseAttributesResponse(
        response?.data as unknown
      );

      // Create mapping from title to api_slug for compatibility
      const mappings: Record<string, string> = {};
      parsed.forEach((attr: unknown) => {
        if (isAttioAttribute(attr)) {
          mappings[attr.title] = attr.api_slug;
        }
      });

      return {
        attributes: parsed,
        mappings,
        count: parsed.length,
        resource_type: resourceType,
      };
    } catch (error: unknown) {
      logger.error(`Failed to discover attributes for ${resourceType}`, error, {
        resourceType,
        options,
      });

      // If it's a 404 or similar API error, convert to structured error for MCP error detection
      if (error && typeof error === 'object' && 'response' in error) {
        const axiosError = error as any;
        const status = axiosError.response?.status || 500;
        const message =
          axiosError.response?.data?.error?.message ||
          axiosError.response?.data?.message ||
          axiosError.message ||
          `API error: ${status}`;

        throw {
          status,
          body: {
            code: 'api_error',
            message: `Failed to discover ${resourceType} attributes: ${message}`,
          },
        };
      }

      throw new Error(
        `Failed to discover ${resourceType} attributes: ${
          error instanceof Error ? error.message : String(error)
        }`
      );
    }
  }

