      };
    }

    // Provide 'assignees' array for E2E expectations on update
    if (updateData.assigneeId) {
      (flatFields as any).assignees = [
        {
          referenced_actor_type: 'workspace-member',
          referenced_actor_id: String(updateData.assigneeId),
        },
      ];
    }

    const result = { ...attioRecord, ...flatFields } as AttioRecord &
      Record<string, unknown>;

    // Emit top-level assignees for E2E expectation
    if (updateData.assigneeId) {
      (result as any).assignees = [
        {
          referenced_actor_type: 'workspace-member',
          referenced_actor_id: String(updateData.assigneeId),
        },
      ];
    }

    try {
      const { logTaskDebug, inspectTaskRecordShape } = await import(
        '../../utils/task-debug.js'
      );
      logTaskDebug(
        'mock.updateTask',
        'Returning updated mock task',
        inspectTaskRecordShape(result)
      );
    } catch {}

    return result as any;
  }

  async createNote(noteData: {
    resource_type: string;
    record_id: string;
    title: string;
    content: string;
    format?: string;
  }): Promise<any> {
    // Validate required parameters
    if (
      !noteData.resource_type ||
      !noteData.record_id ||
      !noteData.title ||
      !noteData.content
    ) {
      throw new Error('missing required parameter');
    }

    // Extract UUID from record_id (handles URIs and raw UUIDs)
    const extractedRecordId = extractRecordId(noteData.record_id);
    if (!extractedRecordId) {
      throw new Error('record not found');
    }

    // Check for invalid IDs following test patterns
    const invalidPatterns = [
      '00000000-0000-0000-0000-000000000000',
      /invalid/i,
      /^invalid-.*-id-\d+$/,
    ];

    const isInvalidId = invalidPatterns.some((pattern) =>
      pattern instanceof RegExp
        ? pattern.test(extractedRecordId)
        : pattern === extractedRecordId
    );

    if (isInvalidId) {
      throw new Error('record not found');
    }

    // Generate mock note response following Attio API format
    const timestamp = Date.now();
    const baseNote = {
      id: {
        workspace_id: 'ws_mock',
        note_id: `note_${timestamp}`,
        record_id: extractedRecordId,
      },
      parent_object: noteData.resource_type,
      parent_record_id: extractedRecordId,
      title: noteData.title,
      content: noteData.content,
      content_markdown:
        noteData.format === 'markdown' || noteData.format === 'html'
          ? noteData.content
          : null,
      content_plaintext:
        noteData.format === 'plaintext' ? noteData.content : null,
      format: noteData.format || 'plaintext',
      tags: [],
      created_at: new Date().toISOString(),
    };

    // Apply E2E markers for test data cleanup
    const markedNote = this.applyE2EMarkers(baseNote);

    return markedNote;
  }

  async listNotes(params: {
    resource_type?: string;
    record_id?: string;
  }): Promise<unknown[]> {
    // Return empty array for mock mode (tests focus on creation)
    return [];
  }

  // Private helper methods

  /**
   * Apply consistent E2E test markers to mock data
   */
  private applyE2EMarkers(data: UnknownRecord, meta?: E2EMeta): UnknownRecord {
    const baseTags = new Set([
      ...(Array.isArray(data.tags) ? data.tags : []),
      'e2e-test',
      'e2e-suite:notes',
    ]);
    if (meta?.runId) {
      baseTags.add(`e2e-run:${meta.runId}`);
    }

    return {
      ...data,
      tags: Array.from(baseTags),
      metadata: Object.assign(data.metadata || {}, {
        e2e: true,
      }),
    };
  }
}

import type { AttioRecord } from '../../../types/attio.js';
import type { UniversalResourceType } from '../../../handlers/tool-configs/universal/types.js';
import type {
  CreateStrategy,
  CreateStrategyParams,
} from './BaseCreateStrategy.js';
import { getCreateService } from '../../create/index.js';
import { getFieldSuggestions } from '../../../handlers/tool-configs/universal/field-mapper.js';
import {
  UniversalValidationError,
  ErrorType,
} from '../../../handlers/tool-configs/universal/schemas.js';
import {
  getFormatErrorHelp,
  convertAttributeFormats,
} from '../../../utils/attribute-format-helpers.js';
import { enhanceUniquenessError } from '../../../handlers/tool-configs/universal/field-mapper/validators/uniqueness-validator.js';

export class CompanyCreateStrategy implements CreateStrategy<AttioRecord> {
  async create(params: CreateStrategyParams): Promise<AttioRecord> {
    const { values, resourceType } = params;
    try {
      // Apply format conversions like monolith for test parity
      const corrected = convertAttributeFormats('companies', values);
      const service = getCreateService();
      const result = (await service.createCompany(
        corrected
      )) as unknown as AttioRecord | null;
      if (!result) {
        throw new UniversalValidationError(
          'Company creation failed: createCompany returned null/undefined',
          ErrorType.API_ERROR,
          { field: 'result' }
        );
      }
      // Type guard for expected structure
      const hasRecordId =
        typeof (result as any)?.id?.record_id === 'string' &&
        (result as any).id.record_id.length > 0;
      if (!hasRecordId) {
        throw new UniversalValidationError(
          'Company creation failed: Invalid record structure',
          ErrorType.API_ERROR,
          { field: 'id' }
        );
      }
      return result as AttioRecord;
    } catch (err: unknown) {
      const msg = err instanceof Error ? err.message : String(err);
      if (msg.includes('Cannot find attribute')) {
        const match = msg.match(/slug\/ID "([^"]+)"/);
        if (match && match[1]) {
          const suggestion = getFieldSuggestions(resourceType, match[1]);
          const enhanced = getFormatErrorHelp('companies', match[1], msg);
          throw new UniversalValidationError(enhanced, ErrorType.USER_ERROR, {
            suggestion,
            field: match[1],
          });
        }
      }
      if (msg.includes('uniqueness constraint')) {
        throw new UniversalValidationError(
          'Uniqueness constraint violation for companies',
          ErrorType.USER_ERROR,
          {
            suggestion:
              'Try searching for existing records first or use different unique values',
          }
        );
      }
      throw err;
    }
  }
}

import type { AttioRecord } from '../../../types/attio.js';
import type {
  CreateStrategy,
