/**
 * Utility for validating function availability with consistent error handling
 * Issue #598: Extract common error handling pattern from UniversalSearchService
 */

import { debug, error } from '../../utils/logger.js';

/**
 * Generic function validation utility that follows the ensure function availability pattern
 *
 * @param fn - The function to validate
 * @param functionName - Name of the function for logging purposes
 * @param serviceName - Name of the service for logging context
 * @returns The validated function or null if invalid
 */
export const ensureFunctionAvailability = async <
  T extends (...args: any[]) => unknown,
>(
  fn: T | null | undefined,
  functionName: string,
  serviceName: string = 'UniversalSearchService'
): Promise<T | null> => {
  try {
    debug(serviceName, `Checking ${functionName} availability`, {
      type: typeof fn,
    });

    if (typeof fn !== 'function') {
      error(serviceName, `${functionName} is not a function`, {
        [functionName]: fn,
      });
      return null;
    }

    return fn as T;
  } catch (err) {
    error(serviceName, `Error accessing ${functionName}`, err);
    return null;
  }
};

/**
 * Search utilities extracted from UniversalSearchService
 * Issue #574: Extract shared utilities for reuse
 */

import { AttioRecord } from '../../types/attio.js';
import { TimeframeParams } from '../search-strategies/interfaces.js';

/**
 * Interfaces for type safety improvements (Issue #598)
 */
interface AttioFieldValueObject {
  value: unknown;
}

interface AttioFieldValueArray extends Array<string | AttioFieldValueObject> {}

type AttioFieldValue =
  | string
  | AttioFieldValueArray
  | AttioFieldValueObject
  | null
  | undefined;

/**
 * Utility functions for search operations
 */
export class SearchUtilities {
  /**
   * Rank search results by relevance based on query match frequency
   * This provides client-side relevance scoring since Attio API doesn't have native relevance ranking
   */
  static rankByRelevance(
    results: AttioRecord[],
    query: string,
    searchFields: string[]
  ): AttioRecord[] {
    // Calculate relevance score for each result
    const scoredResults = results.map((record) => {
      let score = 0;
      const queryLower = query.toLowerCase();

      // Check each search field for matches
      searchFields.forEach((field) => {
        const fieldValue = this.getFieldValue(record, field);
        if (fieldValue) {
          const valueLower = fieldValue.toLowerCase();

          // Exact match gets highest score
          if (valueLower === queryLower) {
            score += 100;
          }
          // Starts with query gets high score
          else if (valueLower.startsWith(queryLower)) {
            score += 50;
          }
          // Contains query gets moderate score
          else if (valueLower.includes(queryLower)) {
            score += 25;
            // Additional score for more occurrences
            const matches = valueLower.split(queryLower).length - 1;
            score += matches * 10;
          }
          // Partial word match gets lower score
          else {
            const queryWords = queryLower.split(/\s+/);
            queryWords.forEach((word) => {
              if (valueLower.includes(word)) {
                score += 5;
              }
            });
          }
        }
      });

      return { record, score };
    });

    // Sort by score (descending) then by name
    scoredResults.sort((a, b) => {
      if (b.score !== a.score) {
        return b.score - a.score;
      }
      // Secondary sort by name if scores are equal
      const nameA = this.getFieldValue(a.record, 'name') || '';
      const nameB = this.getFieldValue(b.record, 'name') || '';
      return nameA.localeCompare(nameB);
    });

    return scoredResults.map((item) => item.record);
  }

  /**
   * Helper method to extract field value from a record
   * Issue #598: Simplified with better type safety and helper methods
   */
  static getFieldValue(record: AttioRecord, field: string): string {
    const values = record.values as Record<string, AttioFieldValue>;
    if (!values) return '';

    const fieldValue = values[field];
    return this.extractStringFromFieldValue(fieldValue);
  }

  /**
   * Extract string value from various Attio field value structures
   */
  private static extractStringFromFieldValue(
    fieldValue: AttioFieldValue
  ): string {
    if (typeof fieldValue === 'string') {
      return fieldValue;
    }

    if (Array.isArray(fieldValue)) {
      return this.extractStringFromArray(fieldValue);
    }

    if (this.isFieldValueObject(fieldValue)) {
      return String(fieldValue.value || '');
    }

    return '';
  }

  /**
   * Extract string from array field values (e.g., email_addresses)
   */
  private static extractStringFromArray(
    fieldArray: AttioFieldValueArray
  ): string {
    if (fieldArray.length === 0) return '';

    const firstItem = fieldArray[0];
    if (typeof firstItem === 'string') {
      return firstItem;
    }

    if (this.isFieldValueObject(firstItem)) {
      return String(firstItem.value || '');
    }

    return '';
  }

  /**
   * Type guard for field value objects
   */
  private static isFieldValueObject(
    value: unknown
  ): value is AttioFieldValueObject {
    return (
      value !== null &&
      value !== undefined &&
      typeof value === 'object' &&
      'value' in value
    );
  }

  /**
   * Helper method to extract field value from a list record for content search
   */
  static getListFieldValue(list: AttioRecord, field: string): string {
    const values = list.values as Record<string, unknown>;
    if (!values) return '';

    const fieldValue = values[field];

    // Handle different field value structures for lists
    if (typeof fieldValue === 'string') {
      return fieldValue;
    } else if (
      fieldValue &&
      typeof fieldValue === 'object' &&
      'value' in fieldValue
    ) {
      return String((fieldValue as { value: unknown }).value || '');
    }

    return '';
  }

  /**
   * Helper method to extract field value from a task record for content search
   */
  static getTaskFieldValue(task: AttioRecord, field: string): string {
    const values = task.values as Record<string, unknown>;
    if (!values) return '';

    const fieldValue = values[field];

    // Handle different field value structures for tasks
    if (typeof fieldValue === 'string') {
      return fieldValue;
    } else if (
      fieldValue &&
      typeof fieldValue === 'object' &&
      'value' in fieldValue
    ) {
      return String((fieldValue as { value: unknown }).value || '');
    }

    return '';
  }

  /**
   * Create date filter from timeframe parameters
   */
  static createDateFilter(
    timeframeParams: TimeframeParams
  ): Record<string, unknown> | null {
    const { timeframe_attribute, start_date, end_date, date_operator } =
      timeframeParams;

    if (!timeframe_attribute) {
      return null;
    }

    const filters: Array<Record<string, unknown>> = [];

    if (date_operator === 'between' && start_date && end_date) {
      // Between date range - use valid API conditions
      filters.push({
        attribute: { slug: timeframe_attribute },
        condition: 'greater_than',
        value: start_date,
      });
      filters.push({
        attribute: { slug: timeframe_attribute },
        condition: 'less_than',
        value: end_date,
      });
    } else if (date_operator === 'greater_than' && start_date) {
      // After start date - use valid API condition
      filters.push({
        attribute: { slug: timeframe_attribute },
        condition: 'greater_than',
        value: start_date,
      });
    } else if (date_operator === 'less_than' && end_date) {
      // Before end date - use valid API condition
      filters.push({
        attribute: { slug: timeframe_attribute },
        condition: 'less_than',
        value: end_date,
      });
    } else if (date_operator === 'equals' && start_date) {
      // Exact date match
      filters.push({
        attribute: { slug: timeframe_attribute },
        condition: 'equals',
        value: start_date,
      });
    }

    if (filters.length === 0) {
      return null;
    }

    return {
      filters,
      matchAny: false, // Use AND logic for date ranges
    };
  }

  /**
   * Merge timeframe filters with existing filters
   */
  static mergeFilters(
    existingFilters: Record<string, unknown> | undefined,
    dateFilter: Record<string, unknown>
  ): Record<string, unknown> {
    if (!existingFilters) {
      return dateFilter;
    }

    // If existing filters already has a filters array, merge them
    if (
      Array.isArray(existingFilters.filters) &&
      Array.isArray(dateFilter.filters)
    ) {
      return {
        ...existingFilters,
        filters: [...existingFilters.filters, ...dateFilter.filters],
      };
    }

    // Otherwise, create a new structure with both sets of filters
    const existingFilterArray = Array.isArray(existingFilters.filters)
      ? existingFilters.filters
      : [];
    const dateFilterArray = Array.isArray(dateFilter.filters)
      ? dateFilter.filters
      : [];

    return {
      ...existingFilters,
      filters: [...existingFilterArray, ...dateFilterArray],
      // Preserve existing matchAny logic if it exists
      matchAny: existingFilters.matchAny || false,
    };
  }
}

import type { AttioRecord } from '../../types/attio.js';
import { FilterValidationError } from '../../errors/api-errors.js';
import { shouldUseMockData } from '../create/index.js';
import { UniversalUtilityService } from '../UniversalUtilityService.js';
import { getCompanyDetails } from '../../objects/companies/index.js';
import { getListDetails } from '../../objects/lists.js';
import { getPersonDetails } from '../../objects/people/basic.js';
import { getObjectRecord } from '../../objects/records/index.js';
import { getTask } from '../../objects/tasks.js';
import type { UniversalResourceType } from '../../handlers/tool-configs/universal/types.js';

export const UpdateValidation = {
  hasForbiddenContent(values: Record<string, unknown>): boolean {
    if (!values || typeof values !== 'object') return false;
    const forbidden = ['content', 'content_markdown', 'content_plaintext'];
    return forbidden.some((field) => field in values);
  },

  assertNoTaskContentUpdate(recordData: Record<string, unknown>): void {
    if (!recordData || typeof recordData !== 'object') return;
    if (this.hasForbiddenContent(recordData)) {
      throw new FilterValidationError(
        'Task content cannot be updated after creation. Content is immutable in the Attio API.'
      );
    }
  },

  sanitizeSpecialCharacters(
    data: Record<string, unknown>
  ): Record<string, unknown> {
    const sanitized: Record<string, unknown> = {};
    for (const [key, value] of Object.entries(data)) {
      if (typeof value === 'string') {
        sanitized[key] = value;
      } else if (Array.isArray(value)) {
        sanitized[key] = value.map((item) =>
          typeof item === 'string' ? item : item
        );
      } else if (value && typeof value === 'object') {
        sanitized[key] = this.sanitizeSpecialCharacters(
          value as Record<string, unknown>
        );
      } else {
        sanitized[key] = value;
      }
    }
    return sanitized;
  },

  async verifyFieldPersistence(
    resourceType: UniversalResourceType,
    recordId: string,
    expectedUpdates: Record<string, unknown>,
    updatedRecord: AttioRecord
  ): Promise<{
    verified: boolean;
    discrepancies: string[];
    warnings: string[];
  }> {
    const result = {
      verified: true,
      discrepancies: [] as string[],
      warnings: [] as string[],
    };
    if (shouldUseMockData() || process.env.SKIP_FIELD_VERIFICATION === 'true') {
      result.warnings.push(
        'Field persistence verification skipped in test environment'
      );
      return result;
    }

    try {
      const verificationRecord = await this.fetchRecordForVerification(
        resourceType,
        recordId
      );
      if (!verificationRecord) {
        result.verified = false;
        result.discrepancies.push(
          'Unable to fetch record for field persistence verification'
        );
        return result;
      }

      for (const [fieldName, expectedValue] of Object.entries(
        expectedUpdates
      )) {
        if (
          ['created_at', 'updated_at', 'id', 'workspace_id'].includes(fieldName)
        )
          continue;
        const actualValue = verificationRecord.values?.[fieldName];
        const comparisonResult = this.compareFieldValues(
          fieldName,
          expectedValue,
          actualValue
        );
        if (!comparisonResult.matches) {
          result.verified = false;
          result.discrepancies.push(
            `Field "${fieldName}" persistence mismatch: expected ${JSON.stringify(expectedValue)}, got ${JSON.stringify(actualValue)}`
          );
        } else if (comparisonResult.warning) {
          result.warnings.push(comparisonResult.warning);
        }
      }
    } catch (error: unknown) {
      const errorMessage =
        error instanceof Error ? error.message : String(error);
      result.warnings.push(
        `Field persistence verification failed: ${errorMessage}`
      );
      console.error('Field persistence verification error:', error);
    }
    return result;
  },

  async fetchRecordForVerification(
    resourceType: UniversalResourceType,
    recordId: string
  ): Promise<AttioRecord | null> {
    try {
      switch (resourceType) {
        case 'companies' as unknown as UniversalResourceType:
          return (await getCompanyDetails(recordId)) as unknown as AttioRecord;
        case 'people' as unknown as UniversalResourceType:
          return (await getPersonDetails(recordId)) as unknown as AttioRecord;
        case 'lists' as unknown as UniversalResourceType: {
          const list = await getListDetails(recordId);
          return {
            id: { record_id: list.id.list_id, list_id: list.id.list_id },
            values: {
              name: (list as any).name || (list as any).title,
              description: (list as any).description,
              parent_object:
                (list as any).object_slug || (list as any).parent_object,
              api_slug: (list as any).api_slug,
              workspace_id: (list as any).workspace_id,
              workspace_member_access: (list as any).workspace_member_access,
              created_at: (list as any).created_at,
            },
          } as unknown as AttioRecord;
        }
        case 'tasks' as unknown as UniversalResourceType: {
          const task = await getTask(recordId);
          return UniversalUtilityService.convertTaskToRecord(task);
        }
        case 'deals' as unknown as UniversalResourceType:
          return await getObjectRecord('deals', recordId);
        case 'records' as unknown as UniversalResourceType:
          return await getObjectRecord('records', recordId);
        default:
          console.warn(
            `No verification method available for resource type: ${resourceType}`
          );
          return null;
      }
    } catch (error: unknown) {
      console.error(
        `Failed to fetch ${resourceType} record ${recordId} for verification:`,
        error
      );
      return null;
    }
  },

  compareFieldValues(
    fieldName: string,
    expectedValue: unknown,
    actualValue: unknown
  ): { matches: boolean; warning?: string } {
    if (expectedValue === null || expectedValue === undefined) {
      return { matches: actualValue === null || actualValue === undefined };
    }
    if (actualValue === null || actualValue === undefined) {
      return { matches: false };
    }
    let unwrappedActual = actualValue;
    if (
      Array.isArray(actualValue) &&
      actualValue.length > 0 &&
      (actualValue as any)[0]?.value !== undefined
    ) {
      unwrappedActual =
        (actualValue as any).length === 1
          ? (actualValue as any)[0].value
          : (actualValue as any).map((v: any) => v.value);
    }
    if (Array.isArray(expectedValue)) {
      if (!Array.isArray(unwrappedActual)) return { matches: false };
      const expectedSet = new Set(
        (expectedValue as unknown[]).map((v) => String(v))
      );
      const actualSet = new Set(
        (unwrappedActual as unknown[]).map((v) => String(v))
      );
      return {
        matches:
          expectedSet.size === actualSet.size &&
          Array.from(expectedSet).every((v) => actualSet.has(v)),
      };
    }
    if (typeof expectedValue === 'string') {
      const actualStr = String(unwrappedActual);
      const matches = expectedValue === actualStr;
      if (!matches && expectedValue.toLowerCase() === actualStr.toLowerCase()) {
        return {
          matches: true,
          warning: `Field "${fieldName}" case mismatch: expected "${expectedValue}", got "${actualStr}"`,
        };
      }
      return { matches };
    }
    if (typeof expectedValue === 'number') {
      const actualNum = Number(unwrappedActual);
      return { matches: !isNaN(actualNum) && expectedValue === actualNum };
    }
    if (typeof expectedValue === 'boolean') {
      const actualBool = Boolean(unwrappedActual);
      return { matches: expectedValue === actualBool };
    }
    return { matches: String(expectedValue) === String(unwrappedActual) };
  },
};

import type { AttioRecord } from '../../../types/attio.js';
import type { UniversalResourceType } from '../../../handlers/tool-configs/universal/types.js';
import type { UpdateStrategy } from './BaseUpdateStrategy.js';
import { shouldUseMockData, getCreateService } from '../../create/index.js';
import { getTask } from '../../../objects/tasks.js';
import { FilterValidationError } from '../../../errors/api-errors.js';
import { UpdateValidation } from '../UpdateValidation.js';

export class TaskUpdateStrategy implements UpdateStrategy {
  async update(
    recordId: string,
    values: Record<string, unknown>,
    _resourceType: UniversalResourceType
  ): Promise<AttioRecord> {
    // 1) Existence check (skip in mock mode)
    try {
      if (!shouldUseMockData()) {
        await getTask(recordId);
      }
    } catch {
      throw {
        status: 404,
        body: {
          code: 'not_found',
          message: `Task record with ID "${recordId}" not found.`,
        },
      };
    }

    // 2) Validate immutability after confirming existence
    UpdateValidation.assertNoTaskContentUpdate(values);

    // 3) Proceed with update via mock or real service
    return this.doUpdateTask(recordId, values);
  }

  private async doUpdateTask(
    recordId: string,
    mappedData: Record<string, unknown>
  ): Promise<AttioRecord> {
    const taskUpdateData: Record<string, unknown> = {};

    // status / is_completed mapping
    if (mappedData.is_completed !== undefined) {
      taskUpdateData.status = mappedData.is_completed ? 'completed' : 'pending';
    } else if (mappedData.status !== undefined) {
      taskUpdateData.status = mappedData.status;
    }

    // assignees  assigneeId
    if (mappedData.assignees !== undefined) {
      const value = mappedData.assignees as unknown;
      let assigneeId: string | undefined;
      if (Array.isArray(value as unknown[])) {
        const first = (value as unknown[])[0] as unknown;
        if (typeof first === 'string') assigneeId = first;
        else if (first && typeof first === 'object') {
          const fo = first as Record<string, unknown>;
          assigneeId =
            (fo.referenced_actor_id as string) ||
            (fo.id as string) ||
            (fo.record_id as string) ||
            (fo.value as string);
        }
      } else if (typeof value === 'string') {
        assigneeId = value as string;
      } else if (value && typeof value === 'object') {
        const vo = value as Record<string, unknown>;
        assigneeId =
          (vo.referenced_actor_id as string) ||
          (vo.id as string) ||
          (vo.record_id as string) ||
          (vo.value as string);
      }
      if (assigneeId) taskUpdateData.assigneeId = assigneeId;
    } else if (mappedData.assignee_id !== undefined) {
      taskUpdateData.assigneeId = mappedData.assignee_id;
    } else if (mappedData.assigneeId !== undefined) {
      taskUpdateData.assigneeId = mappedData.assigneeId;
    }

    // due dates
    if (mappedData.deadline_at !== undefined) {
      taskUpdateData.dueDate = mappedData.deadline_at;
    } else if (mappedData.due_date !== undefined) {
      taskUpdateData.dueDate = mappedData.due_date;
    } else if (mappedData.dueDate !== undefined) {
      taskUpdateData.dueDate = mappedData.dueDate;
    }

    // linked records
    if (mappedData.linked_records !== undefined) {
      if (Array.isArray(mappedData.linked_records)) {
        taskUpdateData.recordIds = (mappedData.linked_records as unknown[]).map(
          (link: unknown) => {
            if (!link || typeof link !== 'object') return link as unknown;
            const lo = link as Record<string, unknown>;
            return (
              (lo.record_id as string) || (lo.id as string) || (lo as unknown)
            );
          }
        );
      } else {
        taskUpdateData.recordIds = [mappedData.linked_records];
      }
    } else if (mappedData.record_id !== undefined) {
      taskUpdateData.recordIds = [mappedData.record_id];
    }

    // Debug hook
    try {
      const mod = (await import('../../../utils/task-debug.js')) as Record<
        string,
        unknown
      >;
      const fn = mod['logTaskDebug'] as unknown;
      if (typeof fn === 'function')
        (fn as Function)('UPDATE_REQUEST', { recordId, taskUpdateData });
    } catch {}

    // Execute update
    const result = await this.updateTaskWithMockSupport(
      recordId,
      taskUpdateData
    );
    return result as AttioRecord;
  }

  private async updateTaskWithMockSupport(
    taskId: string,
    updateData: Record<string, unknown>
  ): Promise<AttioRecord> {
    if (
      shouldUseMockData() ||
      process.env.VITEST === 'true' ||
      process.env.NODE_ENV === 'test'
    ) {
      const { MockService } = await import('../../MockService.js');
      return (await MockService.updateTask(
        taskId,
        updateData
      )) as unknown as AttioRecord;
    }
    const service = getCreateService();
    return (await service.updateTask(
      taskId,
      updateData
    )) as unknown as AttioRecord;
  }
}

import { testDataRegistry } from './test-data-registry.js';

const MOCK_RE = /^mock-(person|company)-/i;

// Try common keys first; then shallow-scan values.
export function findMockAlias(obj: any): string | null {
  if (!process.env.E2E_MODE) return null;
  if (!obj || typeof obj !== 'object') return null;
  const keys = [
    'mock_id',
    'test_alias',
    'alias',
    'external_id',
    'test_id',
    'id_alias',
  ];
  for (const k of keys) {
    const v = (obj as any)[k];
    if (typeof v === 'string' && MOCK_RE.test(v)) return v;
  }
  for (const v of Object.values(obj)) {
    if (typeof v === 'string' && MOCK_RE.test(v)) return v;
  }
  return null;
}

export function registerMockAliasIfPresent(recordData: any, realId: string) {
  const alias = findMockAlias(recordData);
  if (alias && realId) testDataRegistry.set(alias, realId);
}

/**
 * Type definitions for batch operations
 * Created as part of TypeScript 'any' reduction initiative (Issue #502)
 */

import { AttioRecord } from './attio.js';

/**
 * Generic batch response structure from Attio API
 */
export interface BatchResponse<T = AttioRecord> {
  results: BatchItemResult<T>[];
  summary: BatchSummary;
}

/**
 * Individual batch operation result
 */
export interface BatchItemResult<T = AttioRecord> {
  success: boolean;
  data?: T;
  error?: BatchError;
  index?: number;
  id?: string | number;
}

/**
 * Batch operation summary
 */
export interface BatchSummary {
  total: number;
  succeeded: number;
  failed: number;
  skipped?: number;
}

/**
 * Batch operation error details
 */
export interface BatchError {
  message: string;
  code?: string;
  details?: Record<string, unknown>;
  index?: number;
}

/**
 * Batch operation request structure
 */
export interface BatchRequest<T = unknown> {
  operations: T[];
  options?: BatchOptions;
}

/**
 * Batch operation options
 */
export interface BatchOptions {
  stopOnError?: boolean;
  validateBeforeExecute?: boolean;
  maxConcurrent?: number;
}

/**
 * Type for batch formatter functions
 */
export type BatchFormatter<T = AttioRecord> = (
  response: BatchResponse<T>
) => string;

/**
 * Type for batch handler functions
 */
export type BatchHandler<T = AttioRecord> = (
  params: Record<string, unknown>
) => Promise<BatchResponse<T>>;

/**
 * Type guard for batch response
 */
export function isBatchResponse<T = AttioRecord>(
  value: unknown
): value is BatchResponse<T> {
  return (
    typeof value === 'object' &&
    value !== null &&
    'results' in value &&
    Array.isArray((value as Record<string, unknown>).results) &&
    'summary' in value
  );
}

/**
 * Type guard for batch item result
 */
export function isBatchItemResult<T = AttioRecord>(
  value: unknown
): value is BatchItemResult<T> {
  return (
    typeof value === 'object' &&
    value !== null &&
    'success' in value &&
    typeof (value as Record<string, unknown>).success === 'boolean'
  );
}

/**
 * Safely extract batch summary
 */
export function extractBatchSummary(response: unknown): BatchSummary {
  if (isBatchResponse(response)) {
    return response.summary;
  }

  // Fallback for malformed responses
  const obj = response as Record<string, any>;
  return {
    total: obj?.summary?.total || 0,
    succeeded: obj?.summary?.succeeded || 0,
    failed: obj?.summary?.failed || 0,
    skipped: obj?.summary?.skipped || 0,
  } as BatchSummary;
}

/**
 * Safely extract batch results
 */
export function extractBatchResults<T = AttioRecord>(
  response: unknown
): BatchItemResult<T>[] {
  if (isBatchResponse<T>(response)) {
    return response.results;
  }

  // Fallback for malformed responses
  const obj = response as Record<string, any>;
  if (Array.isArray(obj?.results)) {
    return obj.results as BatchItemResult<T>[];
  }

  return [];
}

/**
 * Type definitions for list operations
 * Created as part of TypeScript 'any' reduction initiative (Issue #502)
 */

import { AttioList, AttioListEntry } from './attio.js';

/**
 * List membership information for a record
 */
export interface ListMembership {
  listId: string;
  listName: string;
  entryId: string;
  entryValues?: ListEntryValues;
}

/**
 * Type-safe list entry values
 */
export type ListEntryValues = Record<string, unknown>;

/**
 * List operation parameters
 */
export interface ListOperationParams {
  listId: string;
  recordId?: string;
  objectType?: string;
  limit?: number;
  offset?: number;
  filters?: ListEntryFilters;
}

/**
 * List entry filters with proper typing
 */
export interface ListEntryFilters {
  filters: ListFilter[];
  sort?: ListSort[];
}

/**
 * Individual list filter
 */
export interface ListFilter {
  field: string;
  operator: string;
  value: unknown;
}

/**
 * List sorting configuration
 */
export interface ListSort {
  field: string;
  direction: 'asc' | 'desc';
}

/**
 * Batch list operation request
 */
export interface BatchListRequest {
  operations: ListOperation[];
  options?: BatchListOptions;
}

/**
 * Individual list operation
 */
export interface ListOperation {
  type: 'add' | 'remove' | 'update' | 'get';
  listId: string;
  recordId?: string;
  data?: ListEntryValues;
}

/**
 * Batch list operation options
 */
export interface BatchListOptions {
  stopOnError?: boolean;
  maxConcurrent?: number;
}

/**
 * List API endpoint configuration
 */
export interface ListEndpointConfig {
  method: 'get' | 'post';
  path: string;
  data?: Record<string, unknown>;
}

/**
 * Type guard for list membership
 */
export function isListMembership(value: unknown): value is ListMembership {
  return (
    typeof value === 'object' &&
    value !== null &&
    'listId' in value &&
    'listName' in value &&
    'entryId' in value
  );
}

/**
 * Type guard for list entry filters
 */
export function isListEntryFilters(value: unknown): value is ListEntryFilters {
  return (
    typeof value === 'object' &&
    value !== null &&
    'filters' in value &&
    Array.isArray((value as Record<string, unknown>).filters)
  );
}

/**
 * Safely extract list entry values
 */
export function extractListEntryValues(entry: unknown): ListEntryValues {
  if (typeof entry !== 'object' || entry === null) {
    return {};
  }

  const obj = entry as Record<string, unknown>;

  // Check for common value field names
  if (obj.values && typeof obj.values === 'object') {
    return obj.values as ListEntryValues;
  }

  if (obj.entryValues && typeof obj.entryValues === 'object') {
    return obj.entryValues as ListEntryValues;
  }

  // If no specific values field, return the object itself (minus metadata)
  const { id, listId, entryId, ...values } = obj;
  return values as ListEntryValues;
}

/**
 * Type for list formatter functions
 */
export type ListFormatter = (items: AttioList[] | AttioListEntry[]) => string;

/**
 * Type for list handler functions
 */
export type ListHandler = (
  params: ListOperationParams
) => Promise<AttioList[] | AttioListEntry[]>;

/**
 * Helper to check if error has axios-like response
 */
export function hasErrorResponse(error: unknown): error is {
  response?: {
    status?: number;
    data?: {
      validation_errors?: Array<{
        path?: string[];
        message?: string;
      }>;
      [key: string]: unknown;
    };
  };
  message?: string;
} {
  return typeof error === 'object' && error !== null && 'response' in error;
}

/**
 * Attio API Filter Operators and Validation
 *
 * Based on official Attio API documentation (Context7 research)
 * Provides operator normalization, validation, and 429 backoff handling
 */

export enum FilterOperator {
  // Equality and existence
  EQUALS = '$eq',
  NOT_EMPTY = '$not_empty',

  // String operations
  CONTAINS = '$contains',
  STARTS_WITH = '$starts_with',
  ENDS_WITH = '$ends_with',

  // Numeric/date comparisons
  GREATER_THAN = '$gt',
  GREATER_THAN_OR_EQUAL = '$gte',
  LESS_THAN = '$lt',
  LESS_THAN_OR_EQUAL = '$lte',

  // Array operations (resource-specific)
  IN = '$in',

  // Logical combinators (filter-expression level)
  AND = '$and',
  OR = '$or',
  NOT = '$not',
}

/**
 * Legacy operator mappings for backward compatibility
 * Logs deprecation warnings when used
 */
const LEGACY_OPERATOR_MAP: Record<string, FilterOperator> = {
  gte: FilterOperator.GREATER_THAN_OR_EQUAL,
  lte: FilterOperator.LESS_THAN_OR_EQUAL,
  gt: FilterOperator.GREATER_THAN,
  lt: FilterOperator.LESS_THAN,
  eq: FilterOperator.EQUALS,
  not_empty: FilterOperator.NOT_EMPTY,
  is_not_empty: FilterOperator.NOT_EMPTY, // Common mistake
  contains: FilterOperator.CONTAINS,
  starts_with: FilterOperator.STARTS_WITH,
  ends_with: FilterOperator.ENDS_WITH,
  in: FilterOperator.IN,
};

export function isValidOperator(op: string): boolean {
  return Object.values(FilterOperator).includes(op as FilterOperator);
}

/**
 * Normalize legacy operators to proper $ prefixed format
 * Logs deprecation warnings for legacy usage
 */
export function normalizeOperator(op: string): FilterOperator {
  // Already normalized
  if (isValidOperator(op)) {
    return op as FilterOperator;
  }

  // Legacy mapping with warning
  const normalized = LEGACY_OPERATOR_MAP[op];
  if (normalized) {
    console.warn(
      `[AttioFilterOperators] Deprecated operator '${op}' used. Use '${normalized}' instead.`
    );
    return normalized;
  }

  throw new Error(
    `Invalid filter operator: '${op}'. Supported: ${Object.values(
      FilterOperator
    ).join(', ')}`
  );
}

/**
 * Validate filter operator and value shape
 */
export function validateFilter(
  operator: string,
  value: unknown
): { valid: boolean; error?: string } {
  const normalizedOp = normalizeOperator(operator);

  switch (normalizedOp) {
    case FilterOperator.NOT_EMPTY:
      if (typeof value !== 'boolean') {
        return {
          valid: false,
          error: '$not_empty requires boolean value (true)',
        };
      }
      break;

    case FilterOperator.IN:
      if (!Array.isArray(value)) {
        return { valid: false, error: '$in requires array value' };
      }
      break;

    case FilterOperator.GREATER_THAN:
    case FilterOperator.GREATER_THAN_OR_EQUAL:
    case FilterOperator.LESS_THAN:
    case FilterOperator.LESS_THAN_OR_EQUAL:
      if (typeof value !== 'string' && typeof value !== 'number') {
        return {
          valid: false,
          error: `${normalizedOp} requires string or number value`,
        };
      }
      break;

    case FilterOperator.CONTAINS:
    case FilterOperator.STARTS_WITH:
    case FilterOperator.ENDS_WITH:
      if (typeof value !== 'string') {
        return { valid: false, error: `${normalizedOp} requires string value` };
      }
      break;
  }

  return { valid: true };
}

/**
 * Lightweight concurrency semaphore (no external deps)
 * Includes 429-specific backoff with jitter
 */
export class AttioRateLimitSemaphore {
  private running = 0;
  private queue: Array<() => void> = [];

  constructor(
    // Conservative limit to avoid rate limiting (429) errors
    // Attio API typically allows ~10 req/sec, we use 4 concurrent for safety margin
    private maxConcurrent = 4
  ) {}

  async acquire<T>(fn: () => Promise<T>): Promise<T> {
    return new Promise((resolve, reject) => {
      const execute = async () => {
        this.running++;

        try {
          const result = await this.withRetry(fn);
          resolve(result);
        } catch (error) {
          reject(error);
        } finally {
          this.running--;
          this.processQueue();
        }
      };

      if (this.running < this.maxConcurrent) {
        execute();
      } else {
        this.queue.push(execute);
      }
    });
  }

  private async withRetry<T>(fn: () => Promise<T>, attempt = 1): Promise<T> {
    try {
      return await fn();
    } catch (error: any) {
      // 429 backoff with jitter
      if (error?.status === 429 && attempt <= 4) {
        const baseDelay = Math.pow(2, attempt - 1) * 250; // 250ms, 500ms, 1s, 2s
        const jitter = Math.random() * 100; // 0-100ms jitter
        const delay = baseDelay + jitter;

        console.warn(
          `[AttioRateLimitSemaphore] Rate limit hit, retrying in ${Math.round(
            delay
          )}ms (attempt ${attempt})`
        );
        await new Promise((resolve) => setTimeout(resolve, delay));

        return this.withRetry(fn, attempt + 1);
      }

      throw error;
    }
  }

  private processQueue() {
    if (this.queue.length > 0 && this.running < this.maxConcurrent) {
      const next = this.queue.shift();
      if (next) next();
    }
  }
}

// Global semaphore instance
export const apiSemaphore = new AttioRateLimitSemaphore(4);

/**
 * Utilities for handling Attio API responses
 *
 * Handles both Axios (res.data) and fetch/mock response formats
 */

/**
 * Safely unwrap Attio API response envelope
 * Handles both Axios responses and direct API responses
 */
export function unwrapAttio<T>(res: any): T {
  const envelope = res?.data ?? res;
  // If the envelope clearly looks like an error, just return it as-is and let the caller detect it
  if (envelope?.error && !envelope?.data) return envelope as T;
  return (envelope?.data ?? envelope) as T;
}

/**
 * Normalize Attio note to consistent structure
 * Provides compatibility with record-style tests via record_id alias
 */
export function normalizeNote(note: any) {
  // Ensure we always have a base object to work with
  const source = note || {};
  const idObj = source.id || {};

  // Simplify ID extraction
  const rawId =
    source.note_id ||
    source.record_id ||
    idObj.note_id ||
    idObj.record_id ||
    idObj.id ||
    (typeof source.id === 'string' ? source.id : null);

  const normalized = {
    id: {
      note_id: rawId,
      workspace_id: source.workspace_id || idObj.workspace_id || null,
      record_id: rawId, // alias for tests
    },
    parent_object: source.parent_object || null,
    parent_record_id: source.parent_record_id || null,
    title: source.title || null,
    content_markdown: source.content_markdown || null,
    content_plaintext: source.content_plaintext || null,
    content: source.content_markdown || source.content_plaintext || '',
    created_at: source.created_at || null,
    updated_at: source.updated_at || null,
    tags: Array.isArray(source.tags) ? source.tags : [],
    meeting_id: source.meeting_id || null,
    format: source.format || 'markdown',
  };

  return normalized;
}

/**
 * Normalize array of notes
 */
export function normalizeNotes(notes: any[]): any[] {
  if (!Array.isArray(notes)) return [];
  return notes.map(normalizeNote).filter(Boolean);
}

/**
 * Coerce format to Attio-accepted values and preserve content
 */
export function coerceNoteFormat(
  format?: string,
  content?: string
): { format: 'markdown' | 'plaintext'; content: string } {
  // Convert html to markdown to preserve HTML tags, otherwise default to markdown or plaintext
  const attioFormat =
    format === 'html' || format === 'markdown' ? 'markdown' : 'plaintext';

  // Preserve content as-is - tests expect HTML content to be unchanged
  const processedContent = content || '';

  return {
    format: attioFormat,
    content: processedContent,
  };
}

/**
 * Attribute format helpers to convert common user mistakes to correct API formats
 *
 * This module provides automatic format conversion for common attribute mistakes
 * to improve user experience and reduce errors.
 */

/**
 * Converts common attribute format mistakes to correct API format
 *
 * @param resourceType - The type of resource (companies, people, etc.)
 * @param attributes - The attributes object with potential format issues
 * @returns Corrected attributes object
 */
export function convertAttributeFormats(
  resourceType: string,
  attributes: any
): any {
  let corrected = { ...attributes };

  switch (resourceType) {
    case 'companies':
      corrected = convertCompanyAttributes(corrected);
      break;
    case 'people':
      corrected = convertPeopleAttributes(corrected);
      break;
  }

  return corrected;
}

/**
 * Converts company attribute formats
 */
function convertCompanyAttributes(attributes: any): any {
  const corrected = { ...attributes };

  // Convert 'domain' to 'domains' array
  if ('domain' in corrected && !('domains' in corrected)) {
    corrected.domains = Array.isArray(corrected.domain)
      ? corrected.domain
      : [corrected.domain];
    delete corrected.domain;
    console.error(`[Format Helper] Converted 'domain' to 'domains' array`);
  }

  // Ensure domains is always an array
  if (corrected.domains && !Array.isArray(corrected.domains)) {
    corrected.domains = [corrected.domains];
    console.error(`[Format Helper] Converted domains to array format`);
  }

  // Handle common typos
  if ('typpe' in corrected && !('type' in corrected)) {
    corrected.type = corrected.typpe;
    delete corrected.typpe;
    console.error(`[Format Helper] Fixed typo: 'typpe' -> 'type'`);
  }

  return corrected;
}

/**
 * Converts people attribute formats
 */
function convertPeopleAttributes(attributes: any): any {
  const corrected = { ...attributes };

  // Handle name conversion to personal-name array format
  if (
    corrected.name ||
    corrected.first_name ||
    corrected.last_name ||
    corrected.full_name
  ) {
    const nameObj: Record<string, string> = {};

    // Handle string name input (e.g., "Jane Smith")
    if (typeof corrected.name === 'string') {
      const parts = corrected.name.trim().split(' ');
      if (parts.length >= 2) {
        nameObj.first_name = parts[0];
        nameObj.last_name = parts.slice(1).join(' ');
      } else {
        nameObj.first_name = parts[0] || '';
        nameObj.last_name = '';
      }
      nameObj.full_name = corrected.name;
      console.error(
        `[Format Helper] Parsed string name "${corrected.name}" into components`
      );
    }

    // Handle individual name components
    if (corrected.first_name) {
      nameObj.first_name = corrected.first_name;
    }
    if (corrected.last_name) {
      nameObj.last_name = corrected.last_name;
    }
    if (corrected.full_name) {
      nameObj.full_name = corrected.full_name;
    }

    // Ensure full_name is always present for Attio's personal-name validation
    if (!nameObj.full_name) {
      nameObj.full_name = [nameObj.first_name, nameObj.last_name]
        .filter(Boolean)
        .join(' ');
    }

    // Create name field as ARRAY in personal-name format expected by Attio
    corrected.name = [nameObj];

    // Remove the flattened fields since they're now in the name object
    delete corrected.first_name;
    delete corrected.last_name;
    delete corrected.full_name;

    console.error(
      `[Format Helper] Created name ARRAY in personal-name format:`,
      JSON.stringify(corrected.name)
    );
  }

  // Convert email_addresses from object format to string array
  if (corrected.email_addresses && Array.isArray(corrected.email_addresses)) {
    const converted = corrected.email_addresses.map((item: any) => {
      if (typeof item === 'object' && item.email_address) {
        console.error(
          `[Format Helper] Converting email object format to string`
        );
        return item.email_address;
      }
      return item;
    });
    corrected.email_addresses = converted;
  }

  // Ensure email_addresses is always an array
  if (corrected.email_addresses && !Array.isArray(corrected.email_addresses)) {
    corrected.email_addresses = [corrected.email_addresses];
    console.error(`[Format Helper] Converted email_addresses to array format`);
  }

  // Convert phone_numbers from object format to string array
  if (corrected.phone_numbers && Array.isArray(corrected.phone_numbers)) {
    const converted = corrected.phone_numbers.map((item: any) => {
      if (typeof item === 'object' && (item.phone_number || item.number)) {
        console.error(
          `[Format Helper] Converting phone object format to string`
        );
        return item.phone_number || item.number;
      }
      return item;
    });
    corrected.phone_numbers = converted;
  }

  return corrected;
}

/**
 * Validates people attributes before POST to ensure correct Attio format
 * Throws validation errors if required formats are not met
 */
export function validatePeopleAttributesPrePost(attributes: any): void {
  // Validate name format if present
  if (attributes.name) {
    if (!Array.isArray(attributes.name)) {
      throw new Error('People name must be an array of personal-name objects');
    }

    if (attributes.name.length > 0 && !attributes.name[0].full_name) {
      throw new Error('People name[0].full_name must be a non-empty string');
    }

    if (
      attributes.name.length > 0 &&
      typeof attributes.name[0].full_name !== 'string'
    ) {
      throw new Error('People name[0].full_name must be a string');
    }

    if (
      attributes.name.length > 0 &&
      attributes.name[0].full_name.trim() === ''
    ) {
      throw new Error('People name[0].full_name must be a non-empty string');
    }
  }

  // Validate email_addresses format if present
  if (attributes.email_addresses) {
    if (!Array.isArray(attributes.email_addresses)) {
      throw new Error('People email_addresses must be an array of strings');
    }

    for (const email of attributes.email_addresses) {
      if (typeof email !== 'string') {
        throw new Error('All email_addresses must be strings, not objects');
      }
    }
  }
}

