/**
 * Core attribute mapping functions for translating human-readable attribute names to API slugs
 */
import { loadMappingConfig, MappingConfig } from '../config-loader.js';
import { LEGACY_ATTRIBUTE_MAP } from './legacy-maps.js';
import {
  createCaseInsensitiveMap,
  lookupCaseInsensitive,
  lookupNormalized,
  createNormalizedMap,
  createAggressiveNormalizedMap,
  lookupAggressiveNormalized,
  handleSpecialCases,
} from './mapping-utils.js';

/**
 * Converts a value to a boolean based on common string representations
 *
 * @param value - The value to convert to boolean
 * @returns Boolean representation of the value
 */
export function convertToBoolean(value: any): boolean {
  if (typeof value === 'boolean') return value;
  if (typeof value === 'string') {
    const lowerValue = value.toLowerCase();
    if (['true', 'yes', 'y', '1'].includes(lowerValue)) return true;
    if (['false', 'no', 'n', '0'].includes(lowerValue)) return false;
  }
  if (typeof value === 'number') return !isNaN(value) && value !== 0;

  // If we can't determine, return the original value as boolean
  return Boolean(value);
}

// Error class for attribute mapping errors
export class AttributeMappingError extends Error {
  constructor(
    message: string,
    public details: Record<string, unknown> = {}
  ) {
    super(message);
    this.name = 'AttributeMappingError';
  }
}

// Cache the config to avoid repeatedly loading from disk
let cachedConfig: MappingConfig | null = null;

// Cache for case-insensitive lookups
const caseInsensitiveCaches: Record<
  string,
  Map<string, { original: string; value: string }>
> = {};

// Cache for normalized lookups (spaces removed, case insensitive)
const normalizedCaches: Record<
  string,
  Map<string, { original: string; value: string }>
> = {};

/**
 * Initialize lookup caches for faster mapping
 *
 * @param config The configuration to create caches for
 */
function initializeLookupCaches(config: MappingConfig): void {
  // Create case-insensitive lookup maps
  caseInsensitiveCaches.common = createCaseInsensitiveMap(
    config.mappings.attributes.common
  );
  caseInsensitiveCaches.custom = createCaseInsensitiveMap(
    config.mappings.attributes.custom
  );
  caseInsensitiveCaches.objects = createCaseInsensitiveMap(
    config.mappings.objects
  );
  caseInsensitiveCaches.lists = createCaseInsensitiveMap(config.mappings.lists);
  caseInsensitiveCaches.legacy = createCaseInsensitiveMap(LEGACY_ATTRIBUTE_MAP);

  // Create normalized lookup maps for fuzzy matching
  normalizedCaches.legacy = createNormalizedMap(LEGACY_ATTRIBUTE_MAP);

  // Create maps for object-specific attributes
  for (const [objectType, mappings] of Object.entries(
    config.mappings.attributes.objects
  )) {
    caseInsensitiveCaches[`objects.${objectType}`] =
      createCaseInsensitiveMap(mappings);
  }
}

/**
 * Gets the mapping configuration, loading it from disk if necessary
 */
function getConfig(): MappingConfig {
  // Always reload the config in test environment to get fresh mocks
  if (!cachedConfig || process.env.NODE_ENV === 'test') {
    try {
      cachedConfig = loadMappingConfig();
      // Initialize lookup caches for faster access
      initializeLookupCaches(cachedConfig);
    } catch (error: unknown) {
      console.error('Failed to load mapping configuration:', error);

      // Create a simple config using the legacy map for backward compatibility
      cachedConfig = {
        version: '1.0',
        mappings: {
          attributes: {
            common: { ...LEGACY_ATTRIBUTE_MAP },
            objects: {},
            custom: {},
          },
          objects: {},
          lists: {},
          relationships: {},
        },
      };

      // Initialize with fallback configuration
      initializeLookupCaches(cachedConfig);
    }
  }
  return cachedConfig;
}

/**
 * Invalidates the configuration cache, forcing a reload on next access
 * This is useful for testing and when configuration files change
 */
export function invalidateConfigCache(): void {
  cachedConfig = null;

  // Reset all lookup caches
  Object.keys(caseInsensitiveCaches).forEach((key) => {
    delete caseInsensitiveCaches[key];
  });

  Object.keys(normalizedCaches).forEach((key) => {
    delete normalizedCaches[key];
  });
}

/**
 * Attempts snake case conversion and lookup for attribute mapping.
 * This function safely converts snake_case attributes to Display Case format
 * and looks them up in the mapping caches without causing infinite recursion.
 *
 * @param attributeName - The snake_case attribute name to convert and lookup
 * @returns The mapped slug if found, or undefined if no mapping exists
 *
 * @example
 * ```typescript
 * // For input "b2b_segment", converts to "B2b Segment" and looks up mapping
 * const result = trySnakeCaseConversion("b2b_segment"); // Returns "type_persona"
 * ```
 */
function trySnakeCaseConversion(attributeName: string): string | undefined {
  // Guard conditions to prevent infinite recursion
  // Skip if name already contains spaces or has no underscores to convert
  if (attributeName.includes(' ') || !attributeName.includes('_')) {
    return undefined;
  }

  try {
    // Convert snake_case to Display Case (e.g., "b2b_segment" -> "B2b Segment")
    const potentialDisplayName = attributeName
      .replace(/_/g, ' ')
      .replace(/(\w)(\w*)/g, (_, first, rest) => first.toUpperCase() + rest);

    // Additional safety check: if conversion results in same string, avoid lookup
    if (potentialDisplayName === attributeName) {
      return undefined;
    }

    // Use direct cache lookups to avoid recursive getAttributeSlug calls
    // This prevents infinite recursion while maintaining mapping functionality

    // Try special cases first (highest priority)
    let result = handleSpecialCases(potentialDisplayName);
    if (result) return result;

    // Try cache lookups in order of priority
    if (caseInsensitiveCaches.common) {
      result = lookupCaseInsensitive(
        caseInsensitiveCaches.common,
        potentialDisplayName
      );
      if (result) return result;
    }

    if (caseInsensitiveCaches.custom) {
      result = lookupCaseInsensitive(
        caseInsensitiveCaches.custom,
        potentialDisplayName
      );
      if (result) return result;
    }

    if (caseInsensitiveCaches.legacy) {
      result = lookupCaseInsensitive(
        caseInsensitiveCaches.legacy,
        potentialDisplayName
      );
      if (result) return result;
    }

    return undefined;
  } catch (err) {
    // Graceful error handling: log warning but don't throw
    console.warn(
      `[attribute-mappers] Error in snake case conversion for "${attributeName}": ${err}`
    );
    return undefined;
  }
}

/**
 * Looks up a human-readable attribute name and returns the corresponding slug
 *
 * @param attributeName - The user-provided attribute name
 * @param objectType - Optional object type for object-specific mappings
 * @returns The slug if found, or the original attributeName if not mapped
 */
export function getAttributeSlug(
  attributeName: string,
  objectType?: string
): string {
  if (!attributeName) return attributeName;

  try {
    // First check for special cases that commonly need to be handled
    const specialCaseResult = handleSpecialCases(attributeName);
    if (specialCaseResult) {
      if (process.env.NODE_ENV === 'development') {
        console.error(
          `[attribute-mappers] Special case match: "${attributeName}" -> "${specialCaseResult}"`
        );
      }
      return specialCaseResult;
    }

    // Make sure config is loaded (in case we haven't initialized caches yet)
    const config = getConfig();

    // Ensure at least the basic lookup caches exist (this can happen if called before initialization)
    if (
      !caseInsensitiveCaches.common ||
      !caseInsensitiveCaches.custom ||
      !caseInsensitiveCaches.legacy
    ) {
      // Initialize lookup caches if missing
      initializeLookupCaches(config);
    }

    // Create aggressive normalized caches if they don't exist
    if (!normalizedCaches.aggressiveLegacy) {
      normalizedCaches.aggressiveLegacy =
        createAggressiveNormalizedMap(LEGACY_ATTRIBUTE_MAP);
    }

    let result: string | undefined;

    // TIER 1: Check object-specific mappings if objectType is provided (exact matches)
    if (objectType) {
      const cacheKey = `objects.${objectType}`;
      // Make sure this object-specific cache exists
      if (
        !caseInsensitiveCaches[cacheKey] &&
        config.mappings.attributes.objects[objectType]
      ) {
        caseInsensitiveCaches[cacheKey] = createCaseInsensitiveMap(
          config.mappings.attributes.objects[objectType]
        );
      }

      const objectSpecificCache = caseInsensitiveCaches[cacheKey];
      if (objectSpecificCache) {
        result = lookupCaseInsensitive(objectSpecificCache, attributeName);
        if (result) {
          if (process.env.NODE_ENV === 'development') {
            console.error(
              `[attribute-mappers] Object-specific case-insensitive match for ${objectType}: "${attributeName}" -> "${result}"`
            );
          }
          return result;
        }
      }
    }

    // TIER 2: Check custom and common mappings with case-insensitive lookup
    result = lookupCaseInsensitive(caseInsensitiveCaches.custom, attributeName);
    if (result) {
      if (process.env.NODE_ENV === 'development') {
        console.error(
          `[attribute-mappers] Custom case-insensitive match: "${attributeName}" -> "${result}"`
        );
      }
      return result;
    }

    result = lookupCaseInsensitive(caseInsensitiveCaches.common, attributeName);
    if (result) {
      if (process.env.NODE_ENV === 'development') {
        console.error(
          `[attribute-mappers] Common case-insensitive match: "${attributeName}" -> "${result}"`
        );
      }
      return result;
    }

    // TIER 3: Legacy mapping with case-insensitive lookup
    result = lookupCaseInsensitive(caseInsensitiveCaches.legacy, attributeName);
    if (result) {
      if (process.env.NODE_ENV === 'development') {
        console.error(
          `[attribute-mappers] Legacy case-insensitive match: "${attributeName}" -> "${result}"`
        );
      }
      return result;
    }

    // TIER 4: Try normalized lookup (removes spaces, case-insensitive)
    // Create normalized caches for object-specific mappings if they don't exist
    if (objectType) {
      const normalizedCacheKey = `normalized.objects.${objectType}`;
      if (
        !normalizedCaches[normalizedCacheKey] &&
        config.mappings.attributes.objects[objectType]
      ) {
        normalizedCaches[normalizedCacheKey] = createNormalizedMap(
          config.mappings.attributes.objects[objectType]
        );
      }

      const normalizedObjectSpecificCache =
        normalizedCaches[normalizedCacheKey];
      if (normalizedObjectSpecificCache) {
        result = lookupNormalized(normalizedObjectSpecificCache, attributeName);
        if (result) {
          if (process.env.NODE_ENV === 'development') {
            console.error(
              `[attribute-mappers] Object-specific normalized match for ${objectType}: "${attributeName}" -> "${result}"`
            );
          }
          return result;
        }
      }
    }

    // Check common and custom normalized caches
    if (!normalizedCaches.common) {
      normalizedCaches.common = createNormalizedMap(
        config.mappings.attributes.common
      );
    }

    if (!normalizedCaches.custom) {
      normalizedCaches.custom = createNormalizedMap(
        config.mappings.attributes.custom
      );
    }

    result = lookupNormalized(normalizedCaches.custom, attributeName);
    if (result) {
      if (process.env.NODE_ENV === 'development') {
        console.error(
          `[attribute-mappers] Custom normalized match: "${attributeName}" -> "${result}"`
        );
      }
      return result;
    }

    result = lookupNormalized(normalizedCaches.common, attributeName);
    if (result) {
      if (process.env.NODE_ENV === 'development') {
        console.error(
          `[attribute-mappers] Common normalized match: "${attributeName}" -> "${result}"`
        );
      }
      return result;
    }

    // Check legacy normalized
    if (!normalizedCaches.legacy) {
      normalizedCaches.legacy = createNormalizedMap(LEGACY_ATTRIBUTE_MAP);
    }

    result = lookupNormalized(normalizedCaches.legacy, attributeName);
    if (result) {
      if (process.env.NODE_ENV === 'development') {
        console.error(
          `[attribute-mappers] Legacy normalized match: "${attributeName}" -> "${result}"`
        );
      }
      return result;
    }

    // TIER 5: Aggressive normalization (removes all non-alphanumeric characters)
    result = lookupAggressiveNormalized(
      normalizedCaches.aggressiveLegacy,
      attributeName
    );
    if (result) {
      if (process.env.NODE_ENV === 'development') {
        console.error(
          `[attribute-mappers] Aggressive normalized match: "${attributeName}" -> "${result}"`
        );
      }
      return result;
    }

    // TIER 6: Snake case conversion fallback (last resort)
    result = trySnakeCaseConversion(attributeName);
    if (result && result !== attributeName) {
      if (process.env.NODE_ENV === 'development') {
        console.error(
          `[attribute-mappers] Snake case conversion match: "${attributeName}" -> "${result}"`
        );
      }
      return result;
    }
  } catch (error: unknown) {
    // If there's an error with the config, log detailed error and suggestions
    const errorMsg =
      error instanceof AttributeMappingError
        ? `${error.message} - ${JSON.stringify(error.details)}`
        : `Error using config for attribute mapping: ${error}`;

    console.error(errorMsg);
    console.warn(
      'Falling back to legacy behavior. Check your configuration files for errors.'
    );

    // Try special cases as a last resort, even if there was an error earlier
    const specialCaseResult = handleSpecialCases(attributeName);
    if (specialCaseResult) {
      console.error(
        `[attribute-mappers] Special case match after error: "${attributeName}" -> "${specialCaseResult}"`
      );
      return specialCaseResult;
    }
  }

  // Log that no match was found for easier debugging
  if (process.env.NODE_ENV === 'development') {
    console.warn(
      `[attribute-mappers] No mapping found for attribute: "${attributeName}"`
    );
  }

  // If no match found, return the original
  return attributeName;
}

/**
 * Gets the slug for an object type (e.g., "Companies" -> "companies")
 *
 * @param objectName - The human-readable object name
 * @returns The corresponding slug, or a normalized version of the original name if not found
 */
export function getObjectSlug(objectName: string): string {
  if (!objectName) return objectName;

  try {
    // Make sure config is loaded (in case we haven't initialized caches yet)
    const config = getConfig();

    // Ensure the lookup caches exist (this can happen if called before initialization)
    if (!caseInsensitiveCaches.objects) {
      // Initialize lookup caches if missing
      initializeLookupCaches(config);
    }

    // Use case-insensitive lookup
    const result = lookupCaseInsensitive(
      caseInsensitiveCaches.objects,
      objectName
    );
    if (result) return result;
  } catch (error: unknown) {
    // If there's an error with the config, fall back to simple normalization
    console.error('Error using config for object mapping:', error);
    console.warn(
      'Check your configuration files for errors in the objects section.'
    );
  }

  // If no match is found, convert to lowercase and remove spaces as a fallback
  return objectName.toLowerCase().replace(/\s+/g, '_');
}

/**
 * Gets the slug for a list name
 *
 * @param listName - The human-readable list name
 * @returns The corresponding slug, or the original name if not found
 */
export function getListSlug(listName: string): string {
  if (!listName) return listName;

  try {
    // Make sure config is loaded (in case we haven't initialized caches yet)
    const config = getConfig();

    // Ensure the lookup caches exist (this can happen if called before initialization)
    if (!caseInsensitiveCaches.lists) {
      // Initialize lookup caches if missing
      initializeLookupCaches(config);
    }

    // Use case-insensitive lookup
    const result = lookupCaseInsensitive(caseInsensitiveCaches.lists, listName);
    if (result) return result;
  } catch (error: unknown) {
    // If there's an error with the config, fall back to simple normalization
    console.error('Error using config for list mapping:', error);
    console.warn(
      'Check your configuration files for errors in the lists section.'
    );
  }

  // If no match is found, return the original
  return listName;
}

/**
 * Filter translation functionality for converting attribute names in filters
 */
import { getAttributeSlug } from './attribute-mappers.js';

/**
 * Processes a filter structure to translate any human-readable attribute names to their slug equivalents
 *
 * @param filters - The filters object possibly containing human-readable attribute names
 * @param objectType - Optional object type for more specific attribute mapping
 * @returns A new filters object with attribute names translated to slugs where applicable
 */
export function translateAttributeNamesInFilters(
  filters: any,
  objectType?: string
): any {
  // Handle null, undefined, or non-object filters
  if (!filters || typeof filters !== 'object') {
    return filters;
  }

  // Handle array of filters
  if (Array.isArray(filters)) {
    return filters.map((filter) =>
      translateAttributeNamesInFilters(filter, objectType)
    );
  }

  // Deep clone the filters object to avoid modifying the original
  const translatedFilters = { ...filters };

  // Handle direct filter objects with attribute.slug
  if (isDirectFilterObject(translatedFilters)) {
    return translateDirectFilter(translatedFilters, objectType);
  }

  // Handle nested filter structures
  if (hasNestedFilters(translatedFilters)) {
    translatedFilters.filters = translateNestedFilters(
      translatedFilters.filters,
      objectType
    );
  }

  // Process all other properties recursively
  return translateRemainingProperties(translatedFilters, objectType);
}

/**
 * Checks if an object is a direct filter with attribute.slug
 */
function isDirectFilterObject(filter: any): boolean {
  return filter.attribute && filter.attribute.slug;
}

/**
 * Translates a direct filter object
 */
function translateDirectFilter(filter: any, objectType?: string): any {
  // Determine the object type to use for translation
  // Priority: filter's own objectType > parent objectType
  const typeToUse = filter.objectType || objectType;

  // Create a new object with translated slug
  return {
    ...filter,
    attribute: {
      ...filter.attribute,
      slug: getAttributeSlug(filter.attribute.slug, typeToUse),
    },
  };
}

/**
 * Checks if an object has nested filters
 */
function hasNestedFilters(filter: any): boolean {
  return filter.filters && Array.isArray(filter.filters);
}

/**
 * Translates nested filters
 */
function translateNestedFilters(
  filters: unknown[],
  objectType?: string
): unknown[] {
  return filters.map((filter: any) => {
    if (isDirectFilterObject(filter)) {
      // Determine the object type to use for this specific filter
      const typeToUse = filter.objectType || objectType;

      // Translate the attribute slug if it's a human-readable name
      return translateDirectFilter(filter, typeToUse);
    }
    return translateAttributeNamesInFilters(filter, objectType);
  });
}

/**
 * Translates remaining properties in the filter object
 */
function translateRemainingProperties(filter: any, objectType?: string): any {
  for (const key of Object.keys(filter)) {
    if (typeof filter[key] === 'object' && filter[key] !== null) {
      // Special handling for object-specific sections
      const typeToUse = determineObjectTypeContext(key, filter, objectType);

      filter[key] = translateAttributeNamesInFilters(filter[key], typeToUse);
    }
  }

  return filter;
}

/**
 * Determines the object type context for a property
 */
function determineObjectTypeContext(
  key: string,
  filter: any,
  parentObjectType?: string
): string | undefined {
  // If this key is an object type (companies, people), use it as the type context
  if (key === 'companies' || key === 'people') {
    return key;
  }

  // If this is a resource-specific section with explicit object type, use that
  if (filter.objectType) {
    return filter.objectType;
  }

  // Otherwise use the parent context
  return parentObjectType;
}

/**
 * Auto-discovery module for automatic attribute mapping updates
 */
import {
  getObjectAttributes,
  getAvailableObjects,
} from '../cli/commands/attributes.js';
import {
  loadMappingConfig,
  writeMappingConfig,
  MappingConfig,
} from './config-loader.js';

// Simple logging for auto-discovery - disabled for MCP protocol compatibility
const log = {
  info: (_message: string, _data?: any) => {
    /* Silent for MCP protocol compatibility */
  },
  warn: (_message: string, _data?: any) => {
    /* Silent for MCP protocol compatibility */
  },
  error: (_message: string, _error?: any) => {
    /* Silent for MCP protocol compatibility */
  },
};

/**
 * Configuration for auto-discovery
 */
export interface AutoDiscoveryConfig {
  enabled: boolean;
  runOnStartup: boolean;
  intervalMinutes?: number;
  outputPath?: string;
}

/**
 * Default configuration for auto-discovery
 */
const DEFAULT_CONFIG: AutoDiscoveryConfig = {
  enabled: true,
  runOnStartup: true,
  intervalMinutes: 60, // Run every hour by default
  outputPath: 'configs/runtime/mappings/user.json',
};

let discoveryInterval: NodeJS.Timeout | null = null;

/**
 * Run attribute discovery for all objects
 */
export async function runDiscovery(
  apiKey: string,
  outputPath?: string
): Promise<void> {
  log.info('Starting automatic attribute discovery...');

  try {
    // Load existing config or create new one
    let config: MappingConfig;
    try {
      config = loadMappingConfig();
    } catch (error: unknown) {
      log.warn('Failed to load existing configuration, creating new one...');
      config = {
        version: '1.0',
        metadata: {
          generated: new Date().toISOString(),
          description: 'Generated by automatic discovery',
        },
        mappings: {
          attributes: {
            common: {},
            objects: {},
            custom: {},
          },
          objects: {},
          lists: {},
          relationships: {},
        },
      };
    }

    // Get all available objects
    const objects = await getAvailableObjects(apiKey);
    log.info(`Found ${objects.length} objects in Attio workspace`);

    // Process each object
    for (const objectSlug of objects) {
      try {
        const attributeMappings = await getObjectAttributes(objectSlug, apiKey);
        const attributeCount = Object.keys(attributeMappings).length;

        if (attributeCount > 0) {
          if (!config.mappings.attributes.objects[objectSlug]) {
            config.mappings.attributes.objects[objectSlug] = {};
          }

          // Merge with existing mappings
          config.mappings.attributes.objects[objectSlug] = {
            ...config.mappings.attributes.objects[objectSlug],
            ...attributeMappings,
          };

          log.info(`Discovered ${attributeCount} attributes for ${objectSlug}`);
        }
      } catch (error: unknown) {
        log.error(`Error discovering attributes for ${objectSlug}:`, error);
      }
    }

    // Update metadata
    config.metadata = {
      ...config.metadata,
      generated: new Date().toISOString(),
      lastDiscovery: new Date().toISOString(),
      autoDiscovery: true,
    };

    // Write the updated config
    await writeMappingConfig(config, outputPath || DEFAULT_CONFIG.outputPath);
    log.info('Automatic attribute discovery completed successfully');
  } catch (error: unknown) {
    log.error('Failed to complete automatic discovery:', error);
    throw error;
  }
}

/**
 * Start automatic discovery with optional periodic updates
 */
export async function startAutoDiscovery(
  apiKey: string,
  config: Partial<AutoDiscoveryConfig> = {}
): Promise<void> {
  const settings = { ...DEFAULT_CONFIG, ...config };

  if (!settings.enabled) {
    log.info('Auto-discovery is disabled');
    return;
  }

  // Run on startup if configured
  if (settings.runOnStartup) {
    try {
      await runDiscovery(apiKey, settings.outputPath);
    } catch (error: unknown) {
      log.error('Failed to run discovery on startup:', error);
      // Don't fail the server startup, just log the error
    }
  }

  // Set up periodic discovery if configured
  if (settings.intervalMinutes && settings.intervalMinutes > 0) {
    const intervalMs = settings.intervalMinutes * 60 * 1000;

    discoveryInterval = setInterval(async () => {
      log.info('Running scheduled attribute discovery...');
      try {
        await runDiscovery(apiKey, settings.outputPath);
      } catch (error: unknown) {
        log.error('Failed to run scheduled discovery:', error);
      }
    }, intervalMs);

    log.info(
      `Scheduled attribute discovery every ${settings.intervalMinutes} minutes`
    );
  }
}

/**
 * Stop automatic discovery
 */
export function stopAutoDiscovery(): void {
  if (discoveryInterval) {
    clearInterval(discoveryInterval);
    discoveryInterval = null;
    log.info('Stopped automatic attribute discovery');
  }
}

/**
 * Batch operation validation utilities for DoS protection
 *
 * Provides comprehensive validation for batch operations including
 * size limits, payload validation, and rate limiting checks.
 */

import {
  BATCH_SIZE_LIMITS,
  PAYLOAD_SIZE_LIMITS,
  LIMIT_ERROR_MESSAGES,
  getBatchSizeLimit,
} from '../config/security-limits.js';
import { ErrorType } from './error-handler.js';

/**
 * Validation result for batch operations
 */
export interface BatchValidationResult {
  isValid: boolean;
  error?: string;
  errorType?: ErrorType;
  details?: {
    actualSize?: number;
    maxSize?: number;
    payloadSize?: number;
    maxPayloadSize?: number;
  };
}

/**
 * Calculate the approximate size of a JavaScript object in bytes
 * This is used to estimate payload sizes for validation
 */
function getObjectSize(obj: any): number {
  let size = 0;

  if (obj === null || obj === undefined) {
    return 0;
  }

  if (typeof obj === 'string') {
    return obj.length * 2; // Unicode characters can be up to 2 bytes
  }

  if (typeof obj === 'number') {
    return 8; // Numbers are typically 8 bytes
  }

  if (typeof obj === 'boolean') {
    return 4; // Booleans are typically 4 bytes
  }

  if (obj instanceof Date) {
    return 8; // Dates are stored as numbers
  }

  if (Array.isArray(obj)) {
    for (const item of obj) {
      size += getObjectSize(item);
    }
    return size;
  }

  if (typeof obj === 'object') {
    for (const [key, value] of Object.entries(obj)) {
      size += key.length * 2; // Key size
      size += getObjectSize(value); // Value size
    }
    return size;
  }

  return 0;
}

/**
 * Validates the size of a batch operation
 *
 * @param items - Array of items in the batch
 * @param operationType - Type of operation (create, update, delete, etc.)
 * @param resourceType - Type of resource (companies, people, etc.)
 * @returns Validation result
 */
export function validateBatchSize(
  items: any[] | undefined | null,
  operationType: string,
  resourceType?: string
): BatchValidationResult {
  // Check if items is a valid array
  if (!items || !Array.isArray(items)) {
    return {
      isValid: false,
      error: 'Batch items must be a non-empty array',
      errorType: ErrorType.VALIDATION_ERROR,
    };
  }

  // Check for empty array
  if (items.length === 0) {
    return {
      isValid: false,
      error: 'Batch operation requires at least one item',
      errorType: ErrorType.VALIDATION_ERROR,
    };
  }

  // Get the appropriate size limit
  let maxSize = getBatchSizeLimit(resourceType);

  // Apply more restrictive limits for certain operations
  if (operationType.toLowerCase() === 'delete') {
    maxSize = Math.min(maxSize, BATCH_SIZE_LIMITS.DELETE);
  } else if (operationType.toLowerCase() === 'search') {
    maxSize = Math.min(maxSize, BATCH_SIZE_LIMITS.SEARCH);
  }

  // Check if batch size exceeds limit
  if (items.length > maxSize) {
    return {
      isValid: false,
      error: LIMIT_ERROR_MESSAGES.BATCH_SIZE_EXCEEDED(
        items.length,
        maxSize,
        `${operationType} ${resourceType || ''}`.trim()
      ),
      errorType: ErrorType.VALIDATION_ERROR,
      details: {
        actualSize: items.length,
        maxSize,
      },
    };
  }

  return { isValid: true };
}

/**
 * Validates the payload size of a batch operation
 *
 * @param payload - The payload to validate
 * @param checkSingleRecords - Whether to check individual record sizes
 * @returns Validation result
 */
export function validatePayloadSize(
  payload: any,
  checkSingleRecords: boolean = true
): BatchValidationResult {
  // Calculate total payload size
  const totalSize = getObjectSize(payload);

  // Check total payload size
  if (totalSize > PAYLOAD_SIZE_LIMITS.BATCH_TOTAL) {
    return {
      isValid: false,
      error: LIMIT_ERROR_MESSAGES.PAYLOAD_SIZE_EXCEEDED(
        totalSize,
        PAYLOAD_SIZE_LIMITS.BATCH_TOTAL
      ),
      errorType: ErrorType.VALIDATION_ERROR,
      details: {
        payloadSize: totalSize,
        maxPayloadSize: PAYLOAD_SIZE_LIMITS.BATCH_TOTAL,
      },
    };
  }

  // Check individual record sizes if requested
  if (checkSingleRecords && Array.isArray(payload)) {
    for (let i = 0; i < payload.length; i++) {
      const recordSize = getObjectSize(payload[i]);
      if (recordSize > PAYLOAD_SIZE_LIMITS.SINGLE_RECORD) {
        return {
          isValid: false,
          error:
            `Record at index ${i}: ` +
            LIMIT_ERROR_MESSAGES.SINGLE_RECORD_SIZE_EXCEEDED(
              recordSize,
              PAYLOAD_SIZE_LIMITS.SINGLE_RECORD
            ),
          errorType: ErrorType.VALIDATION_ERROR,
          details: {
            payloadSize: recordSize,
            maxPayloadSize: PAYLOAD_SIZE_LIMITS.SINGLE_RECORD,
          },
        };
      }
    }
  }

  return { isValid: true };
}

/**
 * Validates search query parameters
 *
 * @param query - Search query string
 * @param filters - Optional filter object
 * @returns Validation result
 */
export function validateSearchQuery(
  query?: string,
  filters?: any
): BatchValidationResult {
  // Validate query string length
  if (query && query.length > PAYLOAD_SIZE_LIMITS.SEARCH_QUERY) {
    return {
      isValid: false,
      error: LIMIT_ERROR_MESSAGES.SEARCH_QUERY_TOO_LONG(
        query.length,
        PAYLOAD_SIZE_LIMITS.SEARCH_QUERY
      ),
      errorType: ErrorType.VALIDATION_ERROR,
    };
  }

  // Validate filter object size
  if (filters) {
    const filterSize = getObjectSize(filters);
    if (filterSize > PAYLOAD_SIZE_LIMITS.FILTER_OBJECT) {
      return {
        isValid: false,
        error: LIMIT_ERROR_MESSAGES.FILTER_TOO_COMPLEX(
          filterSize,
          PAYLOAD_SIZE_LIMITS.FILTER_OBJECT
        ),
        errorType: ErrorType.VALIDATION_ERROR,
      };
    }
  }

  return { isValid: true };
}

/**
 * Comprehensive validation for batch operations
 * Combines size, payload, and other validations
 *
 * @param params - Parameters object containing batch operation details
 * @returns Validation result
 */
export function validateBatchOperation(params: {
  items?: any[];
  operationType: string;
  resourceType?: string;
  checkPayload?: boolean;
}): BatchValidationResult {
  const { items, operationType, resourceType, checkPayload = true } = params;

  // Validate batch size
  const sizeValidation = validateBatchSize(items, operationType, resourceType);
  if (!sizeValidation.isValid) {
    return sizeValidation;
  }

  // Validate payload size if requested
  if (checkPayload && items) {
    const payloadValidation = validatePayloadSize(items);
    if (!payloadValidation.isValid) {
      return payloadValidation;
    }
  }

  return { isValid: true };
}

/**
 * Splits a large batch into smaller chunks that respect size limits
 *
 * @param items - Array of items to split
 * @param resourceType - Type of resource for determining limits
 * @returns Array of batches
 */
export function splitBatchIntoChunks<T>(
  items: T[],
  resourceType?: string
): T[][] {
  if (!items || items.length === 0) {
    return [];
  }

  const maxSize = getBatchSizeLimit(resourceType);
  const chunks: T[][] = [];

  for (let i = 0; i < items.length; i += maxSize) {
    chunks.push(items.slice(i, i + maxSize));
  }

  return chunks;
}

/**
 * Creates a safe error message for batch validation failures
 * This ensures no sensitive information is exposed in error messages
 *
 * @param validation - Validation result
 * @returns Safe error message
 */
export function createSafeBatchError(
  validation: BatchValidationResult
): string {
  if (validation.isValid) {
    return '';
  }

  // Return the error message without exposing internal limits
  // The error messages already handle this safely
  return validation.error || 'Batch validation failed';
}

/**
 * Configuration loader for Attio MCP Server
 * Handles loading and merging of configuration files
 */
import * as fs from 'fs';
import * as path from 'path';

/**
 * Interface for mapping configuration
 */
export interface MappingConfig {
  version: string;
  metadata?: Record<string, unknown>;
  mappings: {
    attributes: {
      common: Record<string, string>;
      objects: Record<string, Record<string, string>>;
      custom: Record<string, string>;
    };
    objects: Record<string, string>;
    lists: Record<string, string>;
    relationships: Record<string, string>;
    [key: string]: any; // Allow other mapping types
  };
}

/**
 * Default paths for configuration files
 */
const CONFIG_PATHS = {
  default: path.resolve(process.cwd(), 'configs/runtime/mappings/default.json'),
  user: path.resolve(process.cwd(), 'configs/runtime/mappings/user.json'),
};

/**
 * Deep merges two objects, with values from the source object taking precedence
 *
 * @param target - The target object
 * @param source - The source object to merge in
 * @returns The merged object
 */
function deepMerge(target: any, source: any): any {
  const result = { ...target };

  for (const key in source) {
    if (Object.prototype.hasOwnProperty.call(source, key)) {
      if (
        source[key] &&
        typeof source[key] === 'object' &&
        !Array.isArray(source[key])
      ) {
        // If both target and source have an object at this key, merge them
        if (
          result[key] &&
          typeof result[key] === 'object' &&
          !Array.isArray(result[key])
        ) {
          result[key] = deepMerge(result[key], source[key]);
        } else {
          // Otherwise, just use the source value
          result[key] = { ...source[key] };
        }
      } else {
        // For non-objects, use the source value
        result[key] = source[key];
      }
    }
  }

  return result;
}

/**
 * Creates an empty mapping configuration with default structure
 *
 * @returns An empty mapping configuration
 */
function createEmptyConfig(): MappingConfig {
  return {
    version: '1.0',
    metadata: {
      generated: new Date().toISOString(),
      description: 'Generated empty configuration',
    },
    mappings: {
      attributes: {
        common: {},
        objects: {},
        custom: {},
      },
      objects: {},
      lists: {},
      relationships: {},
    },
  };
}

/**
 * Loads a JSON configuration file
 *
 * @param filePath - Path to the JSON file
 * @returns Parsed JSON object, or null if the file doesn't exist
 */
function loadJsonFile(filePath: string): any {
  try {
    if (fs.existsSync(filePath)) {
      const content = fs.readFileSync(filePath, 'utf8');
      return JSON.parse(content);
    }
  } catch (error: unknown) {
    console.warn(`Warning: Failed to load config file ${filePath}:`, error);
  }
  return null;
}

/**
 * Loads and merges the mapping configuration from default and user files
 *
 * @returns The merged mapping configuration
 */
export function loadMappingConfig(): MappingConfig {
  // Start with empty configuration
  let config = createEmptyConfig();

  // Load and merge the default configuration
  const defaultConfig = loadJsonFile(CONFIG_PATHS.default);
  if (defaultConfig) {
    config = deepMerge(config, defaultConfig);
  }

  // Load and merge the user configuration
  const userConfig = loadJsonFile(CONFIG_PATHS.user);
  if (userConfig) {
    config = deepMerge(config, userConfig);
  }

  return config;
}

/**
 * Writes a mapping configuration to a file
 *
 * @param config - The configuration to write
 * @param filePath - The file path to write to (defaults to user.json)
 */
export async function writeMappingConfig(
  config: MappingConfig,
  filePath: string = CONFIG_PATHS.user
): Promise<void> {
  try {
    // Ensure the directory exists
    const dir = path.dirname(filePath);
    if (!fs.existsSync(dir)) {
      fs.mkdirSync(dir, { recursive: true });
    }

    // Update metadata
    if (!config.metadata) {
      config.metadata = {};
    }
    config.metadata.generated = new Date().toISOString();

    // Write the file
    const content = JSON.stringify(config, null, 2);
    await fs.promises.writeFile(filePath, content, 'utf8');
  } catch (error: unknown) {
    throw new Error(`Failed to write config file ${filePath}: ${error}`);
  }
}

/**
 * Updates a specific section of the mapping configuration
 *
 * @param section - The section to update (e.g., 'attributes.common')
 * @param mappings - The mappings to set or merge
 * @param merge - Whether to merge with existing mappings (default: true)
 * @param filePath - The file path to write to (defaults to user.json)
 */
export async function updateMappingSection(
  section: string,
  mappings: Record<string, unknown>,
  merge: boolean = true,
  filePath: string = CONFIG_PATHS.user
): Promise<void> {
  // Load the current config
  const config = loadMappingConfig();

  // Parse the section path and navigate to the target section
  const sectionParts = section.split('.');
  let target = config.mappings;

  for (let i = 0; i < sectionParts.length - 1; i++) {
    const part = sectionParts[i];
    if (!target[part]) {
      target[part] = {};
    }
    target = target[part];
  }

  const finalPart = sectionParts[sectionParts.length - 1];

  // Update the target section
  if (merge && target[finalPart]) {
    target[finalPart] = { ...target[finalPart], ...mappings };
  } else {
    target[finalPart] = mappings;
  }

  // Write the updated config
  await writeMappingConfig(config, filePath);
}

