/**
 * Utility functions for domain extraction and validation
 */

/**
 * Extracts domain from various input formats
 *
 * @param input - Input string that may contain a domain, URL, or email
 * @returns Extracted domain or null if no valid domain found
 * @example
 * ```typescript
 * extractDomain("https://example.com/path") // Returns "example.com"
 * extractDomain("john@example.com") // Returns "example.com"
 * extractDomain("example.com") // Returns "example.com"
 * extractDomain("company name") // Returns null
 * ```
 */
export function extractDomain(input: string): string | null {
  if (!input || typeof input !== 'string') {
    return null;
  }

  // Clean up input
  const cleanInput = input.trim().toLowerCase();

  // Check for email format
  const emailMatch = cleanInput.match(/^[^\s@]+@([^\s@]+\.[^\s@]+)$/);
  if (emailMatch) {
    return emailMatch[1];
  }

  // Check for URL format
  try {
    const url = new URL(
      cleanInput.startsWith('http') ? cleanInput : `https://${cleanInput}`
    );
    const hostname = url.hostname;

    // Validate that hostname looks like a domain
    if (isValidDomain(hostname)) {
      return normalizeDomain(hostname);
    }
  } catch (error: unknown) {
    // Not a valid URL, continue with other checks
  }

  // Check if input is already a domain
  if (isValidDomain(cleanInput)) {
    return normalizeDomain(cleanInput);
  }

  // Try to extract domain from text that might contain a domain
  const domainPattern =
    /(?:https?:\/\/)?(?:www\.)?([a-zA-Z0-9-]+\.[a-zA-Z]{2,})/;
  const match = cleanInput.match(domainPattern);
  if (match && isValidDomain(match[1])) {
    return normalizeDomain(match[1]);
  }

  return null;
}

/**
 * Validates if a string is a valid domain format
 *
 * @param domain - Domain string to validate
 * @returns True if domain appears valid
 */
export function isValidDomain(domain: string): boolean {
  if (!domain || typeof domain !== 'string') {
    return false;
  }

  // Basic domain validation
  const domainRegex =
    /^[a-zA-Z0-9]([a-zA-Z0-9-]*[a-zA-Z0-9])?(\.[a-zA-Z0-9]([a-zA-Z0-9-]*[a-zA-Z0-9])?)*\.[a-zA-Z]{2,}$/;
  return domainRegex.test(domain) && domain.length <= 253;
}

/**
 * Normalizes domain for consistent matching
 *
 * @param domain - Domain to normalize
 * @returns Normalized domain
 */
export function normalizeDomain(domain: string): string {
  if (!domain) {
    return '';
  }

  return domain
    .trim()
    .toLowerCase()
    .replace(/^www\./, '');
}

/**
 * Checks if a query likely contains a domain
 *
 * @param query - Search query to analyze
 * @returns True if query appears to contain domain information
 */
export function hasDomainIndicators(query: string): boolean {
  if (!query) {
    return false;
  }

  const indicators = [
    /\.(com|org|net|edu|gov|mil|io|co|ai|tech|app|dev|xyz)/i,
    /@[a-zA-Z0-9-]+\./,
    /https?:\/\//,
    /www\./,
  ];

  return indicators.some((pattern) => pattern.test(query));
}

/**
 * Extracts multiple potential domains from a query
 *
 * @param query - Query that may contain multiple domains
 * @returns Array of extracted domains
 */
export function extractAllDomains(query: string): string[] {
  if (!query) {
    return [];
  }

  const domains: string[] = [];

  // Extract email domains
  const emailMatches = query.match(/[^\s@]+@([^\s@]+\.[^\s@]+)/g);
  if (emailMatches) {
    emailMatches.forEach((email) => {
      const domain = extractDomain(email);
      if (domain) {
        domains.push(domain);
      }
    });
  }

  // Extract URL domains
  const urlMatches = query.match(/https?:\/\/[^\s]+/g);
  if (urlMatches) {
    urlMatches.forEach((url) => {
      const domain = extractDomain(url);
      if (domain) {
        domains.push(domain);
      }
    });
  }

  // Extract standalone domains (including subdomains)
  const domainMatches = query.match(
    /(?:^|\s|,)([a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*\.[a-zA-Z]{2,})(?=\s|,|$)/g
  );
  if (domainMatches) {
    domainMatches.forEach((match) => {
      const domain = extractDomain(match.trim().replace(/^[,\s]+/, ''));
      if (domain) {
        domains.push(domain);
      }
    });
  }

  // Remove duplicates and normalize
  return Array.from(new Set(domains.map(normalizeDomain)));
}

/**
 * Utility for dynamically importing ES modules
 *
 * This module provides a wrapper around the dynamic import() function to make it easier to
 * work with ES modules in a Node.js environment that may be transitioning from CommonJS.
 * It helps address common compatibility issues that arise when mixing module systems,
 * such as "require is not defined in ES module scope" errors.
 *
 * Use cases:
 * 1. Loading modules at runtime based on configuration
 * 2. Conditional loading of modules to improve startup performance
 * 3. Handling imports that may not be available in all environments
 * 4. Supporting both CommonJS and ES module imports in the same codebase
 */

/**
 * Dynamically imports a module in an environment-agnostic way
 *
 * This function wraps the standard dynamic import() with error handling and logging.
 * It can be used to load both ES modules and CommonJS modules in an ES module context.
 *
 * @param moduleName - Name of the module to import (e.g., 'handlebars', './config.js')
 * @returns Promise resolving to the imported module
 * @throws Error if the module cannot be imported
 *
 * @example
 * // Load a module dynamically
 * const handlebars = await dynamicImport('handlebars');
 *
 * // Use with destructuring
 * const { compile, registerHelper } = await dynamicImport('handlebars');
 */
export async function dynamicImport(moduleName: string): Promise<any> {
  try {
    return await import(moduleName);
  } catch (error: any) {
    console.error(`Error importing module ${moduleName}:`, error);
    throw new Error(
      `Failed to import module "${moduleName}": ${
        error.message || 'Unknown error'
      }`
    );
  }
}

/**
 * Explicit error detection utilities for MCP responses
 *
 * Provides precise error detection logic to avoid false positives
 * from string-based heuristics and empty/false responses that
 * are actually valid success states.
 */

export interface ErrorAnalysis {
  isError: boolean;
  reason?:
    | 'null_or_undefined_result'
    | 'empty_response'
    | 'explicit_success_false'
    | 'meaningful_error_object'
    | 'meaningful_error_string'
    | 'errors_array_has_items'
    | 'api_error_array'
    | 'empty_array';
}

/**
 * Compute error status with explicit detection logic only
 *
 * Avoids false positives by only flagging:
 * - Null/undefined results
 * - Explicit success: false
 * - Meaningful error objects with messages
 * - Non-empty error strings
 * - Error arrays with actual error items
 *
 * @param result - Result object to analyze
 * @param opts - Optional context for tool-specific logic
 * @returns Error analysis with explicit reasoning
 */
export function computeErrorWithContext(
  result: unknown,
  opts?: { toolName?: string; httpStatus?: number }
): ErrorAnalysis {
  const toolName = opts?.toolName;

  const isPlainObject =
    result && typeof result === 'object' && !Array.isArray(result);
  const isEmptyObject =
    isPlainObject &&
    Object.keys(result as Record<string, unknown>).length === 0;

  // 1) Always surface explicit API errors first
  if (
    (result as any)?.error ||
    (Array.isArray((result as any)?.errors) && (result as any).errors.length)
  ) {
    return { isError: true, reason: 'meaningful_error_object' };
  }

  // Handle arrays (bulk ops, list endpoints, etc.)
  if (Array.isArray(result)) {
    // explicit errors inside any element
    const hasExplicitErr = result.some(
      (r) => r?.error || (Array.isArray(r?.errors) && r.errors.length)
    );
    if (hasExplicitErr)
      return { isError: true, reason: 'api_error_array' as const };

    // empty arrays are usually a failure for create/bulk flows
    if (result.length === 0)
      return { isError: true, reason: 'empty_array' as const };

    return { isError: false };
  }

  // 2) Create-record special case:
  //    On 2xx paths the transport may hand minimal shells through;
  //    don't classify {} as an error here  let the business layer
  //    (extractor/assert) decide shape.
  if (toolName === 'create-record') {
    if (result == null)
      return { isError: true, reason: 'null_or_undefined_result' };
    if (isEmptyObject) return { isError: false };
  }

  // 3) Legacy heuristics (unchanged for other tools)
  if (result == null)
    return { isError: true, reason: 'null_or_undefined_result' };
  if (isEmptyObject) return { isError: true, reason: 'empty_response' as any };

  // Detect empty objects as errors (per Issue #517 analysis)
  // Empty objects {} often indicate failed API responses that should be errors

  // Detect Attio API "unknown" record responses (indicates record not found)
  // Attio sometimes returns fake records with id.record_id: 'unknown' instead of 404s
  if (result && typeof result === 'object' && !Array.isArray(result)) {
    const record = result as Record<string, unknown>;
    if (
      record.id &&
      typeof record.id === 'object' &&
      !Array.isArray(record.id)
    ) {
      const id = record.id as Record<string, unknown>;
      if (id.record_id === 'unknown') {
        return { isError: true, reason: 'empty_response' as any };
      }
    }
  }

  // Check for explicit success: false
  if (typeof result === 'object' && result !== null && 'success' in result) {
    const successField = (result as Record<string, unknown>).success;
    if (successField === false) {
      return { isError: true, reason: 'explicit_success_false' };
    }
  }

  // Check for meaningful error objects
  if (typeof result === 'object' && result !== null && 'error' in result) {
    const errorField = (result as Record<string, unknown>).error;

    // Error object with message
    if (
      errorField &&
      typeof errorField === 'object' &&
      'message' in errorField
    ) {
      const message = (errorField as Record<string, unknown>).message;
      if (typeof message === 'string' && message.trim()) {
        return { isError: true, reason: 'meaningful_error_object' };
      }
    }

    // Error string (non-empty)
    if (typeof errorField === 'string' && errorField.trim().length > 0) {
      return { isError: true, reason: 'meaningful_error_string' };
    }
  }

  // Check for errors array with meaningful items
  if (typeof result === 'object' && result !== null && 'errors' in result) {
    const errorsField = (result as Record<string, unknown>).errors;

    if (Array.isArray(errorsField)) {
      const hasErrors = errorsField.some((err: unknown) => {
        if (!err) return false;

        // Error with message
        if (typeof err === 'object' && 'message' in err) {
          const message = (err as Record<string, unknown>).message;
          return typeof message === 'string' && message.trim();
        }

        // Error with code
        if (typeof err === 'object' && 'code' in err) {
          return true;
        }

        // Non-empty error string
        if (typeof err === 'string') {
          return err.trim().length > 0;
        }

        return false;
      });

      if (hasErrors) {
        return { isError: true, reason: 'errors_array_has_items' };
      }
    }
  }

  // Default to no error for all other cases
  // This includes:
  // - Empty arrays: []
  // - Empty strings: ""
  // - false/null values in data fields
  // - Missing fields
  // - Zero counts
  return { isError: false };
}

/**
 * Check if a result should have empty error fields removed
 *
 * @param result - Result to clean
 * @returns Cleaned result without empty error indicators
 */
export function cleanEmptyErrorFields(result: unknown): unknown {
  if (!result || typeof result !== 'object') {
    return result;
  }

  const cleaned = { ...(result as Record<string, unknown>) };

  // Remove empty error field
  if ('error' in cleaned && (cleaned.error === '' || cleaned.error === null)) {
    delete cleaned.error;
  }

  // Remove empty errors array
  if (
    'errors' in cleaned &&
    Array.isArray(cleaned.errors) &&
    cleaned.errors.length === 0
  ) {
    delete cleaned.errors;
  }

  return cleaned;
}

