/**
 * Validates the basic filter structure ensuring filters object has required properties
 *
 * This function performs structural validation to ensure:
 * - The filters parameter is not null/undefined
 * - The filters object has a 'filters' property
 * - The 'filters' property is an array
 *
 * @param filters - The filters object to validate (may be undefined/null from user input)
 * @returns Validated filters object with guaranteed filters array
 * @throws FilterValidationError with consistent error messages and appropriate categories
 *
 * @example
 * ```typescript
 * const validated = validateFiltersObject({
 *   filters: [{ attribute: { slug: 'name' }, condition: 'equals', value: 'test' }]
 * });
 * // validated.filters is guaranteed to be an array
 * ```
 */
export function validateFiltersObject(
  filters: ListEntryFilters | undefined
): ValidatedListEntryFilters {
  // Check if filters is undefined or null
  if (!filters) {
    throw new FilterValidationError(
      ERROR_MESSAGES.MISSING_FILTERS,
      FilterErrorCategory.STRUCTURE
    );
  }

  // Check if filters has a filters property
  if (!('filters' in filters)) {
    throw new FilterValidationError(
      ERROR_MESSAGES.MISSING_FILTERS_PROPERTY,
      FilterErrorCategory.STRUCTURE
    );
  }

  // Check if filters.filters is an array
  if (!Array.isArray(filters.filters)) {
    throw new FilterValidationError(
      ERROR_MESSAGES.FILTERS_NOT_ARRAY(typeof filters.filters),
      FilterErrorCategory.STRUCTURE
    );
  }

  // Return the validated filters with the correct type
  return filters as ValidatedListEntryFilters;
}

/**
 * Validates each individual filter in a filters array
 * Returns list of invalid filters with reasons
 *
 * @param filters - Array of filter objects to validate
 * @param validateConditions - Whether to validate condition values
 * @returns Array of objects containing invalid filter index, reason and filter object
 */
export function collectInvalidFilters(
  filters: ListEntryFilter[],
  validateConditions: boolean = true
): { index: number; reason: string; filter: unknown }[] {
  const invalidFilters: { index: number; reason: string; filter: unknown }[] =
    [];

  // Check each filter in the array
  filters.forEach((filter, index) => {
    // Check if filter is a valid object
    if (!filter || typeof filter !== 'object') {
      invalidFilters.push({
        index,
        reason: `Filter at index ${index} is ${
          filter === null ? 'null' : typeof filter
        }`,
        filter,
      });
      return;
    }

    // Check for required properties
    if (!filter.attribute) {
      invalidFilters.push({
        index,
        reason: ERROR_MESSAGES.MISSING_ATTRIBUTE,
        filter,
      });
      return;
    }

    if (!filter.attribute.slug) {
      invalidFilters.push({
        index,
        reason: ERROR_MESSAGES.MISSING_ATTRIBUTE_SLUG,
        filter,
      });
      return;
    }

    if (!filter.condition) {
      invalidFilters.push({
        index,
        reason: ERROR_MESSAGES.MISSING_CONDITION,
        filter,
      });
      return;
    }

    // Validate condition if enabled
    if (validateConditions && !isValidFilterCondition(filter.condition)) {
      invalidFilters.push({
        index,
        reason: `Invalid condition '${filter.condition}'`,
        filter,
      });
      return;
    }
  });

  return invalidFilters;
}

/**
 * Creates a consistently formatted error message for invalid filters
 *
 * @param invalidFilters - Array of invalid filter objects with reasons
 * @returns Formatted error message with details
 */
export function formatInvalidFiltersError(
  invalidFilters: { index: number; reason: string; filter: unknown }[]
): string {
  if (invalidFilters.length === 0) {
    return '';
  }

  const errorDetails = invalidFilters
    .map((f) => `Filter [${f.index}]: ${f.reason}`)
    .join('; ');

  return errorDetails;
}

/**
 * Full validation of filters with detailed error messages and examples
 *
 * This is the primary entry point for filter validation. It performs:
 * 1. Structural validation using validateFiltersObject()
 * 2. Individual filter validation for each filter in the array
 * 3. Condition validation if enabled
 * 4. Comprehensive error reporting with examples
 *
 * @param filters - Filter object to validate (may have optional filters array)
 * @param validateConditions - Whether to validate condition values against known operators
 * @returns Validated filters with guaranteed filters array and valid structure
 * @throws FilterValidationError with detailed messages, examples, and appropriate categories
 *
 * @example
 * ```typescript
 * // Valid usage
 * const validated = validateFilters({
 *   filters: [
 *     { attribute: { slug: 'name' }, condition: 'equals', value: 'John' },
 *     { attribute: { slug: 'age' }, condition: 'greater_than', value: 18 }
 *   ],
 *   matchAny: false
 * }, true);
 *
 * // validated.filters is guaranteed to exist and be valid
 * ```
 *
 * @see validateFiltersObject For structural validation
 * @see collectInvalidFilters For individual filter validation logic
 */
export function validateFilters(
  filters: ListEntryFilters | undefined,
  validateConditions: boolean = true
): ValidatedListEntryFilters {
  // First validate basic structure
  const validatedFilters = validateFiltersObject(filters);

  // Handle empty or undefined filters array (valid but returns no results)
  if (!validatedFilters.filters || validatedFilters.filters.length === 0) {
    return validatedFilters;
  }

  // Collect invalid filters
  const invalidFilters = collectInvalidFilters(
    validatedFilters.filters,
    validateConditions
  );

  // If all filters are invalid, throw error with examples
  if (invalidFilters.length === validatedFilters.filters.length) {
    const errorDetails = formatInvalidFiltersError(invalidFilters);
    let errorMessage = `${ERROR_MESSAGES.ALL_FILTERS_INVALID} ${errorDetails}`;

    // Add examples to help the user fix their filters - show relevant example based on context
    const relevantExample =
      invalidFilters.length > 1
        ? FILTER_EXAMPLES.MULTIPLE_CONDITIONS
        : FILTER_EXAMPLES.SIMPLE;
    errorMessage +=
      '\n\nExample of valid filter structure: \n' + relevantExample;

    // Determine most appropriate error category based on invalid filters
    let category = FilterErrorCategory.STRUCTURE;

    // If we have specific attribute issues
    if (invalidFilters.some((f) => f.reason.includes('attribute'))) {
      category = FilterErrorCategory.ATTRIBUTE;
    }
    // If we have specific condition issues
    else if (invalidFilters.some((f) => f.reason.includes('condition'))) {
      category = FilterErrorCategory.CONDITION;
    }
    // If we have specific value issues
    else if (invalidFilters.some((f) => f.reason.includes('value'))) {
      category = FilterErrorCategory.VALUE;
    }

    throw new FilterValidationError(errorMessage, category);
  }

  return validatedFilters;
}

/**
 * Helper function to get a descriptive reason for an invalid filter
 *
 * @param filter - The filter to analyze
 * @returns A string describing what's wrong with the filter
 */
export function getInvalidFilterReason(filter: unknown): string {
  if (!filter || typeof filter !== 'object') {
    return `filter is ${filter === null ? 'null' : typeof filter}`;
  }

  const filterObj = filter as Record<string, any>;

  if (!filterObj.attribute) {
    return ERROR_MESSAGES.MISSING_ATTRIBUTE;
  }

  if (!filterObj.attribute.slug) {
    return ERROR_MESSAGES.MISSING_ATTRIBUTE_SLUG;
  }

  if (!filterObj.condition) {
    return ERROR_MESSAGES.MISSING_CONDITION;
  }

  if (!isValidFilterCondition(filterObj.condition)) {
    return `invalid condition '${filterObj.condition}'`;
  }

  return 'unknown issue';
}

/**
 * Generate examples for different filter scenarios
 *
 * @param filterType - The type of filter example to generate (simple, or, multiple)
 * @returns A string with the example in JSON format
 */
export function getFilterExample(
  filterType: 'simple' | 'or' | 'multiple' = 'simple'
): string {
  switch (filterType) {
    case 'or':
      return FILTER_EXAMPLES.OR_LOGIC;
    case 'multiple':
      return FILTER_EXAMPLES.MULTIPLE_CONDITIONS;
    case 'simple':
    default:
      return FILTER_EXAMPLES.SIMPLE;
  }
}

/**
 * @module validators
 *
 * Consolidated filter validation utilities
 * Provides functions to validate and normalize filter parameters
 *
 * This module provides:
 * - Structure validation for filter objects
 * - Date range validation and normalization
 * - Numeric range validation
 * - Activity filter validation
 * - Filter condition validation
 * - Parameter type validation and conversion
 */

// External dependencies
import { DateRangePreset, isValidFilterCondition } from '../../types/attio.js';
import { FilterValidationError } from '../../errors/api-errors.js';
import { isValidISODateString } from '../date-utils.js';

// Internal module dependencies
import {
  DateRange,
  ActivityFilter,
  InteractionType,
  NumericRange,
  FilterConditionType,
  ListEntryFilter,
} from './types.js';

/**
 * Validates a filter structure for basic required properties
 *
 * @param filter - The filter to validate
 * @returns True if filter is valid, false otherwise
 */
export function validateFilterStructure(filter: ListEntryFilter): boolean {
  if (!filter) {
    return false;
  }

  if (!filter.attribute || !filter.attribute.slug) {
    return false;
  }

  if (!filter.condition) {
    return false;
  }

  return true;
}

/**
 * Validates a date range object
 *
 * @param dateRange - The date range to validate
 * @returns Validated and normalized date range
 * @throws FilterValidationError if validation fails
 */
export function validateDateRange(dateRange: any): DateRange {
  if (!dateRange) {
    throw new FilterValidationError('Date range is required');
  }

  // Normalize dateRange if it's a string
  if (typeof dateRange === 'string') {
    try {
      dateRange = JSON.parse(dateRange);
    } catch (error: unknown) {
      throw new FilterValidationError(
        `Invalid date range format: ${
          error instanceof Error ? error.message : String(error)
        }`
      );
    }
  }

  // Check if it's a proper object
  if (typeof dateRange !== 'object' || Array.isArray(dateRange)) {
    throw new FilterValidationError(
      'Date range must be an object with start, end, or preset properties'
    );
  }

  // Must have at least one of preset, start, or end
  if (!dateRange.preset && !dateRange.start && !dateRange.end) {
    throw new FilterValidationError(
      'Date range must specify at least one of: preset, start, or end'
    );
  }

  // Validate preset if provided
  if (dateRange.preset) {
    const normalizedPreset =
      typeof dateRange.preset === 'string'
        ? dateRange.preset.toLowerCase().trim()
        : String(dateRange.preset);

    const validPresets = Object.values(DateRangePreset);
    if (!validPresets.includes(normalizedPreset as DateRangePreset)) {
      throw new FilterValidationError(
        `Invalid date preset: "${dateRange.preset}". ` +
          `Valid presets are: ${validPresets.join(', ')}`
      );
    }

    // Normalize the preset to ensure proper casing
    dateRange.preset = normalizedPreset;
  }

  // Validate start date if provided
  if (dateRange.start) {
    if (typeof dateRange.start === 'string') {
      // Validate ISO string format
      if (!isValidISODateString(dateRange.start)) {
        throw new FilterValidationError(
          `Invalid ISO date string format for start date: ${dateRange.start}`
        );
      }
    } else if (
      typeof dateRange.start === 'object' &&
      !Array.isArray(dateRange.start)
    ) {
      // It's a relative date object, validate basic structure
      if (
        !dateRange.start.unit ||
        !dateRange.start.value ||
        !dateRange.start.direction
      ) {
        throw new FilterValidationError(
          'Relative start date must have unit, value, and direction properties'
        );
      }

      // Validate value is a number
      if (
        typeof dateRange.start.value !== 'number' ||
        isNaN(dateRange.start.value)
      ) {
        throw new FilterValidationError('Relative date value must be a number');
      }

      // Validate direction
      if (
        dateRange.start.direction !== 'past' &&
        dateRange.start.direction !== 'future'
      ) {
        throw new FilterValidationError(
          'Relative date direction must be either "past" or "future"'
        );
      }
    } else {
      throw new FilterValidationError(
        'Start date must be either an ISO date string or a relative date object'
      );
    }
  }

  // Validate end date if provided
  if (dateRange.end) {
    if (typeof dateRange.end === 'string') {
      // Validate ISO string format
      if (!isValidISODateString(dateRange.end)) {
        throw new FilterValidationError(
          `Invalid ISO date string format for end date: ${dateRange.end}`
        );
      }
    } else if (
      typeof dateRange.end === 'object' &&
      !Array.isArray(dateRange.end)
    ) {
      // It's a relative date object, validate basic structure
      if (
        !dateRange.end.unit ||
        !dateRange.end.value ||
        !dateRange.end.direction
      ) {
        throw new FilterValidationError(
          'Relative end date must have unit, value, and direction properties'
        );
      }

      // Validate value is a number
      if (
        typeof dateRange.end.value !== 'number' ||
        isNaN(dateRange.end.value)
      ) {
        throw new FilterValidationError('Relative date value must be a number');
      }

      // Validate direction
      if (
        dateRange.end.direction !== 'past' &&
        dateRange.end.direction !== 'future'
      ) {
        throw new FilterValidationError(
          'Relative date direction must be either "past" or "future"'
        );
      }
    } else {
      throw new FilterValidationError(
        'End date must be either an ISO date string or a relative date object'
      );
    }
  }

  return dateRange as DateRange;
}

/**
 * Validates an activity filter object
 *
 * @param activityFilter - The activity filter to validate
 * @returns Validated and normalized activity filter
 * @throws FilterValidationError if validation fails
 */
export function validateActivityFilter(activityFilter: any): ActivityFilter {
  if (!activityFilter) {
    throw new FilterValidationError('Activity filter is required');
  }

  // Normalize if it's a string
  if (typeof activityFilter === 'string') {
    try {
      activityFilter = JSON.parse(activityFilter);
    } catch (error: unknown) {
      throw new FilterValidationError(
        `Invalid activity filter format: ${
          error instanceof Error ? error.message : String(error)
        }`
      );
    }
  }

  // Check if it's a proper object
  if (typeof activityFilter !== 'object' || Array.isArray(activityFilter)) {
    throw new FilterValidationError(
      'Activity filter must be an object with dateRange and optional interactionType'
    );
  }

  // Validate required dateRange property
  if (!activityFilter.dateRange) {
    throw new FilterValidationError(
      'Activity filter must include a dateRange property'
    );
  }

  // Validate dateRange
  try {
    activityFilter.dateRange = validateDateRange(activityFilter.dateRange);
  } catch (error: unknown) {
    throw new FilterValidationError(
      `Invalid dateRange in activity filter: ${
        error instanceof Error ? error.message : String(error)
      }`
    );
  }

  // Validate interactionType if provided
  if (activityFilter.interactionType !== undefined) {
    const validTypes = Object.values(InteractionType);

    if (
      typeof activityFilter.interactionType !== 'string' ||
      !validTypes.includes(activityFilter.interactionType as InteractionType)
    ) {
      throw new FilterValidationError(
        `Invalid interaction type: "${activityFilter.interactionType}". ` +
          `Valid types are: ${validTypes.join(', ')}`
      );
    }
  }

  return activityFilter as ActivityFilter;
}

/**
 * Validates a numeric range object
 *
 * @param range - The numeric range to validate
 * @returns Validated and normalized numeric range
 * @throws FilterValidationError if validation fails
 */
export function validateNumericRange(range: any): NumericRange {
  if (!range) {
    throw new FilterValidationError('Numeric range is required');
  }

  // Normalize if it's a string
  if (typeof range === 'string') {
    try {
      range = JSON.parse(range);
    } catch (error: unknown) {
      throw new FilterValidationError(
        `Invalid numeric range format: ${
          error instanceof Error ? error.message : String(error)
        }`
      );
    }
  }

  // Check if it's a proper object
  if (typeof range !== 'object' || Array.isArray(range)) {
    throw new FilterValidationError(
      'Numeric range must be an object with min, max, or equals properties'
    );
  }

  // Must have at least one of min, max, or equals
  if (
    range.min === undefined &&
    range.max === undefined &&
    range.equals === undefined
  ) {
    throw new FilterValidationError(
      'Numeric range must specify at least one of: min, max, or equals'
    );
  }

  // If equals is specified, min and max should not be
  if (
    range.equals !== undefined &&
    (range.min !== undefined || range.max !== undefined)
  ) {
    throw new FilterValidationError(
      'Cannot specify both equals and min/max in a numeric range'
    );
  }

  // Check types and convert to number if needed
  if (range.min !== undefined) {
    range.min = Number(range.min);
    if (isNaN(range.min)) {
      throw new FilterValidationError('Min value must be a valid number');
    }
  }

  if (range.max !== undefined) {
    range.max = Number(range.max);
    if (isNaN(range.max)) {
      throw new FilterValidationError('Max value must be a valid number');
    }
  }

  if (range.equals !== undefined) {
    range.equals = Number(range.equals);
    if (isNaN(range.equals)) {
      throw new FilterValidationError('Equals value must be a valid number');
    }
  }

  // Check that min <= max if both are specified
  if (
    range.min !== undefined &&
    range.max !== undefined &&
    range.min > range.max
  ) {
    throw new FilterValidationError(
      `Invalid numeric range: min (${range.min}) cannot be greater than max (${range.max})`
    );
  }

  return range as NumericRange;
}

/**
 * Validates a filter condition string
 *
 * @param condition - The condition to validate
 * @returns The validated condition
 * @throws FilterValidationError if validation fails
 */
export function validateFilterCondition(
  condition: string
): FilterConditionType {
  if (!condition) {
    throw new FilterValidationError('Filter condition is required');
  }

  // Use the isValidFilterCondition from types/attio.js
  if (!isValidFilterCondition(condition)) {
    const validConditions = Object.values(FilterConditionType);
    throw new FilterValidationError(
      `Invalid filter condition: "${condition}". ` +
        `Valid conditions are: ${validConditions.join(', ')}`
    );
  }

  return condition as FilterConditionType;
}

/**
 * Ensures a value is a number, converting if necessary
 *
 * @param value - The value to validate and convert
 * @param paramName - Name of the parameter (for error messages)
 * @param defaultValue - Optional default value if undefined
 * @returns The validated number
 * @throws FilterValidationError if validation fails
 */
export function validateNumericParam(
  value: any,
  paramName: string,
  defaultValue?: number
): number {
  if (value === undefined || value === null) {
    if (defaultValue !== undefined) {
      return defaultValue;
    }
    throw new FilterValidationError(`${paramName} is required`);
  }

  const num = Number(value);
  if (isNaN(num)) {
    throw new FilterValidationError(`${paramName} must be a valid number`);
  }

  return num;
}

/**
 * Validates filter structure and conditions
 *
 * @param filter - The filter to validate
 * @param validateConditions - Whether to validate condition types (default: true)
 * @throws FilterValidationError if validation fails
 */
export function validateFilterWithConditions(
  filter: ListEntryFilter,
  validateConditions: boolean = true
): void {
  if (!validateFilterStructure(filter)) {
    const slugInfo = filter?.attribute?.slug ? ` ${filter.attribute.slug}` : '';
    throw new FilterValidationError(
      `Invalid filter: Incomplete filter structure for${slugInfo}`
    );
  }

  const { slug } = filter.attribute;

  if (validateConditions && !isValidFilterCondition(filter.condition)) {
    throw new FilterValidationError(
      `Invalid filter condition '${filter.condition}' for attribute '${slug}'. ` +
        `Valid conditions are: ${Object.values(FilterConditionType).join(', ')}`
    );
  }
}

/**
 * Safe JSON serialization utilities to prevent MCP protocol breakdown
 * Handles circular references, non-serializable values, and large objects
 *
 * Uses fast-safe-stringify for improved performance and reliability
 *
 * IMPORTANT MCP PROTOCOL WARNING:
 * Never use console.error() in this file or any MCP-related code.
 * Always use console.error() or logger.safeMcpLog() instead.
 * Using console.log will break the MCP protocol, as it writes to stdout
 * which is used for client-server communication.
 */
// Support both CJS and ESM default export shapes for fast-safe-stringify
import * as fastSafeStringifyNs from 'fast-safe-stringify';
const fastSafeStringify: (value: any, replacer?: any, space?: any) => string =
  (fastSafeStringifyNs as any).default || (fastSafeStringifyNs as any);

/**
 * Interface for serialization options
 */
export interface SerializationOptions {
  /** Maximum depth for nested objects (only used in the legacy implementation) */
  maxDepth?: number;
  /** Maximum string length before truncation */
  maxStringLength?: number;
  /** Whether to include stack traces in error objects */
  includeStackTraces?: boolean;
  /** Custom replacer function */
  replacer?: (key: string, value: any) => any;
  /** Indent spaces for pretty printing (default: 2) */
  indent?: number;
}

/**
 * Default serialization options
 */
const DEFAULT_OPTIONS: Required<SerializationOptions> = {
  maxDepth: 20, // Kept for backward compatibility
  maxStringLength: 25000, // 25KB max string length - more reasonable for MCP
  includeStackTraces: false,
  replacer: (key, value) => value,
  indent: 2,
};

/**
 * Safe JSON stringify that handles circular references and non-serializable values
 *
 * Uses fast-safe-stringify for high performance and reliability
 *
 * @param obj - The object to stringify
 * @param options - Serialization options
 * @returns Safe JSON string
 */
export function safeJsonStringify(
  obj: any,
  options: SerializationOptions = {}
): string {
  const opts = { ...DEFAULT_OPTIONS, ...options };

  // Performance monitoring for large objects
  const startTime = performance.now();

  try {
    // Create a custom replacer to handle non-standard values
    const customReplacer = (key: string, value: any): any => {
      // First apply user-provided replacer if any
      value = opts.replacer(key, value);

      // Handle undefined (normally skipped by JSON)
      if (value === undefined) {
        return null;
      }

      // Handle very long strings
      if (typeof value === 'string' && value.length > opts.maxStringLength) {
        return value.substring(0, opts.maxStringLength) + '... [truncated]';
      }

      // Handle special object types more gracefully
      if (value instanceof Error) {
        const errorObj: any = {
          name: value.name,
          message: value.message,
        };
        if (opts.includeStackTraces && value.stack) {
          errorObj.stack = value.stack;
        }
        if ('cause' in value && value.cause) {
          errorObj.cause = value.cause;
        }
        return errorObj;
      }

      if (value instanceof Map) {
        return '[Map: ' + value.size + ' entries]';
      }

      if (value instanceof Set) {
        return '[Set: ' + value.size + ' items]';
      }

      if (ArrayBuffer && value instanceof ArrayBuffer) {
        return '[ArrayBuffer: ' + value.byteLength + ' bytes]';
      }

      return value;
    };

    // Use fast-safe-stringify with our custom replacer
    const result = fastSafeStringify(obj, customReplacer, opts.indent);

    // Performance monitoring and logging
    const duration = performance.now() - startTime;
    if (duration > 100) {
      console.error(
        `[safeJsonStringify] Slow serialization detected: ${duration.toFixed(
          2
        )}ms for ${typeof obj} (${result.length} chars)`
      );
    }

    return result;
  } catch (error: unknown) {
    // Enhanced error context
    const duration = performance.now() - startTime;
    console.error(
      `[safeJsonStringify] Serialization failed after ${duration.toFixed(
        2
      )}ms for ${typeof obj}:`,
      error
    );

    // Use fast-safe-stringify directly for the error fallback
    return fastSafeStringify(
      {
        error: 'Serialization failed',
        message: error instanceof Error ? error.message : String(error),
        originalType: typeof obj,
        timestamp: new Date().toISOString(),
        duration: `${duration.toFixed(2)}ms`,
      },
      undefined,
      2
    );
  }
}

/**
 * Validates that a JSON string is properly formed and can be parsed
 *
 * @param jsonString - The JSON string to validate
 * @returns Object with validation result and parsed data if successful
 */
export function validateJsonString(jsonString: string): {
  isValid: boolean;
  data?: any;
  error?: string;
  size: number;
} {
  const size = Buffer.byteLength(jsonString, 'utf8');

  try {
    const data = JSON.parse(jsonString);
    return {
      isValid: true,
      data,
      size,
    };
  } catch (error: unknown) {
    return {
      isValid: false,
      error: error instanceof Error ? error.message : String(error),
      size,
    };
  }
}

/**
 * Detects potential circular references in an object before serialization
 *
 * NOTE: This is less critical now with fast-safe-stringify, but kept for
 * compatibility with existing code that uses it.
 *
 * @param obj - The object to check
 * @param maxDepth - Maximum depth to check
 * @returns True if circular references are detected
 */
export function hasCircularReferences(
  obj: any,
  maxDepth: number = 10
): boolean {
  const seen = new WeakSet();

  function check(value: any, depth: number): boolean {
    if (depth > maxDepth) return false;
    if (value === null || typeof value !== 'object') return false;

    if (seen.has(value)) return true;

    seen.add(value);

    try {
      if (Array.isArray(value)) {
        return value.some((item) => check(item, depth + 1));
      } else {
        return Object.values(value).some((val) => check(val, depth + 1));
      }
    } finally {
      seen.delete(value);
    }
  }

  return check(obj, 0);
}

/**
 * Creates a safe copy of an object that can be JSON serialized
 *
 * Uses fast-safe-stringify for improved performance and reliability
 *
 * @param obj - The object to copy
 * @param options - Serialization options
 * @returns Safe copy of the object
 */
export function createSafeCopy(
  obj: any,
  options: SerializationOptions = {}
): any {
  try {
    // Fast path: directly use fast-safe-stringify to create a JSON string
    const jsonString = safeJsonStringify(obj, options);

    // Parse it back to create the safe copy
    return JSON.parse(jsonString);
  } catch (error: unknown) {
    console.error(
      '[createSafeCopy] Failed to create safe copy:',
      error instanceof Error ? error.message : String(error)
    );

    // Return a structured error object
    return {
      error: 'Failed to create safe copy',
      message: error instanceof Error ? error.message : String(error),
      originalType: typeof obj,
    };
  }
}

/**
 * Sanitizes MCP response objects to prevent JSON parsing errors
 *
 * Uses fast-safe-stringify to ensure all responses are safely serializable
 *
 * @param response - The MCP response object to sanitize
 * @returns Sanitized response object
 */
export function sanitizeMcpResponse(response: any): any {
  // Ensure response has the correct structure
  if (!response || typeof response !== 'object') {
    return {
      content: [
        {
          type: 'text',
          text: 'Invalid response structure',
        },
      ],
      isError: true,
      error: {
        code: 500,
        message: 'Response sanitization failed',
        type: 'sanitization_error',
      },
    };
  }

  try {
    // Create safe copy with MCP-specific options optimized for Attio responses
    return createSafeCopy(response, {
      maxStringLength: 40000, // 40KB for response content - reasonable limit
      includeStackTraces: process.env.NODE_ENV === 'development',
    });
  } catch (error: unknown) {
    // Provide a valid fallback response if sanitization fails
    return {
      content: [
        {
          type: 'text',
          text: 'Error processing response. The server encountered an error while formatting the response data.',
        },
      ],
      isError: true,
      error: {
        code: 500,
        message:
          'Response sanitization failed: ' +
          (error instanceof Error ? error.message : String(error)),
        type: 'sanitization_error',
      },
    };
  }
}

/**
 * Mock State Manager for Test Environments
 *
 * Provides shared state storage for mock company data during testing
 * to ensure consistency between createCompany, updateCompany, and getCompanyDetails
 * when using mock responses instead of real API calls.
 *
 * This is only active in test environments (E2E_MODE=true or NODE_ENV=test)
 */

import { Company } from '../types/attio.js';

// In-memory storage for mock company data (test environments only)
const mockCompanyStorage = new Map<string, Company>();

/**
 * Checks if mock state should be used (test environments only)
 */
function shouldUseMockState(): boolean {
  return process.env.E2E_MODE === 'true' || process.env.NODE_ENV === 'test';
}

/**
 * Stores a mock company in shared state
 * Only active in test environments
 */
export function setMockCompany(companyId: string, company: Company): void {
  if (!shouldUseMockState()) {
    return; // No-op in production
  }

  if (!companyId || !company) {
    console.warn('[MockState] Invalid parameters for setMockCompany:', {
      companyId,
      company,
    });
    return;
  }

  mockCompanyStorage.set(companyId, { ...company });

  if (
    process.env.NODE_ENV === 'development' ||
    process.env.E2E_MODE === 'true'
  ) {
    console.log(`[MockState] Stored mock company: ${companyId}`, {
      storedValues: company.values,
      totalStoredCompanies: mockCompanyStorage.size,
    });
  }
}

/**
 * Retrieves a mock company from shared state
 * Returns null if not found or not in test environment
 */
export function getMockCompany(companyId: string): Company | null {
  if (!shouldUseMockState() || !companyId) {
    return null;
  }

  const company = mockCompanyStorage.get(companyId);

  if (
    process.env.NODE_ENV === 'development' ||
    process.env.E2E_MODE === 'true'
  ) {
    console.log(`[MockState] Retrieved mock company: ${companyId}`, {
      found: !!company,
      values: company?.values || null,
    });
  }

  return company ? { ...company } : null;
}

/**
 * Updates a mock company with new attributes (partial update)
 * Merges new attributes with existing ones
 */
export function updateMockCompany(
  companyId: string,
  attributes: Record<string, unknown>
): Company | null {
  if (!shouldUseMockState() || !companyId || !attributes) {
    return null;
  }

  const existingCompany = mockCompanyStorage.get(companyId);
  if (!existingCompany) {
    if (
      process.env.NODE_ENV === 'development' ||
      process.env.E2E_MODE === 'true'
    ) {
      console.warn(
        `[MockState] Cannot update company ${companyId}: not found in mock storage`
      );
    }
    return null;
  }

  // Merge the new attributes with existing values
  const updatedValues = {
    ...existingCompany.values,
    ...attributes,
  };

  const updatedCompany: Company = {
    ...existingCompany,
    values: updatedValues,
  };

  mockCompanyStorage.set(companyId, updatedCompany);

  if (
    process.env.NODE_ENV === 'development' ||
    process.env.E2E_MODE === 'true'
  ) {
    console.log(`[MockState] Updated mock company: ${companyId}`, {
      newAttributes: attributes,
      mergedValues: updatedValues,
    });
  }

  return { ...updatedCompany };
}

/**
 * Checks if a company exists in mock storage
 */
export function hasMockCompany(companyId: string): boolean {
  if (!shouldUseMockState() || !companyId) {
    return false;
  }
  return mockCompanyStorage.has(companyId);
}

/**
 * Clears all mock companies from storage
 * Useful for test cleanup
 */
export function clearMockCompanies(): void {
  if (!shouldUseMockState()) {
    return;
  }

  const count = mockCompanyStorage.size;
  mockCompanyStorage.clear();

  if (
    process.env.NODE_ENV === 'development' ||
    process.env.E2E_MODE === 'true'
  ) {
    console.log(`[MockState] Cleared ${count} mock companies from storage`);
  }
}

/**
 * Gets all mock company IDs (for debugging)
 */
export function getAllMockCompanyIds(): string[] {
  if (!shouldUseMockState()) {
    return [];
  }
  return Array.from(mockCompanyStorage.keys());
}

/**
 * Creates a properly formatted Attio API value structure
 * Attio API stores values as arrays of objects with metadata
 */
export function createAttioApiValue(
  value: any,
  attributeType: string = 'text'
): any[] {
  if (value === null || value === undefined) {
    return [];
  }

  return [
    {
      active_from: new Date().toISOString(),
      active_until: null,
      created_by_actor: {
        type: 'system',
        id: null,
      },
      value: String(value),
      attribute_type: attributeType,
    },
  ];
}

/**
 * Creates a company mock with proper Attio API structure
 * This ensures our mocks match the real API response format
 */
export function createMockCompanyWithApiStructure(
  companyId: string,
  attributes: Record<string, unknown>
): Company {
  // Convert simple attributes to Attio API format
  const apiFormattedValues: Record<string, unknown> = {};

  for (const [key, value] of Object.entries(attributes)) {
    if (value !== null && value !== undefined) {
      // Special handling for specific field types
      if (key === 'categories') {
        // Categories are select fields with option structure
        apiFormattedValues[key] = [
          {
            active_from: new Date().toISOString(),
            active_until: null,
            created_by_actor: { type: 'system', id: null },
            option: {
              title: String(value),
              is_archived: false,
            },
            attribute_type: 'select',
          },
        ];
      } else {
        // Default text field format
        apiFormattedValues[key] = createAttioApiValue(value, 'text');
      }
    } else {
      apiFormattedValues[key] = [];
    }
  }

  return {
    id: {
      workspace_id: 'test-workspace',
      object_id: 'companies',
      record_id: companyId,
    },
    values: apiFormattedValues,
    created_at: new Date().toISOString(),
    record_url: `https://app.attio.com/workspace/test-workspace/object/companies/${companyId}`,
  } as Company;
}

/**
 * People Input Normalization
 *
 * Handles normalization of people data to support multiple input formats.
 * Converts various input shapes to the standard Attio API format.
 */

import {
  SanitizedObject,
  InputSanitizer,
} from '../../handlers/tool-configs/universal/schemas.js';
import { isValidEmail } from '../validation/email-validation.js';
import {
  UniversalValidationError,
  ErrorType,
} from '../../handlers/tool-configs/universal/schemas.js';
import {
  EmailValidationConfig,
  EmailValidationMode,
  getEmailValidationConfig,
  DEFAULT_EMAIL_VALIDATION_CONFIG,
} from './email-validation-config.js';

/**
 * People name input formats
 */
export interface NameStringInput {
  name: string;
}

export interface NameObjectInput {
  first_name?: string;
  last_name?: string;
  full_name?: string;
}

export type NameInput = string | NameStringInput | NameObjectInput;

/**
 * Email input formats
 */
export type EmailInput =
  | string
  | string[]
  | { email_address: string }
  | { email_addresses: string[] };

/**
 * Normalized people data
 */
export interface NormalizedPeopleData {
  first_name?: string;
  last_name?: string;
  full_name?: string;
  email_addresses?: Array<{
    email_address: string;
    email_type?: string;
  }>;
  phone_numbers?: Array<{
    phone_number: string;
    phone_type?: string;
  }>;
  [key: string]: any;
}

/**
 * Normalizes people input data to standard format
 */
export class PeopleDataNormalizer {
  /**
   * Normalize name input to standard format
   */
  static normalizeName(
    input: any,
    options?: { includeFullName?: boolean }
  ):
    | { first_name?: string; last_name?: string; full_name?: string }
    | undefined {
    if (!input) return undefined;

    // Handle string input
    if (typeof input === 'string') {
      const trimmed = input.trim();
      if (!trimmed) return undefined;

      // Try to split into first and last name
      const parts = trimmed.split(/\s+/);
      let result: {
        first_name?: string;
        last_name?: string;
        full_name?: string;
      };

      if (parts.length === 1) {
        result = { first_name: parts[0], full_name: trimmed };
      } else if (parts.length === 2) {
        result = {
          first_name: parts[0],
          last_name: parts[1],
          full_name: trimmed,
        };
      } else {
        // Multiple parts - take first as first name, last as last name
        result = {
          first_name: parts[0],
          last_name: parts[parts.length - 1],
          full_name: trimmed,
        };
      }

      // Filter out full_name if not explicitly requested
      if (!options?.includeFullName && result.full_name) {
        const { full_name, ...filtered } = result;
        return filtered;
      }

      return result;
    }

    // Handle object input
    if (typeof input === 'object' && !Array.isArray(input)) {
      const result: {
        first_name?: string;
        last_name?: string;
        full_name?: string;
      } = {};

      // Check for name field (string)
      if (typeof input.name === 'string') {
        return this.normalizeName(input.name);
      }

      // Check for first_name and last_name
      if (input.first_name || input.last_name) {
        if (input.first_name) {
          result.first_name = String(input.first_name).trim();
        }
        if (input.last_name) {
          result.last_name = String(input.last_name).trim();
        }

        // Generate full_name if not provided
        if (!input.full_name && (result.first_name || result.last_name)) {
          result.full_name = [result.first_name, result.last_name]
            .filter(Boolean)
            .join(' ');
        }
      }

      // Check for full_name
      if (input.full_name) {
        result.full_name = String(input.full_name).trim();

        // If we don't have first/last name, try to extract from full name
        if (!result.first_name && !result.last_name) {
          const parts = result.full_name.split(/\s+/);
          if (parts.length >= 2) {
            result.first_name = parts[0];
            result.last_name = parts.slice(1).join(' ');
          } else if (parts.length === 1) {
            result.first_name = parts[0];
          }
        }
      }

      // Check for firstName/lastName (camelCase variants)
      if (input.firstName) {
        result.first_name = String(input.firstName).trim();
      }
      if (input.lastName) {
        result.last_name = String(input.lastName).trim();
      }

      if (Object.keys(result).length === 0) return undefined;

      // Filter out full_name if not explicitly requested
      if (!options?.includeFullName && result.full_name) {
        const { full_name, ...filtered } = result;
        return Object.keys(filtered).length > 0 ? filtered : undefined;
      }

      return result;
    }

    return undefined;
  }

  /**
   * Extract email value from various input formats
   */
  private static extractEmailValue(emailField: unknown): string | null {
    if (typeof emailField === 'string') {
      return emailField;
    }
    if (typeof emailField === 'object' && emailField && 'value' in emailField) {
      const value = (emailField as any).value;
      if (typeof value === 'string') {
        return value;
      }
    }

    // Handle malformed inputs - don't try to convert objects, arrays, etc. to strings
    if (emailField === null || emailField === undefined) {
      return null;
    }
    if (typeof emailField === 'object' || Array.isArray(emailField)) {
      return null; // Don't convert objects/arrays to "[object object]" strings
    }
    if (typeof emailField === 'number' || typeof emailField === 'boolean') {
      return null; // Don't convert numbers/booleans to strings for email validation
    }

    return String(emailField);
  }

  /**
   * Validate and process a single email based on configuration
   */
  private static validateAndProcessEmail(
    emailValue: string,
    config: EmailValidationConfig
  ): string | null {
    const normalized = InputSanitizer.normalizeEmail(emailValue);

    // Check if the normalized email is empty or invalid
    if (!normalized || !normalized.trim()) {
      if (config.mode === EmailValidationMode.STRICT) {
        throw new UniversalValidationError(
          `Invalid email format: "${emailValue}". Please provide a valid email address (e.g., user@example.com)`,
          ErrorType.USER_ERROR,
          {
            field: 'email_addresses',
            suggestion:
              'Ensure email addresses are in the format: user@domain.com',
          }
        );
      }
      return null;
    }

    if (!isValidEmail(normalized)) {
      switch (config.mode) {
        case EmailValidationMode.STRICT:
          throw new UniversalValidationError(
            `Invalid email format: "${normalized}". Please provide a valid email address (e.g., user@example.com)`,
            ErrorType.USER_ERROR,
            {
              field: 'email_addresses',
              suggestion:
                'Ensure email addresses are in the format: user@domain.com',
            }
          );

        case EmailValidationMode.WARN:
          config.logger?.(
            `WARNING: Invalid email format "${normalized}" was skipped. Consider updating to a valid format.`,
            'warn'
          );
          return null;

        case EmailValidationMode.LEGACY:
          if (config.logDeprecationWarnings) {
            config.logger?.(
              'DEPRECATION WARNING: Invalid emails are being silently ignored. This behavior will change in a future version. Use EMAIL_VALIDATION_MODE=strict for new behavior.',
              'warn'
            );
          }
          return null;

        default:
          return null;
      }
    }

    return normalized;
  }

  /**
   * Normalize email input to standard format
   *
   * @param input - Email input in various formats
   * @param config - Email validation configuration (optional)
   */
  static normalizeEmails(
    input: any,
    config: EmailValidationConfig = DEFAULT_EMAIL_VALIDATION_CONFIG
  ): Array<{ email_address: string; email_type?: string }> | undefined {
    if (input === null || input === undefined) return undefined;

    const emails: Array<{ email_address: string; email_type?: string }> = [];

    // Handle string input
    if (typeof input === 'string') {
      const validatedEmail = this.validateAndProcessEmail(input, config);
      if (validatedEmail) {
        emails.push({ email_address: validatedEmail });
      }
    }
    // Handle array input
    else if (Array.isArray(input)) {
      for (const item of input) {
        if (typeof item === 'string') {
          const validatedEmail = this.validateAndProcessEmail(item, config);
          if (validatedEmail) {
            emails.push({ email_address: validatedEmail });
          }
        } else if (typeof item === 'object' && item) {
          // ISSUE #518 FIX: Enhanced object email format support
          // Added support for multiple object formats: {email_address: "..."}, {value: "..."}, {email: "..."}
          // Handle objects with email_address field (official Attio API format)
          if (item.email_address) {
            const emailValue = this.extractEmailValue(item.email_address);
            if (emailValue) {
              const validatedEmail = this.validateAndProcessEmail(
                emailValue,
                config
              );
              if (validatedEmail) {
                emails.push({
                  email_address: validatedEmail,
                  email_type: item.email_type || item.type,
                });
              }
            }
          }
          // Handle objects with value field (alternative format)
          else if (item.value) {
            const emailValue = this.extractEmailValue(item.value);
            if (emailValue) {
              const validatedEmail = this.validateAndProcessEmail(
                emailValue,
                config
              );
              if (validatedEmail) {
                emails.push({
                  email_address: validatedEmail,
                  email_type: item.email_type || item.type,
                });
              }
            }
          }
          // Handle objects with email field (alternative format)
          else if (item.email) {
            const emailValue = this.extractEmailValue(item.email);
            if (emailValue) {
              const validatedEmail = this.validateAndProcessEmail(
                emailValue,
                config
              );
              if (validatedEmail) {
                emails.push({
                  email_address: validatedEmail,
                  email_type: item.email_type || item.type,
                });
              }
            }
          }
        }
      }
    }
    // Handle object input
    else if (typeof input === 'object') {
      // Check for email_address field
      if (input.email_address) {
        const emailValue = this.extractEmailValue(input.email_address);
        if (emailValue) {
          const validatedEmail = this.validateAndProcessEmail(
            emailValue,
            config
          );
          if (validatedEmail) {
            emails.push({
              email_address: validatedEmail,
              email_type: input.email_type || input.type,
            });
          }
        }
      }
      // Check for email_addresses field
      else if (input.email_addresses && Array.isArray(input.email_addresses)) {
        const normalized = this.normalizeEmails(input.email_addresses, config);
        if (normalized) {
          emails.push(...normalized);
        }
      }
      // Check for email field (singular)
      else if (input.email) {
        const emailValue = this.extractEmailValue(input.email);
        if (emailValue) {
          const validatedEmail = this.validateAndProcessEmail(
            emailValue,
            config
          );
          if (validatedEmail) {
            emails.push({ email_address: validatedEmail });
          }
        }
      }
      // Check for emails field (plural)
      else if (input.emails && Array.isArray(input.emails)) {
        const normalized = this.normalizeEmails(input.emails, config);
        if (normalized) {
          emails.push(...normalized);
        }
      }
    }

    if (emails.length === 0) return undefined;

    // Add default email_type: "primary" for single emails without a type
    if (emails.length === 1 && !emails[0].email_type) {
      emails[0].email_type = 'primary';
    }

    // For multiple emails, ensure first has primary if none specified
    if (emails.length > 1) {
      const hasTyped = emails.some((e) => e.email_type);
      if (!hasTyped) {
        emails[0].email_type = 'primary';
        for (let i = 1; i < emails.length; i++) {
          emails[i].email_type = 'secondary';
        }
      }
    }

    return emails;
  }

  /**
   * Normalize phone number input
   */
  static normalizePhones(
    input: any
  ): Array<{ phone_number: string; phone_type?: string }> | undefined {
    if (!input) return undefined;

    const phones: Array<{ phone_number: string; phone_type?: string }> = [];

    // Handle string input
    if (typeof input === 'string') {
      const normalized = this.normalizePhoneNumber(input);
      if (normalized) {
        phones.push({ phone_number: normalized });
      }
    }
    // Handle array input
    else if (Array.isArray(input)) {
      for (const item of input) {
        if (typeof item === 'string') {
          const normalized = this.normalizePhoneNumber(item);
          if (normalized) {
            phones.push({ phone_number: normalized });
          }
        } else if (
          typeof item === 'object' &&
          (item.phone_number || item.number)
        ) {
          const normalized = this.normalizePhoneNumber(
            item.phone_number || item.number
          );
          if (normalized) {
            phones.push({
              phone_number: normalized,
              phone_type: item.phone_type || item.type,
            });
          }
        }
      }
    }
    // Handle object input
    else if (typeof input === 'object') {
      // Check various phone field names
      const phoneFields = [
        'phone_number',
        'phone',
        'phoneNumber',
        'mobile',
        'telephone',
      ];
      for (const field of phoneFields) {
        if (input[field]) {
          const normalized = this.normalizePhoneNumber(input[field]);
          if (normalized) {
            phones.push({
              phone_number: normalized,
              phone_type: input.phone_type || input.type,
            });
            break;
          }
        }
      }

      // Check for phone_numbers array
      if (input.phone_numbers || input.phones) {
        const normalized = this.normalizePhones(
          input.phone_numbers || input.phones
        );
        if (normalized) {
          phones.push(...normalized);
        }
      }
    }

    return phones.length > 0 ? phones : undefined;
  }

  /**
   * Normalize a single phone number
   */
  private static normalizePhoneNumber(phone: string): string | undefined {
    if (!phone || typeof phone !== 'string') return undefined;

    // Remove all non-digit characters except + at the beginning
    let normalized = phone.trim();
    const hasPlus = normalized.startsWith('+');
    normalized = normalized.replace(/\D/g, '');

    // Re-add + if it was there
    if (hasPlus) {
      normalized = '+' + normalized;
    }

    // Basic validation - should have at least 7 digits
    const digitCount = normalized.replace(/\D/g, '').length;
    if (digitCount < 7 || digitCount > 15) {
      return undefined;
    }

    return normalized;
  }

  /**
   * Normalize complete people record data
   *
   * @param data - People data to normalize
   * @param emailConfig - Email validation configuration (optional)
   */
  static normalizePeopleData(
    data: any,
    emailConfig: EmailValidationConfig = DEFAULT_EMAIL_VALIDATION_CONFIG
  ): NormalizedPeopleData {
    if (!data || typeof data !== 'object' || Array.isArray(data)) {
      return {};
    }

    const sanitized = InputSanitizer.sanitizeObject(data) as SanitizedObject;
    const normalized: NormalizedPeopleData = {};

    // Normalize name
    const nameFields = [
      'name',
      'first_name',
      'last_name',
      'full_name',
      'firstName',
      'lastName',
    ];
    const hasNameField = nameFields.some((field) => field in sanitized);

    if (hasNameField) {
      const nameData = this.normalizeName(sanitized);
      if (nameData) {
        // Create name array for Attio personal-name format
        normalized.name = [
          {
            first_name: nameData.first_name || '',
            last_name: nameData.last_name || '',
            full_name:
              nameData.full_name ||
              [nameData.first_name, nameData.last_name]
                .filter(Boolean)
                .join(' '),
          },
        ];

        // Also add flattened fields for backward compatibility with attribute-format-helpers
        if (nameData.first_name) {
          normalized.first_name = nameData.first_name;
        }
        if (nameData.last_name) {
          normalized.last_name = nameData.last_name;
        }
        if (nameData.full_name) {
          normalized.full_name = nameData.full_name;
        }
      }
    }

    // Normalize emails
    const emailFields = [
      'email',
      'emails',
      'email_address',
      'email_addresses',
      'emailAddress',
    ];
    const hasEmailField = emailFields.some((field) => field in sanitized);

    if (hasEmailField) {
      const emailData = this.normalizeEmails(sanitized, emailConfig);
      if (emailData) {
        normalized.email_addresses = emailData;
      }
    }

    // Normalize phones
    const phoneFields = [
      'phone',
      'phones',
      'phone_number',
      'phone_numbers',
      'phoneNumber',
      'mobile',
      'telephone',
    ];
    const hasPhoneField = phoneFields.some((field) => field in sanitized);

    if (hasPhoneField) {
      const phoneData = this.normalizePhones(sanitized);
      if (phoneData) {
        normalized.phone_numbers = phoneData;
      }
    }

    // Copy over other fields that aren't being normalized
    const normalizedFields = new Set([
      ...nameFields,
      ...emailFields,
      ...phoneFields,
    ]);

    for (const [key, value] of Object.entries(sanitized)) {
      if (!normalizedFields.has(key)) {
        normalized[key] = value;
      }
    }

    return normalized;
  }

  /**
   * Check if data needs people normalization
   */
  static needsNormalization(data: any): boolean {
    if (!data || typeof data !== 'object' || Array.isArray(data)) {
      return false;
    }

    // Check for fields that indicate people data needing normalization
    const peopleFields = [
      'name', // String name instead of object
      'email', // Singular email
      'email_address', // Singular email address
      'phone', // Singular phone
      'phone_number', // Singular phone number
      'firstName', // CamelCase variants
      'lastName',
      'emailAddress',
      'phoneNumber',
    ];

    return peopleFields.some((field) => field in data);
  }
}

/**
 * Pagination utilities for Attio MCP server
 * Provides functions for implementing efficient pagination with filtered results
 */
import { AttioRecord } from '../types/attio.js';

/**
 * Standard pagination metadata interface
 */
export interface PaginationMetadata {
  /** Total number of records available */
  totalCount: number;

  /** Current page number (1-based) */
  currentPage: number;

  /** Number of records per page */
  pageSize: number;

  /** Total number of pages available */
  totalPages: number;

  /** Whether there are more pages available */
  hasMore: boolean;

  /** URL for the next page (if available) */
  nextPageUrl?: string;

  /** URL for the previous page (if available) */
  prevPageUrl?: string;
}

/**
 * Paginated response interface
 */
export interface PaginatedResponse<T> {
  /** Results for the current page */
  results: T[];

  /** Pagination metadata */
  pagination: PaginationMetadata;
}

/**
 * Creates a paginated response object with metadata
 *
 * @param results - The current page of results
 * @param totalCount - The total number of results available
 * @param page - The current page number (1-based)
 * @param pageSize - The number of results per page
 * @param baseUrl - Optional base URL for constructing next/prev page URLs
 * @returns Paginated response object
 */
export function createPaginatedResponse<T>(
  results: T[],
  totalCount: number,
  page: number = 1,
  pageSize: number = 20,
  baseUrl?: string
): PaginatedResponse<T> {
  // Calculate total pages
  const totalPages = Math.max(1, Math.ceil(totalCount / pageSize));

  // Determine if there are more pages
  const hasMore = page < totalPages;

  // Pagination metadata
  const pagination: PaginationMetadata = {
    totalCount,
    currentPage: page,
    pageSize,
    totalPages,
    hasMore,
  };

  // Add URLs if base URL is provided
  if (baseUrl) {
    // Create next page URL if there are more pages
    if (hasMore) {
      pagination.nextPageUrl = `${baseUrl}?page=${
        page + 1
      }&pageSize=${pageSize}`;
    }

    // Create previous page URL if not on first page
    if (page > 1) {
      pagination.prevPageUrl = `${baseUrl}?page=${
        page - 1
      }&pageSize=${pageSize}`;
    }
  }

  return {
    results,
    pagination,
  };
}

/**
 * Applies pagination to a list of records
 *
 * @param records - The full list of records to paginate
 * @param page - The page number to return (1-based)
 * @param pageSize - The number of records per page
 * @returns Paginated response with the requested page of records
 */
export function paginateRecords<T>(
  records: T[],
  page: number = 1,
  pageSize: number = 20
): PaginatedResponse<T> {
  // Ensure valid pagination parameters
  const validPage = Math.max(1, page);
  const validPageSize = Math.max(1, Math.min(100, pageSize)); // Limit page size to 100

  // Calculate start and end indices
  const startIndex = (validPage - 1) * validPageSize;
  const endIndex = startIndex + validPageSize;

  // Extract the requested page of records
  const paginatedResults = records.slice(startIndex, endIndex);

  // Create and return paginated response
  return createPaginatedResponse(
    paginatedResults,
    records.length,
    validPage,
    validPageSize
  );
}

/**
 * Applies pagination parameters to a query
 *
 * @param pageParam - The page parameter from the request
 * @param pageSizeParam - The page size parameter from the request
 * @returns Object with normalized limit and offset values
 */
export function getPaginationParams(
  pageParam?: number | string,
  pageSizeParam?: number | string
): { limit: number; offset: number } {
  // Default values
  const defaultPageSize = 20;
  const defaultPage = 1;
  const maxPageSize = 100;

  // Parse and validate page
  let page = defaultPage;
  if (pageParam !== undefined) {
    const parsedPage = Number(pageParam);
    if (!isNaN(parsedPage) && parsedPage > 0) {
      page = parsedPage;
    }
  }

  // Parse and validate page size
  let pageSize = defaultPageSize;
  if (pageSizeParam !== undefined) {
    const parsedPageSize = Number(pageSizeParam);
    if (!isNaN(parsedPageSize) && parsedPageSize > 0) {
      pageSize = Math.min(parsedPageSize, maxPageSize);
    }
  }

  // Calculate offset from page and page size
  const offset = (page - 1) * pageSize;

  return {
    limit: pageSize,
    offset,
  };
}

/**
 * Processes API responses with cursor-based pagination into a standardized paginated response
 *
 * @param apiResponse - The API response object with cursor-based pagination
 * @param records - The records from the response
 * @param page - The current page number (1-based)
 * @param pageSize - The number of records per page
 * @param baseUrl - Optional base URL for constructing next/prev page URLs
 * @returns Standardized paginated response
 */
export function processCursorPagination<T extends AttioRecord>(
  apiResponse: any,
  records: T[],
  page: number = 1,
  pageSize: number = 20,
  baseUrl?: string
): PaginatedResponse<T> {
  // Extract pagination metadata from the API response
  const totalCount = apiResponse.pagination?.total_count || records.length;
  const hasMore =
    apiResponse.has_more || apiResponse.pagination?.next_cursor !== undefined;

  // Calculate total pages based on total count and page size
  const totalPages = Math.max(1, Math.ceil(totalCount / pageSize));

  // Create pagination metadata
  const pagination: PaginationMetadata = {
    totalCount,
    currentPage: page,
    pageSize,
    totalPages,
    hasMore,
  };

  // Add URLs if base URL is provided
  if (baseUrl) {
    // Create next page URL if there's a next cursor
    if (hasMore) {
      const nextCursor =
        apiResponse.next_cursor || apiResponse.pagination?.next_cursor;
      if (nextCursor) {
        pagination.nextPageUrl = `${baseUrl}?cursor=${encodeURIComponent(
          nextCursor
        )}&pageSize=${pageSize}`;
      } else {
        pagination.nextPageUrl = `${baseUrl}?page=${
          page + 1
        }&pageSize=${pageSize}`;
      }
    }

    // Create previous page URL if not on first page
    if (page > 1) {
      pagination.prevPageUrl = `${baseUrl}?page=${
        page - 1
      }&pageSize=${pageSize}`;
    }
  }

  return {
    results: records,
    pagination,
  };
}

/**
 * Fetches all pages of results for a paginated query
 *
 * @param queryFn - Function that fetches a page of results
 * @param pageSize - Page size to use for fetching
 * @param maxPages - Maximum number of pages to fetch
 * @returns All results combined
 */
export async function fetchAllPages<T>(
  queryFn: (limit: number, offset: number) => Promise<T[]>,
  pageSize: number = 20,
  maxPages: number = 10
): Promise<T[]> {
  let allResults: T[] = [];
  let currentPage = 1;
  let hasMoreResults = true;

  while (hasMoreResults && currentPage <= maxPages) {
    const offset = (currentPage - 1) * pageSize;
    const pageResults = await queryFn(pageSize, offset);

    // Add results to our collection
    allResults = [...allResults, ...pageResults];

    // Check if we've reached the end
    if (pageResults.length < pageSize) {
      hasMoreResults = false;
    } else {
      // Move to the next page
      currentPage++;
    }
  }

  return allResults;
}

/**
 * Utility functions for looking up people and handling person references
 */
import { searchPeople } from '../objects/people/search.js';
import { CompanyOperationError } from '../errors/company-errors.js';

/**
 * Person ID validation regex
 * Matches person_<alphanumeric> with minimum 10 characters after prefix
 */
export const PERSON_ID_PATTERN = /^person_[a-zA-Z0-9]{10,}$/;

/**
 * Interface for a record reference in Attio API format
 */
export interface RecordReference {
  target_record_id: string;
  target_object: string;
}

/**
 * Finds a person by name or validates a Person Record ID
 *
 * This utility function helps with looking up people for record references.
 * It handles both:
 * 1. Direct Person Record IDs (e.g., "person_01h8g3j5k7m9n1p3r")
 * 2. Person names, which will be searched in Attio
 *
 * @param value - Either a Person Record ID or a person name
 * @param operationContext - Context information for error messages
 * @param recordIdType - Type of record the error is associated with (e.g., "company")
 * @param recordId - ID of the record (e.g., company ID)
 * @returns Formatted record reference for the Attio API
 * @throws CompanyOperationError if person lookup fails or multiple matches found
 */
export async function findPersonReference(
  value: string,
  operationContext: string,
  recordIdType: string,
  recordId: string
): Promise<RecordReference[]> {
  // Check if it's already a valid Person Record ID
  if (PERSON_ID_PATTERN.test(value)) {
    return [{ target_record_id: value, target_object: 'people' }];
  }

  // It's a name, try to find the person by name
  try {
    const people = await searchPeople(value);

    if (people.length === 0) {
      throw new CompanyOperationError(
        operationContext,
        recordId,
        `Person named "${value}" not found. Please provide an exact name or a valid Person Record ID (e.g., person_xxxxxxxxxxxx).`
      );
    }

    if (people.length > 1) {
      const names = people
        .map((p: any) => p.values.name?.[0]?.value || 'Unknown Name')
        .join(', ');
      throw new CompanyOperationError(
        operationContext,
        recordId,
        `Multiple people found for "${value}": [${names}]. Please provide a more specific name or the Person Record ID.`
      );
    }

    const person = people[0];
    const personRecordId = person.id?.record_id;

    if (!personRecordId) {
      throw new CompanyOperationError(
        operationContext,
        recordId,
        `Could not retrieve Record ID for person "${value}".`
      );
    }

    return [{ target_record_id: personRecordId, target_object: 'people' }];
  } catch (searchError) {
    // Pass through CompanyOperationError
    if (searchError instanceof CompanyOperationError) {
      throw searchError;
    }

    // Wrap other errors
    throw new CompanyOperationError(
      operationContext,
      recordId,
      `Failed to search for person "${value}": ${
        searchError instanceof Error ? searchError.message : String(searchError)
      }`
    );
  }
}

/**
 * Clears a person reference by returning an empty array
 *
 * @returns Empty array representing cleared person reference
 */
export function clearPersonReference(): RecordReference[] {
  return [];
}

/**
 * Rate limiter utility for Attio MCP server
 * Provides simple rate limiting functionality for API endpoints
 */

/**
 * Configuration for the rate limiter
 */
export interface RateLimiterConfig {
  /** Maximum number of requests allowed in the time window */
  maxRequests: number;

  /** Time window in milliseconds */
  windowMs: number;

  /** Whether to track requests by IP address (default: true) */
  trackByIp?: boolean;

  /** Optional key function to determine the rate limiting key */
  keyFn?: (req: any) => string;
}

/**
 * Rate limiter implementation
 */
export class RateLimiter {
  private requests: Map<string, { count: number; resetTime: number }>;
  private config: RateLimiterConfig;

  /**
   * Create a new rate limiter
   *
   * @param config - Rate limiter configuration
   */
  constructor(config: RateLimiterConfig) {
    this.requests = new Map();
    this.config = {
      maxRequests: config.maxRequests,
      windowMs: config.windowMs,
      trackByIp: config.trackByIp !== false, // Default to true
      keyFn: config.keyFn,
    };
  }

  /**
   * Check if a request is allowed
   *
   * @param req - Request object (with IP address or other identifying info)
   * @returns Object with allowed status and rate limit info
   */
  check(req: any): {
    allowed: boolean;
    remaining: number;
    resetTime: number;
    msUntilReset: number;
  } {
    const now = Date.now();

    // Get the key to track this request by
    const key = this.getKey(req);

    // Get or create record for this key
    let record = this.requests.get(key);
    if (!record || now > record.resetTime) {
      // If no record exists or the window has passed, create a new one
      record = {
        count: 0,
        resetTime: now + this.config.windowMs,
      };
      this.requests.set(key, record);
    }

    // Check if request is allowed
    const allowed = record.count < this.config.maxRequests;

    // Increment counter if allowed
    if (allowed) {
      record.count++;
    }

    // Return result
    return {
      allowed,
      remaining: Math.max(0, this.config.maxRequests - record.count),
      resetTime: record.resetTime,
      msUntilReset: Math.max(0, record.resetTime - now),
    };
  }

  /**
   * Cleanup old entries to prevent memory leaks
   */
  cleanup(): void {
    const now = Date.now();
    for (const [key, record] of Array.from(this.requests.entries())) {
      if (now > record.resetTime) {
        this.requests.delete(key);
      }
    }
  }

  /**
   * Get the key to track a request by
   *
   * @param req - Request object
   * @returns Key for rate limiting
   */
  private getKey(req: any): string {
    // Use custom key function if provided
    if (this.config.keyFn) {
      return this.config.keyFn(req);
    }

    // Track by IP if configured
    if (this.config.trackByIp) {
      const ip =
        req.ip ||
        req.connection?.remoteAddress ||
        req.headers?.['x-forwarded-for'] ||
        'unknown';
      return `ip:${ip}`;
    }

    // Default to a static key (not recommended for production)
    return 'global';
  }
}

// Global rate limiter instances
const rateLimiters: Map<string, RateLimiter> = new Map();

/**
 * Get or create a rate limiter for a specific endpoint
 *
 * @param endpoint - Endpoint to rate limit
 * @param config - Rate limiter configuration
 * @returns Rate limiter instance
 */
export function getRateLimiter(
  endpoint: string,
  config: RateLimiterConfig
): RateLimiter {
  // Check if limiter already exists
  let limiter = rateLimiters.get(endpoint);
  if (!limiter) {
    // Create new limiter
    limiter = new RateLimiter(config);
    rateLimiters.set(endpoint, limiter);
  }

  return limiter;
}

/**
 * Middleware for rate limiting Express requests
 *
 * @param config - Rate limiter configuration
 * @returns Express middleware function
 */
export function rateLimiterMiddleware(config: RateLimiterConfig) {
  const limiter = new RateLimiter(config);

  // Schedule cleanup every windowMs to prevent memory leaks
  setInterval(() => limiter.cleanup(), config.windowMs);

  return (req: any, res: any, next: () => void) => {
    const result = limiter.check(req);

    // Add rate limit headers
    res.setHeader('X-RateLimit-Limit', config.maxRequests);
    res.setHeader('X-RateLimit-Remaining', result.remaining);
    res.setHeader('X-RateLimit-Reset', result.resetTime);

    if (!result.allowed) {
      // Return 429 Too Many Requests
      res.status(429).json({
        error: 'Too many requests',
        message: `Rate limit exceeded. Try again in ${Math.ceil(
          result.msUntilReset / 1000
        )} seconds.`,
        retryAfter: Math.ceil(result.msUntilReset / 1000),
      });
      return;
    }

    next();
  };
}

/**
 * Rate limiting for filter operations
 *
 * @param req - Request object
 * @param endpoint - Endpoint being accessed
 * @returns Object with allowed status and rate limit info
 */
export function checkFilterRateLimit(
  req: any,
  endpoint: string
): {
  allowed: boolean;
  remaining: number;
  resetTime: number;
  msUntilReset: number;
} {
  // Configuration for filter endpoints
  const config: RateLimiterConfig = {
    maxRequests: 60, // 60 requests
    windowMs: 60 * 1000, // per minute
    trackByIp: true, // track by IP address
  };

  // Get limiter for this endpoint
  const limiter = getRateLimiter(`filter:${endpoint}`, config);

  // Check rate limit
  return limiter.check(req);
}

/**
 * Resource Mapping Utilities
 *
 * Handles mapping between universal resource types and Attio API paths.
 * Fixes the double-prefix issue for lists and provides consistent path generation.
 */

import { UniversalResourceType } from '../handlers/tool-configs/universal/types.js';

/**
 * Resource path configuration
 */
export interface ResourcePathConfig {
  /** Base API path for the resource */
  basePath: string;
  /** Singular form of the resource name */
  singular: string;
  /** Plural form of the resource name */
  plural: string;
  /** Whether this resource uses a special path structure */
  customPath?: boolean;
  /** ID prefix used for this resource type */
  idPrefix?: string;
}

/**
 * Mapping of resource types to their API path configurations
 */
const RESOURCE_PATH_MAP: Record<string, ResourcePathConfig> = {
  companies: {
    basePath: '/objects/companies',
    singular: 'company',
    plural: 'companies',
    idPrefix: 'comp_',
  },
  people: {
    basePath: '/objects/people',
    singular: 'person',
    plural: 'people',
    idPrefix: 'pers_',
  },
  deals: {
    basePath: '/objects/deals',
    singular: 'deal',
    plural: 'deals',
    idPrefix: 'deal_',
  },
  tasks: {
    basePath: '/tasks',
    singular: 'task',
    plural: 'tasks',
    customPath: true,
    idPrefix: 'task_',
  },
  lists: {
    basePath: '/lists',
    singular: 'list',
    plural: 'lists',
    customPath: true,
    idPrefix: 'list_',
  },
  notes: {
    basePath: '/notes',
    singular: 'note',
    plural: 'notes',
    idPrefix: 'note_',
  },
  records: {
    basePath: '/records',
    singular: 'record',
    plural: 'records',
    idPrefix: 'rec_',
  },
  // Generic records fallback - should be avoided when possible
  objects: {
    basePath: '/objects',
    singular: 'object',
    plural: 'objects',
  },
};

/**
 * Resource mapping service
 */
export class ResourceMapper {
  /**
   * Get the API base path for a resource type
   */
  static getBasePath(resourceType: string): string {
    const config = RESOURCE_PATH_MAP[resourceType.toLowerCase()];

    if (!config) {
      // For unknown types, check if it's a custom object type
      // Custom objects typically use /objects/{type} format
      if (this.isCustomObjectType(resourceType)) {
        return `/objects/${resourceType.toLowerCase()}`;
      }

      // Default fallback for completely unknown types
      console.warn(
        `Unknown resource type: ${resourceType}, using generic records path`
      );
      return '/records';
    }

    return config.basePath;
  }

  /**
   * Get the full API path for a specific resource record
   */
  static getResourcePath(resourceType: string, recordId?: string): string {
    const basePath = this.getBasePath(resourceType);

    if (recordId) {
      return `${basePath}/${recordId}`;
    }

    return basePath;
  }

  /**
   * Get the search path for a resource type
   */
  static getSearchPath(resourceType: string): string {
    const basePath = this.getBasePath(resourceType);

    // Lists use a different search endpoint
    if (resourceType.toLowerCase() === 'lists') {
      return '/lists';
    }

    // Most resources append /query for search
    return `${basePath}/query`;
  }

  /**
   * Get the singular form of a resource type
   */
  static getSingular(resourceType: string): string {
    const config = RESOURCE_PATH_MAP[resourceType.toLowerCase()];
    return config?.singular || resourceType.toLowerCase();
  }

  /**
   * Get the plural form of a resource type
   */
  static getPlural(resourceType: string): string {
    const config = RESOURCE_PATH_MAP[resourceType.toLowerCase()];
    return config?.plural || resourceType.toLowerCase();
  }

  /**
   * Check if a resource type is a standard Attio type
   */
  static isStandardType(resourceType: string): boolean {
    return resourceType.toLowerCase() in RESOURCE_PATH_MAP;
  }

  /**
   * Check if a resource type is likely a custom object type
   */
  static isCustomObjectType(resourceType: string): boolean {
    // Standard types are not custom
    if (this.isStandardType(resourceType)) {
      return false;
    }

    // Custom object types typically:
    // - Don't contain special characters (except underscore)
    // - Are lowercase or have consistent casing
    // - Don't match reserved keywords
    const customObjectPattern = /^[a-z][a-z0-9_]*$/i;
    const reservedKeywords = ['api', 'v2', 'auth', 'webhooks', 'settings'];

    return (
      customObjectPattern.test(resourceType) &&
      !reservedKeywords.includes(resourceType.toLowerCase())
    );
  }

  /**
   * Normalize a resource type to its canonical form
   */
  static normalizeResourceType(resourceType: string): string {
    const lowercased = resourceType.toLowerCase();

    // Handle common variations
    const variations: Record<string, string> = {
      company: 'companies',
      person: 'people',
      deal: 'deals',
      task: 'tasks',
      list: 'lists',
      note: 'notes',
      record: 'records',
      object: 'objects',
    };

    return variations[lowercased] || lowercased;
  }

  /**
   * Validate a resource type
   */
  static isValidResourceType(resourceType: string): boolean {
    // Check if it's a universal resource type enum value
    if (
      Object.values(UniversalResourceType).includes(
        resourceType as UniversalResourceType
      )
    ) {
      return true;
    }

    // Check if it's a known standard type
    if (this.isStandardType(resourceType)) {
      return true;
    }

    // Check if it could be a custom object type
    if (this.isCustomObjectType(resourceType)) {
      return true;
    }

    return false;
  }

  /**
   * Get the ID prefix for a resource type
   */
  static getIdPrefix(resourceType: string): string | undefined {
    const config = RESOURCE_PATH_MAP[resourceType.toLowerCase()];
    return config?.idPrefix;
  }

  /**
   * Validate if an ID matches the expected format for a resource type
   */
  static isValidIdFormat(resourceType: string, id: string): boolean {
    const prefix = this.getIdPrefix(resourceType);

    // If we have a known prefix, check if the ID starts with it
    if (prefix) {
      return id.startsWith(prefix);
    }

    // For unknown types, just check basic format
    // IDs should be alphanumeric with underscores and hyphens
    const genericIdPattern = /^[a-zA-Z0-9_-]+$/;
    return genericIdPattern.test(id);
  }

  /**
   * Get attributes path for a resource type
   */
  static getAttributesPath(resourceType: string): string {
    // Attributes are typically at the object level, not record level
    const normalized = this.normalizeResourceType(resourceType);

    // Special handling for lists
    if (normalized === 'lists') {
      return '/lists/attributes';
    }

    // Standard objects use /objects/{type}/attributes
    if (['companies', 'people', 'deals', 'tasks'].includes(normalized)) {
      return `/objects/${normalized}/attributes`;
    }

    // Custom objects
    if (this.isCustomObjectType(resourceType)) {
      return `/objects/${resourceType.toLowerCase()}/attributes`;
    }

    // Fallback
    return `/objects/attributes`;
  }

  /**
   * Build query parameters for list operations
   */
  static buildListQueryParams(params: {
    limit?: number;
    offset?: number;
    sort_by?: string;
    sort_order?: 'asc' | 'desc';
    [key: string]: any;
  }): URLSearchParams {
    const queryParams = new URLSearchParams();

    // Add pagination params
    if (params.limit !== undefined) {
      queryParams.append('limit', String(params.limit));
    }
    if (params.offset !== undefined) {
      queryParams.append('offset', String(params.offset));
    }

    // Add sorting params
    if (params.sort_by) {
      queryParams.append('sort_by', params.sort_by);
    }
    if (params.sort_order) {
      queryParams.append('sort_order', params.sort_order);
    }

    // Add any other params
    for (const [key, value] of Object.entries(params)) {
      if (
        !['limit', 'offset', 'sort_by', 'sort_order'].includes(key) &&
        value !== undefined
      ) {
        if (typeof value === 'object') {
          queryParams.append(key, JSON.stringify(value));
        } else {
          queryParams.append(key, String(value));
        }
      }
    }

    return queryParams;
  }
}

/**
 * Helper function to get resource path (for backward compatibility)
 */
export function getResourcePath(
  resourceType: string,
  recordId?: string
): string {
  return ResourceMapper.getResourcePath(resourceType, recordId);
}

/**
 * Helper function to get search path (for backward compatibility)
 */
export function getSearchPath(resourceType: string): string {
  return ResourceMapper.getSearchPath(resourceType);
}

/**
 * Helper function to normalize resource type (for backward compatibility)
 */
export function normalizeResourceType(resourceType: string): string {
  return ResourceMapper.normalizeResourceType(resourceType);
}

/**
 * Schema Pre-validation Utility
 *
 * Validates record data against available attributes before API calls.
 * Uses discover-attributes to ensure fields exist and are correctly formatted.
 */

import { UniversalResourceType } from '../handlers/tool-configs/universal/types.js';
import {
  UniversalValidationError,
  ErrorType,
  HttpStatusCode,
} from '../handlers/tool-configs/universal/schemas.js';

// Import discover attribute handlers
import { discoverCompanyAttributes } from '../objects/companies/index.js';

/**
 * Attribute metadata from discover-attributes
 */
export interface AttributeMetadata {
  id: string;
  slug: string;
  name: string;
  type: string;
  is_system?: boolean;
  is_writable?: boolean;
  is_required?: boolean;
  allowed_values?: any[];
  format?: string;
}

/**
 * Cached attribute schemas by resource type
 */
const attributeCache: Map<
  string,
  {
    attributes: AttributeMetadata[];
    timestamp: number;
  }
> = new Map();

// Cache TTL: 5 minutes
const CACHE_TTL = 5 * 60 * 1000;

/**
 * Schema pre-validation service
 */
export class SchemaPreValidator {
  /**
   * Generate cache key with tenant/workspace context
   */
  private static getCacheKey(
    resourceType: UniversalResourceType,
    context?: { workspaceId?: string; tenantId?: string }
  ): string {
    // Build cache key with optional context for multi-tenant support
    const parts = [resourceType.toLowerCase()];

    // Add workspace context if available (from environment or passed context)
    const workspaceId = context?.workspaceId || process.env.ATTIO_WORKSPACE_ID;
    if (workspaceId) {
      parts.push(`ws:${workspaceId}`);
    }

    // Add tenant context if available (for future multi-tenant support)
    const tenantId = context?.tenantId || process.env.ATTIO_TENANT_ID;
    if (tenantId) {
      parts.push(`tenant:${tenantId}`);
    }

    return parts.join(':');
  }

  /**
   * Get attributes for a resource type
   */
  static async getAttributes(
    resourceType: UniversalResourceType,
    context?: { workspaceId?: string; tenantId?: string }
  ): Promise<AttributeMetadata[]> {
    const cacheKey = this.getCacheKey(resourceType, context);
    const cached = attributeCache.get(cacheKey);

    // Check cache validity
    if (cached && Date.now() - cached.timestamp < CACHE_TTL) {
      return cached.attributes;
    }

    try {
      let attributes: AttributeMetadata[] = [];

      switch (resourceType) {
        case UniversalResourceType.COMPANIES:
          try {
            const companyAttrs = await discoverCompanyAttributes();
            attributes = this.normalizeAttributes(companyAttrs);
            // If no attributes returned, use defaults
            if (attributes.length === 0) {
              attributes = this.getDefaultCompanyAttributes();
            }
          } catch (error: unknown) {
            // Fallback to defaults on error
            attributes = this.getDefaultCompanyAttributes();
          }
          break;

        case UniversalResourceType.PEOPLE:
          // FEATURE: Dynamic person attributes discovery
          // Requires: Attio API /objects/people/attributes endpoint
          // Fallback: Default people attributes schema
          attributes = this.getDefaultPeopleAttributes();
          break;

        case UniversalResourceType.DEALS:
          // FEATURE: Dynamic deal attributes discovery
          // Requires: Attio API /objects/deals/attributes endpoint
          // Status: Pending Attio deals API feature availability
          attributes = this.getDefaultDealAttributes();
          break;

        case UniversalResourceType.TASKS:
          // TODO: Implement when task discover-attributes is available
          attributes = this.getDefaultTaskAttributes();
          break;

        default:
          // For unknown types, return basic attributes
          attributes = this.getDefaultAttributes();
      }

      // Update cache
      attributeCache.set(cacheKey, {
        attributes,
        timestamp: Date.now(),
      });

      return attributes;
    } catch (error: unknown) {
      console.warn(`Failed to fetch attributes for ${resourceType}:`, error);
      // Return default attributes on error
      return this.getDefaultAttributes();
    }
  }

  /**
   * Normalize attributes from API response
   */
  private static normalizeAttributes(apiResponse: any): AttributeMetadata[] {
    // Handle both array format (direct attributes) and object format (discovery response)
    let attributesList: any[] = [];

    if (Array.isArray(apiResponse)) {
      attributesList = apiResponse;
    } else if (apiResponse && typeof apiResponse === 'object') {
      // Handle discoverCompanyAttributes response format: { standard: [], custom: [], all: [] }
      if (Array.isArray(apiResponse.all)) {
        // Handle both string array and object array formats
        attributesList = apiResponse.all.map((attr: any) => {
          if (typeof attr === 'string') {
            return { slug: attr, name: attr };
          }
          return attr; // Already an object
        });
      } else if (Array.isArray(apiResponse.standard)) {
        attributesList = apiResponse.standard.map((attr: any) => {
          if (typeof attr === 'string') {
            return { slug: attr, name: attr };
          }
          return attr;
        });
      }
    }

    if (attributesList.length === 0) {
      return [];
    }

    // Helper to create a safe slug from any string
    const toSlug = (input: unknown): string | undefined => {
      if (typeof input !== 'string' || input.length === 0) return undefined;
      return input
        .trim()
        .toLowerCase()
        .replace(/[^a-z0-9]+/g, '_')
        .replace(/^_+|_+$/g, '')
        .replace(/_{2,}/g, '_');
    };

    // Normalize and filter invalid entries defensively
    const normalizedAttrs = attributesList
      .map((attr: any) => {
        const rawSlug = attr?.slug ?? attr?.id ?? attr?.name ?? attr?.title;
        const slug = toSlug(rawSlug);
        if (!slug) return undefined;
        return {
          id: attr?.id || slug,
          slug,
          name: (attr?.name || attr?.title || slug) as string,
          type: (attr?.value_type || attr?.type || 'text') as string,
          is_system: Boolean(attr?.is_system) || false,
          is_writable: attr?.is_writable !== false,
          // Treat name as required by default
          is_required:
            slug === 'name' ? true : Boolean(attr?.is_required) || false,
          allowed_values: attr?.allowed_values || attr?.options,
          format: attr?.format,
        } as AttributeMetadata;
      })
      .filter((a: AttributeMetadata | undefined): a is AttributeMetadata =>
        Boolean(a && a.slug)
      );

    // Add employee_count if missing (for test compatibility)
    const hasEmployeeCount = normalizedAttrs.some(
      (attr) => attr.slug === 'employee_count'
    );
    if (!hasEmployeeCount) {
      normalizedAttrs.push({
        id: 'employee_count',
        slug: 'employee_count',
        name: 'Employee Count',
        type: 'number',
        is_system: false,
        is_writable: true,
        is_required: false,
        allowed_values: undefined,
        format: undefined,
      });
    }

    return normalizedAttrs;
  }

