/**
 * ID Format Validation Utilities
 *
 * Provides early validation of record IDs to prevent unnecessary API calls
 * for invalid ID formats, improving performance for 404 responses.
 */

/**
 * Valid ID patterns for different resource types
 */
const ID_PATTERNS = {
  // Standard MongoDB ObjectId pattern (24 hex characters)
  OBJECT_ID: /^[a-f0-9]{24}$/,

  // UUID v4 pattern
  UUID: /^[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i,

  // Attio-specific patterns (if different from standard ObjectId)
  ATTIO_RECORD: /^[a-f0-9]{24}$/,
  ATTIO_WORKSPACE: /^[a-f0-9]{24}$/,

  // Legacy or alternative formats (add as discovered)
  LEGACY: /^[A-Za-z0-9_-]{20,30}$/,
};

/**
 * Validation result
 */
export interface IdValidationResult {
  isValid: boolean;
  format?: string;
  message?: string;
}

/**
 * Validate a record ID format
 *
 * @param id The ID to validate
 * @param resourceType Optional resource type for specific validation
 * @returns Validation result with details
 */
export function validateRecordId(
  id: string | undefined | null,
  _resourceType?: string
): IdValidationResult {
  // Check for missing ID
  if (!id) {
    return {
      isValid: false,
      message: 'Record ID is required',
    };
  }

  // Check for empty string
  if (id.trim() === '') {
    return {
      isValid: false,
      message: 'Record ID cannot be empty',
    };
  }

  // Check length constraints
  if (id.length < 20) {
    return {
      isValid: false,
      message: `Invalid record ID format: ID too short (${id.length} characters)`,
    };
  }

  if (id.length > 50) {
    return {
      isValid: false,
      message: `Invalid record ID format: ID too long (${id.length} characters)`,
    };
  }

  // Check against known patterns
  if (ID_PATTERNS.OBJECT_ID.test(id)) {
    return {
      isValid: true,
      format: 'ObjectId',
    };
  }

  if (ID_PATTERNS.UUID.test(id)) {
    return {
      isValid: true,
      format: 'UUID',
    };
  }

  if (ID_PATTERNS.LEGACY.test(id)) {
    return {
      isValid: true,
      format: 'Legacy',
    };
  }

  // Check for common invalid patterns
  if (id.includes(' ')) {
    return {
      isValid: false,
      message: 'Record ID cannot contain spaces',
    };
  }

  if (id.startsWith('http')) {
    return {
      isValid: false,
      message: 'Record ID appears to be a URL, not a valid ID',
    };
  }

  if (id.includes('@')) {
    return {
      isValid: false,
      message: 'Record ID appears to be an email, not a valid ID',
    };
  }

  // Default rejection for unrecognized formats
  return {
    isValid: false,
    message: `Invalid record ID format: ${id}`,
  };
}

/**
 * Validate multiple IDs at once
 *
 * @param ids Array of IDs to validate
 * @param resourceType Optional resource type
 * @returns Map of ID to validation result
 */
export function validateRecordIds(
  ids: string[],
  resourceType?: string
): Map<string, IdValidationResult> {
  const results = new Map<string, IdValidationResult>();

  for (const id of ids) {
    results.set(id, validateRecordId(id, resourceType));
  }

  return results;
}

/**
 * Check if all IDs in a list are valid
 *
 * @param ids Array of IDs to check
 * @returns True if all IDs are valid
 */
export function areAllIdsValid(ids: string[]): boolean {
  return ids.every((id) => validateRecordId(id).isValid);
}

/**
 * Filter out invalid IDs from a list
 *
 * @param ids Array of IDs to filter
 * @returns Object with valid and invalid ID arrays
 */
export function filterValidIds(ids: string[]): {
  valid: string[];
  invalid: Array<{ id: string; reason: string }>;
} {
  const valid: string[] = [];
  const invalid: Array<{ id: string; reason: string }> = [];

  for (const id of ids) {
    const result = validateRecordId(id);
    if (result.isValid) {
      valid.push(id);
    } else {
      invalid.push({
        id,
        reason: result.message || 'Invalid format',
      });
    }
  }

  return { valid, invalid };
}

/**
 * Normalize an ID to a consistent format
 *
 * @param id The ID to normalize
 * @returns Normalized ID or null if invalid
 */
export function normalizeId(id: string): string | null {
  const validation = validateRecordId(id);
  if (!validation.isValid) {
    return null;
  }

  // Trim whitespace
  let normalized = id.trim();

  // Convert to lowercase for hex-based IDs
  if (validation.format === 'ObjectId' || validation.format === 'UUID') {
    normalized = normalized.toLowerCase();
  }

  return normalized;
}

/**
 * Generate a cache key for a record ID
 * Used for 404 response caching
 *
 * @param resourceType The resource type
 * @param id The record ID
 * @returns Cache key string
 */
export function generateIdCacheKey(resourceType: string, id: string): string {
  const normalizedId = normalizeId(id);
  if (!normalizedId) {
    return `${resourceType}:invalid:${id}`;
  }
  return `${resourceType}:${normalizedId}`;
}

/**
 * Check if an error is likely due to an invalid ID format
 *
 * @param error The error to check
 * @returns True if error appears to be ID-related
 */
export function isIdFormatError(error: any): boolean {
  if (!error) return false;

  const errorMessage = error.message || error.toString() || '';
  const lowerMessage = errorMessage.toLowerCase();

  return (
    lowerMessage.includes('invalid id') ||
    lowerMessage.includes('invalid record id') ||
    lowerMessage.includes('malformed id') ||
    lowerMessage.includes('objectid failed') ||
    lowerMessage.includes('cast to objectid failed') ||
    lowerMessage.includes('invalid uuid') ||
    lowerMessage.includes('not a valid identifier')
  );
}

/**
 * Extract IDs from various input formats
 *
 * @param input String that may contain IDs
 * @returns Array of potential IDs found
 */
export function extractIds(input: string): string[] {
  const ids: string[] = [];

  // Try to match ObjectId pattern
  const objectIdMatches = input.match(/[a-f0-9]{24}/g);
  if (objectIdMatches) {
    ids.push(...objectIdMatches);
  }

  // Try to match UUID pattern
  const uuidMatches = input.match(
    /[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}/gi
  );
  if (uuidMatches) {
    ids.push(...uuidMatches);
  }

  return Array.from(new Set(ids)); // Remove duplicates
}

/**
 * UUID Validation Utilities for Issue #416
 *
 * This module provides UUID format validation to properly distinguish between
 * "Invalid format" and "Not found" errors, addressing the misleading error
 * messages reported in Issue #416.
 */

import {
  EnhancedApiError,
  ErrorTemplates,
} from '../../errors/enhanced-api-errors.js';

/**
 * UUID format validation regex
 * Matches standard UUID v4 format: xxxxxxxx-xxxx-xxxx-xxxx-xxxxxxxxxxxx
 */
const UUID_REGEX =
  /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;

/**
 * Validate if a string is in proper UUID format
 *
 * @param uuid - String to validate
 * @returns true if the string is a valid UUID format
 */
export function isValidUUID(uuid: string): boolean {
  return UUID_REGEX.test(uuid);
}

/**
 * Validate record ID format and throw appropriate error if invalid
 *
 * This function addresses Issue #416 by ensuring that format validation
 * errors are distinct from "not found" errors.
 *
 * @param recordId - Record ID to validate
 * @param resourceType - Type of resource for context
 * @throws EnhancedApiError with proper format error message
 */
export function validateRecordId(recordId: string, resourceType: string): void {
  if (!isValidUUID(recordId)) {
    throw ErrorTemplates.INVALID_UUID_FORMAT(recordId, resourceType);
  }
}

/**
 * Enhanced UUID validation with detailed error context
 *
 * @param uuid - UUID string to validate
 * @param context - Additional context for error messages
 * @returns Validation result with error details
 */
export interface UUIDValidationResult {
  isValid: boolean;
  error?: string;
  suggestion?: string;
}

export function validateUUIDWithDetails(
  uuid: string,
  _options?: { resourceType?: string; operation?: string }
): UUIDValidationResult {
  if (!uuid || typeof uuid !== 'string') {
    return {
      isValid: false,
      error: 'Record ID is required and must be a string',
      suggestion:
        'Provide a valid UUID format (e.g., "a1b2c3d4-e5f6-7890-abcd-ef1234567890")',
    };
  }

  if (uuid.length !== 36) {
    return {
      isValid: false,
      error: `Invalid UUID length: ${uuid.length} characters (expected 36)`,
      suggestion:
        'UUIDs must be exactly 36 characters long with dashes at positions 8, 13, 18, 23',
    };
  }

  if (!UUID_REGEX.test(uuid)) {
    // Analyze what's wrong with the format
    const issues: string[] = [];

    if (
      uuid.charAt(8) !== '-' ||
      uuid.charAt(13) !== '-' ||
      uuid.charAt(18) !== '-' ||
      uuid.charAt(23) !== '-'
    ) {
      issues.push('incorrect dash positions (should be at 8, 13, 18, 23)');
    }

    const nonHexChars = uuid.replace(/-/g, '').match(/[^0-9a-fA-F]/g);
    if (nonHexChars) {
      issues.push(
        `invalid characters: ${Array.from(new Set(nonHexChars)).join(', ')}`
      );
    }

    return {
      isValid: false,
      error: `Invalid UUID format: ${issues.join('; ')}`,
      suggestion:
        'Use hexadecimal characters (0-9, a-f) with dashes at positions 8, 13, 18, 23',
    };
  }

  return { isValid: true };
}

/**
 * Create a properly formatted error for invalid UUIDs
 *
 * @param recordId - The invalid record ID
 * @param resourceType - Resource type for context
 * @param operation - Operation being performed
 * @returns EnhancedApiError with detailed UUID format guidance
 */
export function createInvalidUUIDError(
  recordId: string,
  resourceType: string,
  operation: string = 'GET'
): EnhancedApiError {
  const validation = validateUUIDWithDetails(recordId, {
    resourceType,
    operation,
  });

  return new EnhancedApiError(
    `Invalid record identifier format: '${recordId}'`,
    400,
    `/objects/${resourceType}`,
    operation,
    {
      field: 'record_id',
      resourceType,
      documentationHint:
        validation.suggestion ||
        'Expected UUID format (e.g., "a1b2c3d4-e5f6-7890-abcd-ef1234567890")',
    }
  );
}

/**
 * Create a properly formatted error for records not found
 *
 * @param recordId - The record ID that wasn't found
 * @param resourceType - Resource type for context
 * @returns EnhancedApiError with clear "not found" message
 */
export function createRecordNotFoundError(
  recordId: string,
  resourceType: string
): EnhancedApiError {
  return new EnhancedApiError(
    `Record not found`,
    404,
    `/objects/${resourceType}/${recordId}`,
    'GET',
    {
      recordId,
      resourceType,
      httpStatus: 404,
      documentationHint: `Use search-records to find valid ${resourceType} IDs.`,
    }
  );
}

/**
 * Utility to distinguish between format errors and not found errors
 *
 * This function encapsulates the core logic for Issue #416 - it determines
 * whether an error should be treated as a format issue or a not found issue.
 *
 * @param error - Original error from API
 * @param recordId - Record ID that was requested
 * @param resourceType - Resource type for context
 * @returns Appropriate EnhancedApiError
 */
export function classifyRecordError(
  error: Error,
  recordId: string,
  resourceType: string
): EnhancedApiError {
  const errorMessage = error.message.toLowerCase();

  // If the UUID format is invalid, it's definitely a format error
  if (!isValidUUID(recordId)) {
    return createInvalidUUIDError(recordId, resourceType);
  }

  // If UUID format is valid but we got an error, check the error type
  if (
    errorMessage.includes('not found') ||
    errorMessage.includes('does not exist') ||
    (error as any).status === 404 ||
    (error as any).statusCode === 404
  ) {
    return createRecordNotFoundError(recordId, resourceType);
  }

  // For other errors with valid UUIDs, preserve the original error but enhance it
  return new EnhancedApiError(
    error.message,
    (error as any).statusCode || (error as any).status || 500,
    `/objects/${resourceType}/${recordId}`,
    'GET',
    {
      recordId,
      resourceType,
      originalError: error,
    }
  );
}

/**
 * Generate example UUIDs for error messages and documentation
 */
export function generateExampleUUID(): string {
  // Generate a valid UUID v4 for examples
  const hex = '0123456789abcdef';
  let uuid = '';

  for (let i = 0; i < 36; i++) {
    if (i === 8 || i === 13 || i === 18 || i === 23) {
      uuid += '-';
    } else {
      uuid += hex[Math.floor(Math.random() * 16)];
    }
  }

  return uuid;
}

/**
 * Extract UUID from various input formats including URIs
 *
 * Handles:
 * - Raw UUIDs: "a1b2c3d4-e5f6-7890-abcd-ef1234567890"
 * - URI paths: "https://app.attio.com/records/companies/a1b2c3d4-e5f6-7890-abcd-ef1234567890"
 * - Custom schemes: "attio://companies/a1b2c3d4-e5f6-7890-abcd-ef1234567890"
 *
 * @param input - Input string that may contain a UUID
 * @returns Extracted UUID string or null if no valid UUID found
 */
export function extractRecordId(input: string): string | null {
  if (!input || typeof input !== 'string') {
    return null;
  }

  // First try direct UUID matching
  const directMatch = input.match(UUID_REGEX);
  if (directMatch) {
    return directMatch[0];
  }

  // Try URL parsing for URI-style inputs
  try {
    const url = new URL(input);
    const pathSegments = url.pathname.split('/').filter(Boolean);

    // Check each path segment for a UUID
    for (const segment of pathSegments) {
      if (UUID_REGEX.test(segment)) {
        return segment;
      }
    }
  } catch {
    // Not a valid URL, continue with other strategies
  }

  // Try extracting UUID from anywhere in the string as fallback
  const anyMatch = input.match(
    /[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}/i
  );
  if (anyMatch) {
    return anyMatch[0];
  }

  return null;
}

/**
 * Common UUID validation patterns for testing
 */
export const UUIDTestPatterns = {
  VALID: [
    'a1b2c3d4-e5f6-7890-abcd-ef1234567890',
    '123e4567-e89b-12d3-a456-426614174000',
    '00000000-0000-0000-0000-000000000000',
  ],
  INVALID_FORMAT: [
    'not-a-uuid',
    'a1b2c3d4e5f6-7890-abcd-ef1234567890', // Missing dash
    'a1b2c3d4-e5f6-7890-abcd-ef1234567890x', // Too long
    'a1b2c3d4-e5f6-7890-abcd-ef123456789', // Too short
    'g1b2c3d4-e5f6-7890-abcd-ef1234567890', // Invalid character
    '12345678901234567890123456789012345678', // No dashes
  ],
  EDGE_CASES: [
    '', // Empty string
    null as any, // Null
    undefined as any, // Undefined
    123 as any, // Number
    {} as any, // Object
  ],
};

/**
 * Validation for Attio attribute types
 * Provides validation and type conversion for attribute values
 * to ensure they match Attio's expected types
 *
 * This module handles both validation and automatic type conversion
 * for common data format mismatches, which is particularly useful for
 * LLM-generated content where string representations of other data types
 * are common.
 */

/**
 * Supported attribute types in Attio
 */
export type AttributeType =
  | 'string'
  | 'number'
  | 'boolean'
  | 'date'
  | 'array'
  | 'object'
  | 'select'
  | 'record-reference';

/**
 * Result of attribute validation
 */
export interface ValidationResult {
  /** Whether the validation was successful */
  valid: boolean;
  /** The converted value (may differ from input if type conversion was applied) */
  convertedValue?: any;
  /** Error message if validation failed */
  error?: string;
}

/**
 * Validates an attribute value against the expected type
 *
 * This function validates that a value matches the expected attribute type,
 * and attempts to convert it when possible. It handles both strict validation
 * and auto-conversion for common type mismatches.
 *
 * @param attributeName - The name of the attribute being validated
 * @param value - The value to validate
 * @param expectedType - The expected attribute type
 * @returns Validation result with success status and optionally converted value
 *
 * @example
 * // Validate a string
 * validateAttributeValue('company_name', 'Acme Inc', 'string')
 * // Returns: { valid: true, convertedValue: 'Acme Inc' }
 *
 * @example
 * // Convert string to number
 * validateAttributeValue('employee_count', '250', 'number')
 * // Returns: { valid: true, convertedValue: 250 }
 *
 * @example
 * // Convert string to boolean
 * validateAttributeValue('is_active', 'yes', 'boolean')
 * // Returns: { valid: true, convertedValue: true }
 *
 * @example
 * // Handle invalid values
 * validateAttributeValue('revenue', 'not-a-number', 'number')
 * // Returns: { valid: false, error: 'Invalid number value...' }
 */
export function validateAttributeValue(
  attributeName: string,
  value: any,
  expectedType: AttributeType
): ValidationResult {
  // Handle null case first
  if (value === null || value === undefined) {
    return { valid: true, convertedValue: null };
  }

  // Validate based on expected type
  switch (expectedType) {
    case 'boolean':
      return validateBooleanValue(attributeName, value);
    case 'number':
      return validateNumberValue(attributeName, value);
    case 'string':
      return validateStringValue(attributeName, value);
    case 'date':
      return validateDateValue(attributeName, value);
    case 'array':
      return validateArrayValue(attributeName, value);
    case 'object':
      return validateObjectValue(attributeName, value);
    case 'select':
      return validateSelectValue(attributeName, value);
    case 'record-reference':
      return validateRecordReferenceValue(attributeName, value);
    default:
      // If the type doesn't match any known type, pass it through
      return { valid: true, convertedValue: value };
  }
}

/**
 * Validates a boolean value
 *
 * This function validates and converts values to boolean type.
 * It handles various string representations ('true', 'yes', 'on', '1')
 * and numeric values (1, 0) in addition to native boolean values.
 *
 * @param attributeName - The name of the attribute
 * @param value - The value to validate
 * @returns Validation result
 *
 * @example
 * // Native boolean
 * validateBooleanValue('is_active', true)
 * // Returns: { valid: true, convertedValue: true }
 *
 * @example
 * // String conversion
 * validateBooleanValue('is_active', 'yes')
 * // Returns: { valid: true, convertedValue: true }
 *
 * @example
 * // Numeric conversion
 * validateBooleanValue('is_active', 1)
 * // Returns: { valid: true, convertedValue: true }
 */
function validateBooleanValue(
  attributeName: string,
  value: any
): ValidationResult {
  // Already a boolean - simple case
  if (typeof value === 'boolean') {
    return { valid: true, convertedValue: value };
  }

  // Auto-conversion cases
  if (typeof value === 'string') {
    const stringValue = value.toLowerCase().trim();

    // Reject empty strings explicitly
    if (stringValue === '') {
      return {
        valid: false,
        error: `Invalid boolean value for "${attributeName}". Empty strings cannot be converted to boolean.`,
      };
    }

    if (
      stringValue === 'true' ||
      stringValue === 'yes' ||
      stringValue === '1' ||
      stringValue === 'on'
    ) {
      return { valid: true, convertedValue: true };
    }
    if (
      stringValue === 'false' ||
      stringValue === 'no' ||
      stringValue === '0' ||
      stringValue === 'off'
    ) {
      return { valid: true, convertedValue: false };
    }
  }

  // Number conversion
  if (typeof value === 'number') {
    if (value === 1) {
      return { valid: true, convertedValue: true };
    }
    if (value === 0) {
      return { valid: true, convertedValue: false };
    }
  }

  // Invalid boolean value
  return {
    valid: false,
    error: `Invalid boolean value for "${attributeName}". Expected a boolean or a string like "true"/"false", but got ${typeof value}.`,
  };
}

/**
 * Validates a numeric value
 *
 * This function validates and converts values to number type.
 * It handles numeric strings and boolean values in addition to native numbers.
 * NaN values are considered invalid numbers.
 *
 * @param attributeName - The name of the attribute
 * @param value - The value to validate
 * @returns Validation result
 *
 * @example
 * // Native number
 * validateNumberValue('revenue', 1000000)
 * // Returns: { valid: true, convertedValue: 1000000 }
 *
 * @example
 * // String conversion
 * validateNumberValue('employee_count', '250')
 * // Returns: { valid: true, convertedValue: 250 }
 *
 * @example
 * // Boolean conversion
 * validateNumberValue('binary_value', true)
 * // Returns: { valid: true, convertedValue: 1 }
 */
function validateNumberValue(
  attributeName: string,
  value: any
): ValidationResult {
  // Already a number - simple case
  if (typeof value === 'number' && !isNaN(value)) {
    return { valid: true, convertedValue: value };
  }

  // Auto-conversion from string
  if (typeof value === 'string') {
    const trimmed = value.trim();

    // Reject empty strings explicitly
    if (trimmed === '') {
      return {
        valid: false,
        error: `Invalid number value for "${attributeName}". Empty strings cannot be converted to numbers.`,
      };
    }

    const numericValue = Number(trimmed);

    if (!isNaN(numericValue)) {
      return { valid: true, convertedValue: numericValue };
    }
  }

  // Auto-conversion from boolean
  if (typeof value === 'boolean') {
    return { valid: true, convertedValue: value ? 1 : 0 };
  }

  // Invalid number value
  return {
    valid: false,
    error: `Invalid number value for "${attributeName}". Expected a number or a numeric string, but got ${typeof value}.`,
  };
}

/**
 * Validates a string value
 *
 * This function validates and converts values to string type.
 * It handles numbers, booleans, dates, and even attempts to stringify
 * objects in addition to native strings.
 *
 * @param attributeName - The name of the attribute
 * @param value - The value to validate
 * @returns Validation result
 *
 * @example
 * // Native string
 * validateStringValue('name', 'Acme Corporation')
 * // Returns: { valid: true, convertedValue: 'Acme Corporation' }
 *
 * @example
 * // Number conversion
 * validateStringValue('id_text', 12345)
 * // Returns: { valid: true, convertedValue: '12345' }
 *
 * @example
 * // Date conversion
 * validateStringValue('date_text', new Date('2023-01-15'))
 * // Returns: { valid: true, convertedValue: '2023-01-15T00:00:00.000Z' }
 *
 * @example
 * // Object conversion
 * validateStringValue('metadata_text', { id: 123, type: 'customer' })
 * // Returns: { valid: true, convertedValue: '{"id":123,"type":"customer"}' }
 */
function validateStringValue(
  attributeName: string,
  value: any
): ValidationResult {
  // Already a string - simple case
  if (typeof value === 'string') {
    return { valid: true, convertedValue: value };
  }

  // Auto-conversion cases
  if (typeof value === 'number' || typeof value === 'boolean') {
    return { valid: true, convertedValue: String(value) };
  }

  // Handle date objects
  if (value instanceof Date) {
    return { valid: true, convertedValue: value.toISOString() };
  }

  // Handle objects that can be stringified
  if (typeof value === 'object' && value !== null) {
    try {
      return { valid: true, convertedValue: JSON.stringify(value) };
    } catch (error: unknown) {
      // Failed to stringify
    }
  }

  // Invalid string value
  return {
    valid: false,
    error: `Invalid string value for "${attributeName}". Expected a string or a value that can be converted to string, but got ${typeof value}.`,
  };
}

/**
 * Validates a date value
 *
 * This function validates and converts values to date format.
 * It handles Date objects, ISO date strings, and timestamps
 * (both in seconds and milliseconds).
 * The converted value is always returned as an ISO string.
 *
 * @param attributeName - The name of the attribute
 * @param value - The value to validate
 * @returns Validation result
 *
 * @example
 * // Date object
 * validateDateValue('created_at', new Date('2023-01-15'))
 * // Returns: { valid: true, convertedValue: '2023-01-15T00:00:00.000Z' }
 *
 * @example
 * // ISO date string
 * validateDateValue('created_at', '2023-01-15T12:30:00Z')
 * // Returns: { valid: true, convertedValue: '2023-01-15T12:30:00.000Z' }
 *
 * @example
 * // Timestamp in milliseconds
 * validateDateValue('created_at', 1673784600000) // 2023-01-15T12:30:00.000Z
 * // Returns: { valid: true, convertedValue: '2023-01-15T12:30:00.000Z' }
 *
 * @example
 * // Timestamp in seconds
 * validateDateValue('created_at', 1673784600) // 2023-01-15T12:30:00.000Z
 * // Returns: { valid: true, convertedValue: '2023-01-15T12:30:00.000Z' }
 */
function validateDateValue(
  attributeName: string,
  value: any
): ValidationResult {
  // Already a date object - simple case
  if (value instanceof Date) {
    if (!isNaN(value.getTime())) {
      return { valid: true, convertedValue: value.toISOString() };
    } else {
      return {
        valid: false,
        error: `Invalid date value for "${attributeName}". The date is invalid.`,
      };
    }
  }

  // ISO date string
  if (typeof value === 'string') {
    // Try to create a date object from the string
    const dateObj = new Date(value);
    if (!isNaN(dateObj.getTime())) {
      return { valid: true, convertedValue: dateObj.toISOString() };
    }
  }

  // Unix timestamp (number)
  if (typeof value === 'number') {
    // Assume milliseconds if greater than 1e10 (Jan 26 1970), otherwise seconds
    const timestamp = value > 1e10 ? value : value * 1000;
    const dateObj = new Date(timestamp);
    if (!isNaN(dateObj.getTime())) {
      return { valid: true, convertedValue: dateObj.toISOString() };
    }
  }

  // Invalid date value
  return {
    valid: false,
    error: `Invalid date value for "${attributeName}". Expected a Date object, ISO date string, or timestamp, but got ${typeof value}.`,
  };
}

/**
 * Validates an array value
 *
 * This function validates and converts values to array format.
 * It handles native arrays and also converts single values to
 * single-element arrays when appropriate.
 *
 * @param attributeName - The name of the attribute
 * @param value - The value to validate
 * @returns Validation result
 *
 * @example
 * // Native array
 * validateArrayValue('tags', ['software', 'tech'])
 * // Returns: { valid: true, convertedValue: ['software', 'tech'] }
 *
 * @example
 * // Single value conversion
 * validateArrayValue('tags', 'software')
 * // Returns: { valid: true, convertedValue: ['software'] }
 *
 * @example
 * // Empty array
 * validateArrayValue('tags', [])
 * // Returns: { valid: true, convertedValue: [] }
 */
function validateArrayValue(
  attributeName: string,
  value: any
): ValidationResult {
  // Already an array - simple case
  if (Array.isArray(value)) {
    return { valid: true, convertedValue: value };
  }

  // Convert single value to array
  if (value !== null && value !== undefined) {
    return { valid: true, convertedValue: [value] };
  }

  // Invalid array value
  return {
    valid: false,
    error: `Invalid array value for "${attributeName}". Expected an array, but got ${typeof value}.`,
  };
}

/**
 * Validates an object value
 *
 * This function validates that a value is a proper object (not null or array).
 * Unlike other validators, it does not attempt to convert non-object values
 * due to the ambiguity of what such a conversion should produce.
 *
 * @param attributeName - The name of the attribute
 * @param value - The value to validate
 * @returns Validation result
 *
 * @example
 * // Valid object
 * validateObjectValue('metadata', { id: 123, type: 'customer' })
 * // Returns: { valid: true, convertedValue: { id: 123, type: 'customer' } }
 *
 * @example
 * // Empty object
 * validateObjectValue('settings', {})
 * // Returns: { valid: true, convertedValue: {} }
 *
 * @example
 * // Invalid (array)
 * validateObjectValue('metadata', ['item1', 'item2'])
 * // Returns: { valid: false, error: 'Invalid object value...' }
 */
function validateObjectValue(
  attributeName: string,
  value: any
): ValidationResult {
  // Already an object - simple case
  if (typeof value === 'object' && value !== null && !Array.isArray(value)) {
    return { valid: true, convertedValue: value };
  }

  // Invalid object value
  return {
    valid: false,
    error: `Invalid object value for "${attributeName}". Expected an object, but got ${typeof value}.`,
  };
}

/**
 * Validates a select value
 *
 * This function validates and converts values for select/option fields.
 * It handles string values, arrays of strings, and attempts to convert
 * other types to strings. Since we don't have access to the valid options list,
 * we only validate that the format is correct, not that the values are valid options.
 *
 * @param attributeName - The name of the attribute
 * @param value - The value to validate
 * @returns Validation result
 *
 * @example
 * // Single option
 * validateSelectValue('status', 'active')
 * // Returns: { valid: true, convertedValue: 'active' }
 *
 * @example
 * // Multiple options
 * validateSelectValue('categories', ['software', 'tech'])
 * // Returns: { valid: true, convertedValue: ['software', 'tech'] }
 *
 * @example
 * // Converting non-string values in array
 * validateSelectValue('categories', ['software', 123, true])
 * // Returns: { valid: true, convertedValue: ['software', '123', 'true'] }
 */
function validateSelectValue(
  attributeName: string,
  value: any
): ValidationResult {
  // For select values, we expect strings or arrays of strings
  // Since we don't have access to the valid options list here,
  // we'll just validate that it's a string or array of strings

  // Single string value
  if (typeof value === 'string') {
    return { valid: true, convertedValue: value };
  }

  // Array of strings
  if (Array.isArray(value)) {
    const allStrings = value.every((item) => typeof item === 'string');
    if (allStrings) {
      return { valid: true, convertedValue: value };
    }

    // Try to convert all elements to strings
    const convertedArray = value.map((item) => String(item));
    return { valid: true, convertedValue: convertedArray };
  }

  // Convert single value to string
  if (value !== null && value !== undefined) {
    return { valid: true, convertedValue: String(value) };
  }

  // Invalid select value
  return {
    valid: false,
    error: `Invalid select value for "${attributeName}". Expected a string or array of strings, but got ${typeof value}.`,
  };
}

/**
 * Validates a record reference value
 *
 * This function validates and normalizes record reference values.
 * It handles string IDs, objects with record_id or id properties,
 * and arrays of these types. The result is always normalized to a
 * string ID or array of string IDs.
 *
 * @param attributeName - The name of the attribute
 * @param value - The value to validate
 * @returns Validation result
 *
 * @example
 * // String ID
 * validateRecordReferenceValue('parent_company', 'rec_123456')
 * // Returns: { valid: true, convertedValue: 'rec_123456' }
 *
 * @example
 * // Object with record_id
 * validateRecordReferenceValue('parent_company', { record_id: 'rec_123456' })
 * // Returns: { valid: true, convertedValue: 'rec_123456' }
 *
 * @example
 * // Array of mixed references
 * validateRecordReferenceValue('related_companies', [
 *   'rec_123',
 *   { record_id: 'rec_456' },
 *   { id: 'rec_789' }
 * ])
 * // Returns: { valid: true, convertedValue: ['rec_123', 'rec_456', 'rec_789'] }
 */
function validateRecordReferenceValue(
  attributeName: string,
  value: any
): ValidationResult {
  // For record references, we need IDs
  // Could be a string ID, an object with record_id, or an array of either

  // String ID
  if (typeof value === 'string') {
    return { valid: true, convertedValue: value };
  }

  // Object with record_id
  if (typeof value === 'object' && value !== null && !Array.isArray(value)) {
    if ('record_id' in value && typeof value.record_id === 'string') {
      return { valid: true, convertedValue: value.record_id };
    }

    if ('id' in value && typeof value.id === 'string') {
      return { valid: true, convertedValue: value.id };
    }
  }

  // Array of IDs or objects
  if (Array.isArray(value)) {
    const convertedIds = value
      .map((item) => {
        if (typeof item === 'string') {
          return item;
        }

        if (typeof item === 'object' && item !== null) {
          if ('record_id' in item && typeof item.record_id === 'string') {
            return item.record_id;
          }

          if ('id' in item && typeof item.id === 'string') {
            return item.id;
          }
        }

        return null;
      })
      .filter((id) => id !== null);

    if (convertedIds.length > 0) {
      return { valid: true, convertedValue: convertedIds };
    }
  }

  // Invalid record reference value
  return {
    valid: false,
    error: `Invalid record reference value for "${attributeName}". Expected a record ID string, object with record_id, or array of either, but got ${typeof value}.`,
  };
}

