  },
  "[markdown]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode",
    "editor.wordWrap": "on",
    "editor.quickSuggestions": {
      "comments": false,
      "strings": false,
      "other": false
    }
  },
  "[yaml]": {
    "editor.defaultFormatter": "esbenp.prettier-vscode",
    "editor.tabSize": 2,
    "editor.insertSpaces": true
  },

  // ESLint Settings
  "eslint.validate": [
    "javascript",
    "typescript"
  ],
  "eslint.workingDirectories": [
    {
      "mode": "auto"
    }
  ],
  "eslint.options": {
    "extensions": [".js", ".ts"]
  },
  "eslint.runtime": "node",

  // Prettier Settings
  "prettier.semi": true,
  "prettier.singleQuote": true,
  "prettier.trailingComma": "es5",
  "prettier.printWidth": 80,
  "prettier.tabWidth": 2,
  "prettier.configPath": ".prettierrc",

  // Vitest Settings
  "vitest.enable": true,
  "vitest.commandLine": "npx vitest",
  "vitest.nodeExecutable": "node",
  "vitest.include": ["test/**/*.test.ts", "test/**/*.test.js"],
  "vitest.exclude": [
    "test/integration/**/*.test.ts",
    "test/e2e/**/*.test.ts"
  ],

  // Terminal Settings
  "terminal.integrated.env.osx": {
    "NODE_ENV": "development"
  },
  "terminal.integrated.env.linux": {
    "NODE_ENV": "development"
  },
  "terminal.integrated.env.windows": {
    "NODE_ENV": "development"
  },

  // Git Settings
  "git.autofetch": true,
  "git.confirmSync": false,
  "git.enableSmartCommit": true,
  "git.postCommitCommand": "none",

  // Debug Settings
  "debug.javascript.autoAttachFilter": "smart",
  "debug.javascript.terminalOptions": {
    "skipFiles": [
      "<node_internals>/**",
      "node_modules/**"
    ]
  },

  // MCP-specific Settings
  "json.schemas": [
    {
      "fileMatch": ["**/config/mappings/*.json"],
      "url": "./config/mappings/schema.json"
    },
    {
      "fileMatch": ["**/.mcp/*.json"],
      "url": "https://modelcontextprotocol.io/schemas/2024-11/schema.json"
    }
  ],

  // Code Lens Settings
  "typescript.implementationsCodeLens.enabled": true,
  "typescript.referencesCodeLens.enabled": true,
  "typescript.referencesCodeLens.showOnAllFunctions": false,

  // Problem Matchers
  "problems.showCurrentInStatus": true,
  "problems.decorations.enabled": true,

  // Workspace-specific Recommendations
  "recommendations": [
    "dbaeumer.vscode-eslint",
    "esbenp.prettier-vscode",
    "vitest.explorer",
    "ms-vscode.vscode-typescript-next"
  ],

  // Custom Task Runner
  "task.problemMatchers": [
    "$tsc",
    "$eslint-stylish"
  ],

  // Spell Checker Settings
  "cSpell.words": [
    "attio",
    "vitest",
    "tsconfig",
    "wireit",
    "modelcontextprotocol",
    "kesslerio",
    "smithery",
    "codex",
    "CRUD",
    "todos",
    "dtype",
    "multiselect",
    "autogen",
    "preflight",
    "syncpack"
  ],

  // Better Comments Settings
  "better-comments.tags": [
    {
      "tag": "!",
      "color": "#FF2D00",
      "strikethrough": false,
      "underline": false,
      "backgroundColor": "transparent",
      "bold": false,
      "italic": false
    },
    {
      "tag": "?",
      "color": "#3498DB",
      "strikethrough": false,
      "underline": false,
      "backgroundColor": "transparent",
      "bold": false,
      "italic": false
    },
    {
      "tag": "//",
      "color": "#474747",
      "strikethrough": true,
      "underline": false,
      "backgroundColor": "transparent",
      "bold": false,
      "italic": false
    },
    {
      "tag": "todo",
      "color": "#FF8C00",
      "strikethrough": false,
      "underline": false,
      "backgroundColor": "transparent",
      "bold": false,
      "italic": false
    },
    {
      "tag": "RULE",
