  "files.autoSave": "onFocusChange",

  // Search Settings
  "search.exclude": {
    "**/node_modules": true,
    "**/dist": true,
    "**/coverage": true,
    "**/.git": true,
    "**/logs": true,
    "**/logs.backup": true,
