    // Analyze CRUD performance
    if (metrics.crud.length > 0) {
      const avgCrud = metrics.crud.reduce((a, b) => a + b, 0) / metrics.crud.length;
      const maxCrud = Math.max(...metrics.crud);

      this.log('\nCRUD Operation Performance:', colors.bright);
