    if (metrics) {
      const violations = [];

      if (metrics.notFound.some(t => t > BUDGETS.notFound)) {
        violations.push('404 responses');
      }
      if (metrics.search.some(t => t > BUDGETS.search)) {
        violations.push('search operations');
