        <h2>Environment</h2>
        <table>
            <tr><td>Node Version</td><td>${report.environment.nodeVersion}</td></tr>
            <tr><td>Platform</td><td>${report.environment.platform}</td></tr>
            <tr><td>Architecture</td><td>${report.environment.arch}</td></tr>
        </table>
    </div>

    <div class="metric">
