/**
 * Notes-related tool configurations for companies
 */
import {
  getCompanyNotes,
  createCompanyNote,
} from '../../../objects/companies/index.js';
import { NotesToolConfig, CreateNoteToolConfig } from '../../tool-types.js';

// Company notes tool configurations
export const notesToolConfigs = {
  notes: {
    name: 'get-company-notes',
    handler: getCompanyNotes,
    formatResult: (notes: any) => {
      if (!notes || notes.length === 0) {
        return 'No notes found for this company.';
      }

      // Debug logging in development to help identify API response structure
      if (process.env.NODE_ENV === 'development' || process.env.DEBUG) {
        console.log(
          '[get-company-notes] Debug - Raw notes response:',
          JSON.stringify(notes.slice(0, 1), null, 2)
        );
      }

      return `Found ${notes.length} notes:\n${notes
        .map((note: any) => {
          // The AttioNote interface shows these are direct properties
          const title = note.title || 'Untitled';
          const content = note.content || '';
          const timestamp = note.created_at || 'unknown';

          // Additional debug logging for each note
          if (process.env.NODE_ENV === 'development' || process.env.DEBUG) {
            console.log(
              `[get-company-notes] Note fields available:`,
              Object.keys(note)
            );
            console.log(
              `[get-company-notes] Content found:`,
              !!content,
              content ? `(${content.length} chars)` : '(none)'
            );
          }

          return `- ${title} (Created: ${timestamp})\n  ${
            content
              ? content.length > 200
                ? content.substring(0, 200) + '...'
                : content
              : 'No content'
          }`;
        })
        .join('\n\n')}`;
    },
  } as NotesToolConfig,

  createNote: {
    name: 'create-company-note',
    handler: createCompanyNote,
    idParam: 'companyId',
    formatResult: (note: any) => {
      if (!note) {
        return 'Failed to create note.';
      }
      // Truncate content at 100 chars for readability in console output
      return `Successfully created note: ${
        note.title || 'Untitled'
      }\nContent: ${
        note.content
          ? note.content.length > 100
            ? note.content.substring(0, 100) + '...'
            : note.content
          : 'No content'
      }\nCreated at: ${note.created_at || 'unknown'}`;
    },
  } as CreateNoteToolConfig,
};

// Notes tool definitions
export const notesToolDefinitions = [
  {
    name: 'get-company-notes',
    description: 'Get notes for a company',
    inputSchema: {
      type: 'object',
      properties: {
        companyId: {
          type: 'string',
          description:
            'ID of the company to get notes for (provide either this or uri)',
        },
        uri: {
          type: 'string',
          description:
            "URI of the company in the format 'attio://companies/{id}' (provide either this or companyId)",
        },
        limit: {
          type: 'number',
          description: 'Maximum number of notes to fetch (default: 10)',
        },
        offset: {
          type: 'number',
          description: 'Number of notes to skip for pagination (default: 0)',
        },
      },
    },
  },
  {
    name: 'create-company-note',
    description: 'Create a note for a specific company',
    inputSchema: {
      type: 'object',
      properties: {
        companyId: {
          type: 'string',
          description:
            'ID of the company to create a note for (provide either this or uri)',
        },
        uri: {
          type: 'string',
          description:
            "URI of the company in the format 'attio://companies/{id}' (provide either this or companyId)",
        },
        title: {
          type: 'string',
          description: 'Title of the note (required)',
        },
        content: {
          type: 'string',
          description: 'Content of the note',
        },
      },
      required: ['title', 'content'],
    },
  },
];

/**
 * Unit tests for company notes formatter
 * Tests the fix for issue #338 - notes content not being displayed
 */

import { describe, it, expect } from 'vitest';

// Mock the formatter function based on the actual implementation
function mockNotesFormatter(notes: any[]) {
  if (!notes || notes.length === 0) {
    return 'No notes found for this company.';
  }

  return `Found ${notes.length} notes:\n${notes
    .map((note: any) => {
      // Handle different possible field structures from the API
      const title =
        note.title || note.data?.title || note.values?.title || 'Untitled';
      const content =
        note.content ||
        note.data?.content ||
        note.values?.content ||
        note.text ||
        note.body;
      const timestamp =
        note.timestamp ||
        note.created_at ||
        note.data?.created_at ||
        note.values?.created_at ||
        'unknown';

      return `- ${title} (Created: ${timestamp})\n  ${
        content
          ? content.length > 200
            ? content.substring(0, 200) + '...'
            : content
          : 'No content'
      }`;
    })
    .join('\n\n')}`;
}

describe('Company Notes Formatter', () => {
  describe('Content Extraction', () => {
    it('should extract content from standard note structure', () => {
      const notes = [
        {
          id: { note_id: 'note1' },
          title: 'Test Note',
          content: 'This is the note content',
          created_at: '2024-01-01T00:00:00Z',
        },
      ];

      const result = mockNotesFormatter(notes);

      expect(result).toContain('Test Note');
      expect(result).toContain('This is the note content');
      expect(result).toContain('2024-01-01T00:00:00Z');
      expect(result).not.toContain('No content');
    });

    it('should extract content from nested data structure', () => {
      const notes = [
        {
          id: { note_id: 'note2' },
          title: 'Nested Note',
          data: {
            content: 'Content in data field',
            created_at: '2024-01-01T00:00:00Z',
          },
        },
      ];

      const result = mockNotesFormatter(notes);

      expect(result).toContain('Nested Note');
      expect(result).toContain('Content in data field');
      expect(result).not.toContain('No content');
    });

    it('should extract content from values structure (Attio-style)', () => {
      const notes = [
        {
          id: { note_id: 'note3' },
          title: 'Values Note',
          values: {
            content: 'Content in values field',
            created_at: '2024-01-01T00:00:00Z',
          },
        },
      ];

      const result = mockNotesFormatter(notes);

      expect(result).toContain('Values Note');
      expect(result).toContain('Content in values field');
      expect(result).not.toContain('No content');
    });

    it('should handle alternative content field names', () => {
      const notes = [
        {
          id: { note_id: 'note4' },
          title: 'Text Note',
          text: 'Content in text field',
          created_at: '2024-01-01T00:00:00Z',
        },
      ];

      const result = mockNotesFormatter(notes);

      expect(result).toContain('Text Note');
      expect(result).toContain('Content in text field');
      expect(result).not.toContain('No content');
    });

    it('should handle body field as content source', () => {
      const notes = [
        {
          id: { note_id: 'note5' },
          title: 'Body Note',
          body: 'Content in body field',
          created_at: '2024-01-01T00:00:00Z',
        },
      ];

      const result = mockNotesFormatter(notes);

      expect(result).toContain('Body Note');
      expect(result).toContain('Content in body field');
      expect(result).not.toContain('No content');
    });
  });

  describe('Fallback Handling', () => {
    it('should show "No content" when no content fields are present', () => {
      const notes = [
        {
          id: { note_id: 'note6' },
          title: 'Empty Note',
          created_at: '2024-01-01T00:00:00Z',
        },
      ];

      const result = mockNotesFormatter(notes);

      expect(result).toContain('Empty Note');
      expect(result).toContain('No content');
    });

    it('should handle missing title gracefully', () => {
      const notes = [
        {
          id: { note_id: 'note7' },
          content: 'Content without title',
          created_at: '2024-01-01T00:00:00Z',
        },
      ];

      const result = mockNotesFormatter(notes);

      expect(result).toContain('Untitled');
      expect(result).toContain('Content without title');
    });

    it('should handle missing timestamp gracefully', () => {
      const notes = [
        {
          id: { note_id: 'note8' },
          title: 'Note without timestamp',
          content: 'Content without timestamp',
        },
      ];

      const result = mockNotesFormatter(notes);

      expect(result).toContain('Note without timestamp');
      expect(result).toContain('Content without timestamp');
      expect(result).toContain('Created: unknown');
    });
  });

  describe('Content Truncation', () => {
    it('should truncate long content at 200 characters', () => {
      const longContent = 'A'.repeat(250);
      const notes = [
        {
          id: { note_id: 'note9' },
          title: 'Long Note',
          content: longContent,
          created_at: '2024-01-01T00:00:00Z',
        },
      ];

      const result = mockNotesFormatter(notes);

      expect(result).toContain('Long Note');
      expect(result).toContain('A'.repeat(200) + '...');
      expect(result).not.toContain('A'.repeat(250));
    });

    it('should not truncate short content', () => {
      const shortContent = 'Short content';
      const notes = [
        {
          id: { note_id: 'note10' },
          title: 'Short Note',
          content: shortContent,
          created_at: '2024-01-01T00:00:00Z',
        },
      ];

      const result = mockNotesFormatter(notes);

      expect(result).toContain('Short Note');
      expect(result).toContain('Short content');
      expect(result).not.toContain('...');
    });
  });

  describe('Multiple Notes', () => {
    it('should handle multiple notes with different structures', () => {
      const notes = [
        {
          id: { note_id: 'note11' },
          title: 'Standard Note',
          content: 'Standard content',
          created_at: '2024-01-01T00:00:00Z',
        },
        {
          id: { note_id: 'note12' },
          title: 'Nested Note',
          data: {
            content: 'Nested content',
            created_at: '2024-01-02T00:00:00Z',
          },
        },
      ];

      const result = mockNotesFormatter(notes);

      expect(result).toContain('Found 2 notes');
      expect(result).toContain('Standard Note');
      expect(result).toContain('Standard content');
      expect(result).toContain('Nested Note');
      expect(result).toContain('Nested content');
    });
  });

  describe('Edge Cases', () => {
    it('should handle empty notes array', () => {
      const result = mockNotesFormatter([]);
      expect(result).toBe('No notes found for this company.');
    });

    it('should handle null notes', () => {
      const result = mockNotesFormatter(null);
      expect(result).toBe('No notes found for this company.');
    });

    it('should handle undefined notes', () => {
      const result = mockNotesFormatter(undefined);
      expect(result).toBe('No notes found for this company.');
    });
  });
});

/**
 * Note operations for people
 */
import {
  getPersonNotes,
  createPersonNote,
} from '../../../objects/people/index.js';
import { NotesToolConfig, CreateNoteToolConfig } from '../../tool-types.js';
import { NoteDisplay } from '../../../types/tool-types.js';

export const notesToolConfigs = {
  notes: {
    name: 'get-person-notes',
    handler: getPersonNotes,
    formatResult: (notes: NoteDisplay[]) => {
      if (!notes || notes.length === 0) {
        return 'No notes found for this person.';
      }
      return `Found ${notes.length} notes:\n${notes
        .map(
          (note) =>
            `- ${note.title || 'Untitled'} (Created: ${
              note.timestamp || 'unknown'
            })\n  ${
              note.content
                ? note.content.length > 100
                  ? note.content.substring(0, 100) + '...'
                  : note.content
                : 'No content'
            }`
        )
        .join('\n\n')}`;
    },
  } as NotesToolConfig,

  createNote: {
    name: 'create-person-note',
    handler: createPersonNote,
    idParam: 'personId',
  } as CreateNoteToolConfig,
};

export const notesToolDefinitions = [
  {
    name: 'get-person-notes',
    description: 'Get notes for a person',
    inputSchema: {
      type: 'object',
      properties: {
        personId: {
          type: 'string',
          description: 'ID of the person to get notes for',
        },
      },
      required: ['personId'],
    },
  },
  {
    name: 'create-person-note',
    description: 'Create a note for a specific person',
    inputSchema: {
      type: 'object',
      properties: {
        personId: {
          type: 'string',
          description: 'ID of the person to create a note for',
        },
        title: {
          type: 'string',
          description: 'Title of the note (required)',
        },
        content: { type: 'string', description: 'Content of the note' },
      },
      required: ['personId', 'title', 'content'],
    },
  },
];

